        [301] = {803,"Lưu Biểu","Ai đánh ai còn chưa biết đâu, các ngươi hãy xem sự lợi hại của Lưu Cảnh Thăng ta!","Ai đánh ai còn chưa biết đâu, các ngươi hãy xem sự lợi hại của Lưu Cảnh Thăng ta!","","",},
        [302] = {804,"Lưu Biểu","Từ xưa anh hùng xuất thiếu niên, tướng quân trẻ tuổi quả nhiên lợi hại, Lưu Cảnh Thăng xin chịu thua!","Từ xưa anh hùng xuất thiếu niên, tướng quân trẻ tuổi quả nhiên lợi hại, Lưu Cảnh Thăng xin chịu thua!","Nhân vật","",},
        [303] = {804,"Nhân vật","Bị đánh xong liền hạ giọng nhỉ! Nhận thua rồi thì hãy giao #Danh Tướng Lệnh# ra đây!","Bị đánh xong liền hạ giọng nhỉ! Nhận thua rồi thì hãy giao #Danh Tướng Lệnh# ra đây!","","",},
