        [306] = {804,"Hoàng Cái","Tướng quân, không xong rồi! Chúa công đã trúng gian kế của Khoái Lương, bị Hoàng Tổ lừa ra khỏi thành, một mình chiến đấu, nguy hiểm vô cùng!","Tướng quân, không xong rồi! Chúa công đã trúng gian kế của Khoái Lương, bị Hoàng Tổ lừa ra khỏi thành, một mình chiến đấu, nguy hiểm vô cùng!","","",},
        [307] = {804,"Nhân vật","Hoàng Cái lão tướng quân đừng nôn nóng, chúng ta sẽ đi cứu Tôn Kiên ngay!","Hoàng Cái lão tướng quân đừng nôn nóng, chúng ta sẽ đi cứu Tôn Kiên ngay!","","",},
        [308] = {805,"Nhân vật","Chúng ta đã chậm một bước! Tuy bắt được Hoàng Tổ, nhưng Tôn Kiên tướng quân đã hy sinh.","Chúng ta đã chậm một bước! Tuy bắt được Hoàng Tổ, nhưng Tôn Kiên tướng quân đã hy sinh.","Tinh Thái","",},
        [309] = {805,"Tinh Thái","Lời thề độc của Tôn Kiên đã ứng nghiệm, xem ra, ngọc tỷ hẳn nằm trong tay hắn...(Nói xong, Tôn Sách cưỡi ngựa đuổi đến.)","Lời thề độc của Tôn Kiên đã ứng nghiệm, xem ra, ngọc tỷ hẳn nằm trong tay hắn...(Nói xong, Tôn Sách cưỡi ngựa đuổi đến.)","","",},
        [310] = {805,"Tôn Sách","Kẻ nào hại phụ thân ta? Có phải các ngươi không?","Kẻ nào hại phụ thân ta? Có phải các ngươi không?","","",},
        [311] = {805,"Nhân vật","Ngươi là?","Ngươi là?","","",},
        [312] = {805,"Tôn Sách","Ta là trưởng tử của Tôn Kiên, Tiểu Bá Vương Tôn Sách! Các ngươi hại cha ta, nộp mạng đi!","Ta là trưởng tử của Tôn Kiên, Tiểu Bá Vương Tôn Sách! Các ngươi hại cha ta, nộp mạng đi!","","",},
        [313] = {806,"Hoàng Cái","Thiếu chủ hiểu lầm rồi! Vị tướng quân trẻ tuổi này đến để cứu chúa công, là bọn người Hoàng Tổ đã hại chúa công.","Thiếu chủ hiểu lầm rồi! Vị tướng quân trẻ tuổi này đến để cứu chúa công, là bọn người Hoàng Tổ đã hại chúa công.","Tôn Sách","",},
        [314] = {806,"Tôn Sách","Cha ta mất, Sách nhất thời nóng vội, hành sự lỗ mãng, đã hiểu lầm tướng quân, mong bỏ qua cho.","Cha ta mất, Sách nhất thời nóng vội, hành sự lỗ mãng, đã hiểu lầm tướng quân, mong bỏ qua cho.","","",},
        [315] = {806,"Nhân vật","Được rồi. Ta đã bắt sống Hoàng Tổ, chi bằng dùng Hoàng Tổ đổi lấy thi thể của Tôn Kiên tướng quân?","Được rồi. Ta đã bắt sống Hoàng Tổ, chi bằng dùng Hoàng Tổ đổi lấy thi thể của Tôn Kiên tướng quân?","","",},
        [316] = {806,"Tôn Sách","Vậy thì xin đa tạ! Đại ơn của tướng quân, Sách không biết lấy gì báo đáp, #Danh Tướng Lệnh# này xin tặng cho tướng quân!","Vậy thì xin đa tạ! Đại ơn của tướng quân, Sách không biết lấy gì báo đáp, #Danh Tướng Lệnh# này xin tặng cho tướng quân!","","",},
        [317] = {806,"Nhân vật","Được, Tôn Sách tướng quân quả nhiên thẳng thắn, chẳng hay sau này định thế nào?","Được, Tôn Sách tướng quân quả nhiên thẳng thắn, chẳng hay sau này định thế nào?","","",},
        [318] = {806,"Tôn Sách","Nhiều thuộc hạ cũ của phụ thân ở dưới trướng Viên Thuật, Sách sẽ tạm thời theo Viên Thuật, đợi đủ lông đủ cánh sẽ suy tính, trả thù cho cha.","Nhiều thuộc hạ cũ của phụ thân ở dưới trướng Viên Thuật, Sách sẽ tạm thời theo Viên Thuật, đợi đủ lông đủ cánh sẽ suy tính, trả thù cho cha.","","",},
        [319] = {806,"Tôn Sách","Đến ngày đó, tướng quân đến Giang Đông, Sách sẽ hết lòng hỗ trợ! (Tôn Sách đau lòng, đưa thi thể Tôn Kiên đi xa)","Đến ngày đó, tướng quân đến Giang Đông, Sách sẽ hết lòng hỗ trợ! (Tôn Sách đau lòng, đưa thi thể Tôn Kiên đi xa)","","",},
        [320] = {806,"Tinh Thái","Chúa công, giờ chúng ta đi đâu?","Chúa công, giờ chúng ta đi đâu?","","",},
        [321] = {806,"Nhân vật","Hôm đó Tào Tháo đi chặn Đổng Trác, nghe nói đã đến Huỳnh Dương, chúng ta cũng đến đó xem sao!","Hôm đó Tào Tháo đi chặn Đổng Trác, nghe nói đã đến Huỳnh Dương, chúng ta cũng đến đó xem sao!","","",},
        [322] = {901,"Nhân vật","Hạ Hầu tướng quân, Tào Tháo đâu rồi?","Hạ Hầu tướng quân, Tào Tháo đâu rồi?","Hạ Hầu Đôn","",},
        [323] = {901,"Hạ Hầu Đôn","Đổng tặc hỏa thiêu Lạc Dương, trốn đến Trường An, chúa công đến Huỳnh Dương diệt Đổng tặc!","Đổng tặc hỏa thiêu Lạc Dương, trốn đến Trường An, chúa công đến Huỳnh Dương diệt Đổng tặc!","Nhân vật","",},
        [324] = {901,"Nhân vật","Hiện tình hình sao rồi?","Hiện tình hình sao rồi?","Hạ Hầu Đôn","",},
        [325] = {901,"Hạ Hầu Đôn","Thái Thú Huỳnh Dương là Từ Vinh giúp Đổng tặc, Lữ Bố sau khi nhận tinh binh, Lý Nho lại hiến kế mai phục, đại quân của chúa công đã không chống đỡ được!","Thái Thú Huỳnh Dương là Từ Vinh giúp Đổng tặc, Lữ Bố sau khi nhận tinh binh, Lý Nho lại hiến kế mai phục, đại quân của chúa công đã không chống đỡ được!","Nhân vật","",},
        [326] = {901,"Nhân vật","Vậy Tào Tháo sẽ gặp nguy hiểm! Ta sẽ đến ứng cứu!","Vậy Tào Tháo sẽ gặp nguy hiểm! Ta sẽ đến ứng cứu!","Hạ Hầu Đôn","",},
        [327] = {902,"Tinh Thái","Chúa công, không xong rồi! Tào Tháo đã trúng mai phục ngoài thành Huỳnh Dương, bị đại quân của Đổng Trác đánh giáp công, rất nguy hiểm!","Chúa công, không xong rồi! Tào Tháo đã trúng mai phục ngoài thành Huỳnh Dương, bị đại quân của Đổng Trác đánh giáp công, rất nguy hiểm!","Nhân vật","",},
        [328] = {902,"Nhân vật","Tinh Thái muội muội đừng lo! Đã có ta!","Tinh Thái muội muội đừng lo! Đã có ta!","Tinh Thái","",},
        [329] = {902,"Tinh Thái","Chúa công, người kia hình như là Hạ Hầu Uyên, chúng ta hãy đi hỏi tình hình.","Chúa công, người kia hình như là Hạ Hầu Uyên, chúng ta hãy đi hỏi tình hình.","Nhân vật","",},
        [330] = {902,"Hạ Hầu Uyên","Lại có truy binh! Chúa công hãy đi trước, Hạ Hầu Uyên sẽ chặn hậu!","Lại có truy binh! Chúa công hãy đi trước, Hạ Hầu Uyên sẽ chặn hậu!","Nhân vật","",},
        [331] = {902,"Hạ Hầu Uyên","Các ngươi là ai?","Các ngươi là ai?","Nhân vật","",},
        [332] = {902,"Ngân Bình","Tên Hạ Hầu Uyên không biết điều! Ngay cả chúa công nhà ta cũng không biết!","Tên Hạ Hầu Uyên không biết điều! Ngay cả chúa công nhà ta cũng không biết!","Hạ Hầu Uyên","",},
        [333] = {903,"Tinh Thái","Tào Tháo, vai người trúng tên, để ta trị thương.","Tào Tháo, vai người trúng tên, để ta trị thương.","Tào Tháo","",},
        [334] = {903,"Tào Tháo","Là ai muốn hãm hại ta?","Là ai muốn hãm hại ta?","Tinh Thái","",},
        [335] = {903,"Tinh Thái","Chúa công, Tào Tháo chắc là do vết thương nhiễm trùng nên thần trí không được minh mẫn.","Chúa công, Tào Tháo chắc là do vết thương nhiễm trùng nên thần trí không được minh mẫn.","Tào Tháo","",},
        [336] = {903,"Ngân Bình","Tào Tháo rất đa nghi, ngay cả chúa công cũng không nhận ra.","Tào Tháo rất đa nghi, ngay cả chúa công cũng không nhận ra.","Tào Tháo","",},
        [337] = {903,"Nhân vật","Tin chắc là Tào huynh chinh chiến liên tục, lại bị thương, nên không rõ địch ta, chúng ta phải đánh ngất người để trị thương.","Tin chắc là Tào huynh chinh chiến liên tục, lại bị thương, nên không rõ địch ta, chúng ta phải đánh ngất người để trị thương.","Tào Tháo","",},
        [338] = {904,"Tinh Thái","Tào Tháo, người khỏe lại chưa?","Tào Tháo, người khỏe lại chưa?","Tào Tháo","",},
        [339] = {904,"Tào Tháo","Đa tạ anh hùng cứu giúp! Đa tạ y thuật của Tinh Thái cô nương. Cô nương y thuật tài giỏi, không biết sư phụ là ai?","Đa tạ anh hùng cứu giúp! Đa tạ y thuật của Tinh Thái cô nương. Cô nương y thuật tài giỏi, không biết sư phụ là ai?","Tinh Thái","",},
        [340] = {904,"Tinh Thái","Sư phụ của ta là Thần Y Hoa Đà, có một vị đạo nhân tên Tả Từ mang cho ta một túi thuốc, bảo ta hết lòng trợ giúp người có thiên mệnh.","Sư phụ của ta là Thần Y Hoa Đà, có một vị đạo nhân tên Tả Từ mang cho ta một túi thuốc, bảo ta hết lòng trợ giúp người có thiên mệnh.","Tào Tháo","",},
        [341] = {904,"Tào Tháo","Y thuật của Tinh Thái cô nương thì ra có nguồn gốc kỳ lạ như thế. Vậy ai là người có thiên mệnh?","Y thuật của Tinh Thái cô nương thì ra có nguồn gốc kỳ lạ như thế. Vậy ai là người có thiên mệnh?","Tinh Thái","",},
        [342] = {904,"Tinh Thái","Chúa công nhà ta chính là người có thiên mệnh! Người sẽ là người kết thúc loạn thế này!","Chúa công nhà ta chính là người có thiên mệnh! Người sẽ là người kết thúc loạn thế này!","Tào Tháo","",},
