        [344] = {904,"Ngân Bình","Chúa công, còn có ta! Ta sẽ giúp người đánh nhau!","Chúa công, còn có ta! Ta sẽ giúp người đánh nhau!","Tinh Thái","",},
        [345] = {904,"Nhân vật","Được! Được!","Được! Được!","Ngân Bình","",},
        [346] = {904,"Tào Tháo","Quá giỏi, vị tiểu anh hùng, thủ hạ của người quả thật tài giỏi.","Quá giỏi, vị tiểu anh hùng, thủ hạ của người quả thật tài giỏi.","Nhân vật","",},
        [347] = {905,"Hạ Hầu Đôn","Chúa công, Hạ Hầu Đôn dẫn theo vài kỵ binh đến, Tào Nhân Lý Điển Nhạc Tiến cũng đã đến.","Chúa công, Hạ Hầu Đôn dẫn theo vài kỵ binh đến, Tào Nhân Lý Điển Nhạc Tiến cũng đã đến.","Tào Tháo","",},
        [348] = {905,"Tào Tháo","Huỳnh Dương Thái Thú Từ Vinh mai phục ta, hôm nay đa tạ anh hùng cứu giúp, nếu không đã nguy mất.","Huỳnh Dương Thái Thú Từ Vinh mai phục ta, hôm nay đa tạ anh hùng cứu giúp, nếu không đã nguy mất.","Hạ Hầu Đôn","",},
        [349] = {905,"Hạ Hầu Đôn","Hạ Hầu Đôn có một thỉnh cầu!","Hạ Hầu Đôn có một thỉnh cầu!","Nhân vật","",},
        [350] = {905,"Nhân vật","Xin cứ nói.","Xin cứ nói.","Hạ Hầu Đôn","",},
        [351] = {905,"Hạ Hầu Đôn","Nghe nói tướng quân ở Hổ Lao Quan bốn lần chiến Lữ Bố, uy chấn chư hầu, lực chiến cao cường, danh tiếng khắp thiên hạ, không biết Hạ Hầu Đôn có thể tỉ thí với tướng quân không?","Nghe nói tướng quân ở Hổ Lao Quan bốn lần chiến Lữ Bố, uy chấn chư hầu, lực chiến cao cường, danh tiếng khắp thiên hạ, không biết Hạ Hầu Đôn có thể tỉ thí với tướng quân không?","Nhân vật","",},
        [352] = {905,"Nhân vật","Chỉ có không ngừng chiến đấu mới có thể gia tăng thực lực. Hạ Hầu tướng quân hãy ra chiêu!","Chỉ có không ngừng chiến đấu mới có thể gia tăng thực lực. Hạ Hầu tướng quân hãy ra chiêu!","Hạ Hầu Đôn","",},
        [353] = {906,"Hạ Hầu Đôn","Tướng quân quả thật lợi, Hạ Hầu Đôn chịu thua. Danh Tướng Lệnh xin tặng cho tướng quân!","Tướng quân quả thật lợi, Hạ Hầu Đôn chịu thua. Danh Tướng Lệnh xin tặng cho tướng quân!","Nhân vật","",},
        [354] = {906,"Nhân vật","Được! Được!","Được! Được!","Tào Tháo","",},
        [355] = {906,"Tào Tháo","Đổng Trác lừa dối thiên tử, hãm hại bá tánh, gây nhiều tội ác, ai ai cũng căm hận!","Đổng Trác lừa dối thiên tử, hãm hại bá tánh, gây nhiều tội ác, ai ai cũng căm hận!","Nhân vật","",},
        [356] = {906,"Tào Tháo","Nhưng tiếc, trong trận chiến tại Huỳnh Dương ta bị bại, nếu không ta quyết lấy thủ cấp của Đổng Trác!","Nhưng tiếc, trong trận chiến tại Huỳnh Dương ta bị bại, nếu không ta quyết lấy thủ cấp của Đổng Trác!","Nhân vật","",},
        [357] = {906,"Nhân vật","Tào huynh yên tâm, đợi ta đến Trường An sẽ diệt Đổng Trác!","Tào huynh yên tâm, đợi ta đến Trường An sẽ diệt Đổng Trác!","Tào Tháo","",},
        [358] = {906,"Tào Tháo","Quả là anh hùng hào kiệt! Diệt Đổng Trác nhờ vào ngươi! Ta sẽ về Nội Hân chiêu mộ binh lính, hẹn ngày tái ngộ.","Quả là anh hùng hào kiệt! Diệt Đổng Trác nhờ vào ngươi! Ta sẽ về Nội Hân chiêu mộ binh lính, hẹn ngày tái ngộ.","Nhân vật","",},
        [359] = {906,"Ngân Bình","Đổng Trác sẽ không thể sống lâu! Chúng ta hãy mau đến Trường An diệt Đổng Trác!","Đổng Trác sẽ không thể sống lâu! Chúng ta hãy mau đến Trường An diệt Đổng Trác!","Tinh Thái","",},
        [360] = {906,"Tinh Thái","Việc này không thể gấp! Lần trước ám sát Đổng Trác thất bại, xung quanh Đổng Trác rất đông binh lính. Chúa công, hay là chúng ta hãy vào Trường An trước, sau đó tìm Vương Doãn do thám tình hình?","Việc này không thể gấp! Lần trước ám sát Đổng Trác thất bại, xung quanh Đổng Trác rất đông binh lính. Chúa công, hay là chúng ta hãy vào Trường An trước, sau đó tìm Vương Doãn do thám tình hình?","Nhân vật","",},
        [361] = {906,"Nhân vật","Được, cứ theo lời Tinh Thái! Chúng ta đến Trường An tìm Vương Doãn!","Được, cứ theo lời Tinh Thái! Chúng ta đến Trường An tìm Vương Doãn!","Tinh Thái","",},
        [362] = {1001,"Tinh Thái","Đổng Trác phóng hỏa Lạc Dương, ép bá tánh đến Trường An. Chúa công, phía trước là phủ đệ của Vương Doãn tại Trường An.","Đổng Trác phóng hỏa Lạc Dương, ép bá tánh đến Trường An. Chúa công, phía trước là phủ đệ của Vương Doãn tại Trường An.","Ngân Bình","",},
        [363] = {1001,"Ngân Bình","Đó không phải là Lữ Bố sao? Hắn tìm Vương Doãn có việc gì?","Đó không phải là Lữ Bố sao? Hắn tìm Vương Doãn có việc gì?","Tinh Thái","",},
        [364] = {1001,"Lữ Bố","Ta và Điêu Thuyền yêu nhau, Vương Tư Đồ hứa gả Điêu Thuyền cho ta, sao lại đưa đến bên thái sư, vậy là có ý gì?","Ta và Điêu Thuyền yêu nhau, Vương Tư Đồ hứa gả Điêu Thuyền cho ta, sao lại đưa đến bên thái sư, vậy là có ý gì?","Vương Doãn","",},
        [365] = {1001,"Vương Doãn","Tướng quân đã trách nhầm lão phu! Hôm qua thái sư đến phủ nói chọn ngày tháng tốt nên đã lệnh ta đưa Điêu Thuyền qua đó cho tướng quân.","Tướng quân đã trách nhầm lão phu! Hôm qua thái sư đến phủ nói chọn ngày tháng tốt nên đã lệnh ta đưa Điêu Thuyền qua đó cho tướng quân.","Lữ Bố","",},
        [366] = {1001,"Vương Doãn","Tướng quân hãy suy nghĩ, thái sư đích thân đến, lão phu làm sao cản được?","Tướng quân hãy suy nghĩ, thái sư đích thân đến, lão phu làm sao cản được?","Lữ Bố","",},
        [367] = {1001,"Lữ Bố","Tư đồ đừng trách tội, là Lữ Bố hiểu lầm, để ngày sau quay về taa5 tội. (Lữ Bố lập tức về Đổng phủ)","Tư đồ đừng trách tội, là Lữ Bố hiểu lầm, để ngày sau quay về taa5 tội. (Lữ Bố lập tức về Đổng phủ)","Vương Doãn","",},
        [368] = {1001,"Ngân Bình","Vừa rồi ở phủ của Vương Doãn đã chứng kiến một việc.","Vừa rồi ở phủ của Vương Doãn đã chứng kiến một việc.","Tinh Thái","",},
        [369] = {1001,"Tinh Thái","Điêu Thuyền tỉ tỉ quả thật khổ, Đổng Trác háo sắc, chắc...","Điêu Thuyền tỉ tỉ quả thật khổ, Đổng Trác háo sắc, chắc...","Ngân Bình","",},
        [370] = {1001,"Nhân vật","Chúng ta mau tìm Vương Doãn hỏi rõ, không thể để Điêu Thuyền chịu khổ!","Chúng ta mau tìm Vương Doãn hỏi rõ, không thể để Điêu Thuyền chịu khổ!","Ngân Bình","",},
        [371] = {1002,"Nhân vật","Vương Doãn, tại sao ngươi lại đưa Điêu Thuyền qua bên Đổng Trác?","Vương Doãn, tại sao ngươi lại đưa Điêu Thuyền qua bên Đổng Trác?","Vương Doãn","",},
        [372] = {1002,"Vương Doãn","Bá tánh đang chịu nguy hiểm. Ác tặc Đổng Trác, muốn soán ngôi vua.","Bá tánh đang chịu nguy hiểm. Ác tặc Đổng Trác, muốn soán ngôi vua.","Nhân vật","",},
        [373] = {1002,"Vương Doãn","Lão phu dùng kế mỹ nhân, để Điêu Thuyền ly gián Đổng Trác và Lữ Bố, Lữ Bố tin chắc sẽ diệt Đổng Trác.","Lão phu dùng kế mỹ nhân, để Điêu Thuyền ly gián Đổng Trác và Lữ Bố, Lữ Bố tin chắc sẽ diệt Đổng Trác.","Nhân vật","",},
        [374] = {1002,"Nhân vật","Diệt Đổng Trác, hãy để ta! Sao lại để phận gái yếu đuối phải hi sinh!","Diệt Đổng Trác, hãy để ta! Sao lại để phận gái yếu đuối phải hi sinh!","Vương Doãn","",},
        [375] = {1002,"Nhân vật","Chúng ta mau đi cứu Điêu Thuyền!","Chúng ta mau đi cứu Điêu Thuyền!","","",},
        [376] = {1003,"Tinh Thái","Điêu Thuyền tỉ tỉ, chúng tôi đến cứu tỉ, hãy mau theo chúng tôi.","Điêu Thuyền tỉ tỉ, chúng tôi đến cứu tỉ, hãy mau theo chúng tôi.","Điêu Thuyền","",},
        [377] = {1003,"Điêu Thuyền","Tư đồ chưa từng ép ta, là do ta tự nguyện, chỉ cần diệt được Đổng Trắc, thiên hạ sẽ được bình yên!","Tư đồ chưa từng ép ta, là do ta tự nguyện, chỉ cần diệt được Đổng Trắc, thiên hạ sẽ được bình yên!","Tinh Thái","",},
        [378] = {1003,"Tinh Thái","Điêu Thuyền tỉ tỉ quả là một cô gái kiên cường.","Điêu Thuyền tỉ tỉ quả là một cô gái kiên cường.","Điêu Thuyền","",},
        [379] = {1003,"Tinh Thái","Điêu Thuyền tỉ tỉ yên tâm, chúa công nhà ta tin chắc có thể diệt Đổng Trác, cho Điêu Thuyền cô nương một cuộc sống bình an.","Điêu Thuyền tỉ tỉ yên tâm, chúa công nhà ta tin chắc có thể diệt Đổng Trác, cho Điêu Thuyền cô nương một cuộc sống bình an.","Điêu Thuyền","",},
        [380] = {1004,"Nhân vật","Lữ Bố, hãy đi diệt Đổng Trác, Điêu Thuyền nếu đồng ý theo ngươi, ta sẽ không cản!","Lữ Bố, hãy đi diệt Đổng Trác, Điêu Thuyền nếu đồng ý theo ngươi, ta sẽ không cản!","Lữ Bố","",},
        [381] = {1004,"Lữ Bố","Tuy ta không thích Đổng Trác, nhưng Đổng Trác với ta là tình phụ tử, diệt hắn sẽ bị người đời dị nghị!","Tuy ta không thích Đổng Trác, nhưng Đổng Trác với ta là tình phụ tử, diệt hắn sẽ bị người đời dị nghị!","Nhân vật","",},
