        [386] = {1004,"Nhân vật","Lữ Bố thật có khí thế! Quả đúng là anh hùng hào kiệt! Ta rất thích!","Lữ Bố thật có khí thế! Quả đúng là anh hùng hào kiệt! Ta rất thích!","Lữ Bố","",},
        [387] = {1005,"Đổng Trác","Ta phải mau vào cung! Thiên tử truyền gọi, nói sẽ nhường ngôi cho ta. Đổng Trác ta quả thật có thể làm hoàng đế rồi!","Ta phải mau vào cung! Thiên tử truyền gọi, nói sẽ nhường ngôi cho ta. Đổng Trác ta quả thật có thể làm hoàng đế rồi!","Nhân vật","",},
        [388] = {1005,"Nhân vật","Đổng Trác, ngươi dám ức hiếp Điêu Thuyền, hãy nộp mạng!","Đổng Trác, ngươi dám ức hiếp Điêu Thuyền, hãy nộp mạng!","Đổng Trác","",},
