        [392] = {1006,"Đổng Trác","Ta không phục, ta vốn là Thái Sư Đại Hán... Ta sắp có Điêu Thuyền... Ta sắp làm hoàng thượng... (Đổng Trác bị diệt)","Ta không phục, ta vốn là Thái Sư Đại Hán... Ta sắp có Điêu Thuyền... Ta sắp làm hoàng thượng... (Đổng Trác bị diệt)","Lữ Bố","",},
        [393] = {1006,"Lữ Bố","Thuyền Nhi, kể từ khi chia tay tại Phụng Nghi Đình, ta ngày đêm nhớ nàng!","Thuyền Nhi, kể từ khi chia tay tại Phụng Nghi Đình, ta ngày đêm nhớ nàng!","Điêu Thuyền","",},
