        [397] = {1006,"Nhân vật","Khụ! (Xin hai vị đừng bày tỏ tình cảm nữa, ta chịu không nổi rồi!)","Khụ! (Xin hai vị đừng bày tỏ tình cảm nữa, ta chịu không nổi rồi!)","Điêu Thuyền","",},
        [398] = {1006,"Điêu Thuyền","Để tướng quân cười chê! Đa tạ tướng quân cứu giúp! Tại Mi Ổ, Đổng tặc từng tặng ta 1 Danh Tướng Lệnh, mong tướng quân hãy tiếp nhận!","Để tướng quân cười chê! Đa tạ tướng quân cứu giúp! Tại Mi Ổ, Đổng tặc từng tặng ta 1 Danh Tướng Lệnh, mong tướng quân hãy tiếp nhận!","Nhân vật","",},
        [399] = {1006,"Nhân vật","Đa tạ Điêu Thuyền cô nương! Vậy ta không khách khí!","Đa tạ Điêu Thuyền cô nương! Vậy ta không khách khí!","Lữ Bố","",},
        [400] = {1006,"Lữ Bố","Tiểu anh hùng, từ giờ ngươi sẽ là bằng hữu của ta!","Tiểu anh hùng, từ giờ ngươi sẽ là bằng hữu của ta!","Nhân vật","",},
        [401] = {1006,"Lữ Bố","Sau này khi có cần đến Lữ Phụng Tiên, chỉ cần lên tiếng, ta sẽ không từ chối.","Sau này khi có cần đến Lữ Phụng Tiên, chỉ cần lên tiếng, ta sẽ không từ chối.","Nhân vật","",},
        [402] = {1006,"Nhân vật","Ta chỉ có một yêu cầu là sau này đối xử tốt với Điêu Thuyền cô nương! Cáo từ!","Ta chỉ có một yêu cầu là sau này đối xử tốt với Điêu Thuyền cô nương! Cáo từ!","Lữ Bố","",},
        [403] = {1006,"Tinh Thái","Chúa công, giờ chúng ta đi đâu?","Chúa công, giờ chúng ta đi đâu?","Nhân vật","",},
        [404] = {1006,"Ngân Bình","Đổng Trác sau khi bị diệt, thuộc hạ Lý Thôi Quách Tị đều đã chạy trốn đến Lương Châu.","Đổng Trác sau khi bị diệt, thuộc hạ Lý Thôi Quách Tị đều đã chạy trốn đến Lương Châu.","Nhân vật","",},
        [405] = {1006,"Nhân vật","Họ cũng là người xấu, Đổng Trác cũng vì thế mà có thể làm càn, chúng ta phải truy đuổi!","Họ cũng là người xấu, Đổng Trác cũng vì thế mà có thể làm càn, chúng ta phải truy đuổi!","Ngân Bình","",},
        [406] = {1101,"Lý Thôi","Lại là ngươi! Là ngươi tự đến nộp mạng!","Lại là ngươi! Là ngươi tự đến nộp mạng!","Ngân Bình","",},
        [407] = {1101,"Ngân Bình","Đừng nhiều lời! Ngươi chẳng đáng hầu hạ cho chúa công!","Đừng nhiều lời! Ngươi chẳng đáng hầu hạ cho chúa công!","Lý Thôi","",},
