        [409] = {1102,"Lý Thôi","Lại thất bại! Tiểu tử, lần sau ta quyết diệt ngươi. (Lý Thôi nhanh chóng trốn mất)","Lại thất bại! Tiểu tử, lần sau ta quyết diệt ngươi. (Lý Thôi nhanh chóng trốn mất)","Tinh Thái","",},
        [410] = {1102,"Tinh Thái","Có bản lĩnh thì đừng chạy!","Có bản lĩnh thì đừng chạy!","Lý Thôi","",},
        [411] = {1102,"Ngân Bình","Phía trước còn có Quách Tị, chúng ta hãy diệt Quách Tị!","Phía trước còn có Quách Tị, chúng ta hãy diệt Quách Tị!","Lý Thôi","",},
        [412] = {1103,"Giả Hủ","Mọi người đừng vội, ta có một kế!","Mọi người đừng vội, ta có một kế!","Lý Thôi","",},
        [413] = {1103,"Giả Hủ","Nếu thành công có thể quay lại làm việc cho triều đình, nếu không thành, chúng ta mới chạy trốn tiếp.","Nếu thành công có thể quay lại làm việc cho triều đình, nếu không thành, chúng ta mới chạy trốn tiếp.","Lý Thôi","",},
        [414] = {1103,"Lý Thôi","Văn Hòa, ngươi có cách gì không?","Văn Hòa, ngươi có cách gì không?","Giả Hủ","",},
        [415] = {1103,"Giả Hủ","Các vị nếu tự ý bỏ trốn, chỉ cần một nhóm lính nhỏ cũng có thể bắt được, chi bằng tập trung binh lính tấn công Trường An, trả thù cho Đổng Trác.","Các vị nếu tự ý bỏ trốn, chỉ cần một nhóm lính nhỏ cũng có thể bắt được, chi bằng tập trung binh lính tấn công Trường An, trả thù cho Đổng Trác.","Lý Thôi","",},
        [416] = {1103,"Lý Thôi","Kế này được, Vương Doãn đang ở Trường An tiêu diệt thuộc hạ cũ của Đổng Trác. Chúng ta loan tin nói Vương Doãn muốn diệt hết người Tây Lương, như thế có thể tập trung binh lính tấn công về Trường An.","Kế này được, Vương Doãn đang ở Trường An tiêu diệt thuộc hạ cũ của Đổng Trác. Chúng ta loan tin nói Vương Doãn muốn diệt hết người Tây Lương, như thế có thể tập trung binh lính tấn công về Trường An.","Giả Hủ","",},
        [417] = {1104,"Tinh Thái","Chúa công nguy rồi! Lý Thôi Quách Tị sau khi chạy trốn, nghe kế của Giả Hủ, truyền tin khắp nơi nói triều đình muốn diệt hết người Tây Lương, họ tập hợp được gần 10 vạn người, phản công Trường An.","Chúa công nguy rồi! Lý Thôi Quách Tị sau khi chạy trốn, nghe kế của Giả Hủ, truyền tin khắp nơi nói triều đình muốn diệt hết người Tây Lương, họ tập hợp được gần 10 vạn người, phản công Trường An.","Nhân vật","",},
        [418] = {1104,"Tinh Thái","Nghe nói Vương Doãn đã bị diệt, Lý Thôi Quách Tị còn ép Hiến Đế phong họ làm Đại Tướng Quân!","Nghe nói Vương Doãn đã bị diệt, Lý Thôi Quách Tị còn ép Hiến Đế phong họ làm Đại Tướng Quân!","Nhân vật","",},
        [419] = {1104,"Nhân vật","Sao lại như thế! Còn Lữ Bố đâu, không phải hắn ta trấn giữ Trường An sao?","Sao lại như thế! Còn Lữ Bố đâu, không phải hắn ta trấn giữ Trường An sao?","Tinh Thái","",},
        [420] = {1104,"Ngân Bình","Lữ Bố tuy anh dũng, nhưng binh lính quá ít, Tây Lương có hơn 10 vạn quân! Nghe nói hắn đã dẫn vài thuộc hạ chạy đến Hoài Nam nhờ Viên Thuật!","Lữ Bố tuy anh dũng, nhưng binh lính quá ít, Tây Lương có hơn 10 vạn quân! Nghe nói hắn đã dẫn vài thuộc hạ chạy đến Hoài Nam nhờ Viên Thuật!","Nhân vật","",},
        [421] = {1104,"Nhân vật","Nếu như thế, chúng ta phải nhanh chóng về Trường An!","Nếu như thế, chúng ta phải nhanh chóng về Trường An!","Ngân Bình","",},
        [422] = {1105,"Tinh Thái","Giả Hủ, nếu không phải kế của ngươi, Trường An sao lại phải chịu tai kiếp này!","Giả Hủ, nếu không phải kế của ngươi, Trường An sao lại phải chịu tai kiếp này!","Giả Hủ","",},
        [423] = {1105,"Giả Hủ","Ta vốn là người Lương Châu, đây chỉ là kế bảo tồn tính mạng! Nếu không phải ta, văn võ bá quan trong Trường An mất mạng càng nhiều!","Ta vốn là người Lương Châu, đây chỉ là kế bảo tồn tính mạng! Nếu không phải ta, văn võ bá quan trong Trường An mất mạng càng nhiều!","Tinh Thái","",},
        [424] = {1105,"Giả Hủ","Các ngươi yên tâm, có ta nhất định sẽ khống chế Lý Thôi Quách Tị, bảo vệ bá quan và thiên tử. (Giả Hủ chạy trốn)","Các ngươi yên tâm, có ta nhất định sẽ khống chế Lý Thôi Quách Tị, bảo vệ bá quan và thiên tử. (Giả Hủ chạy trốn)","Tinh Thái","",},
        [425] = {1105,"Tinh Thái","Đáng hận! Giả Hủ thật gian xảo, lại để hắn chạy thoát!","Đáng hận! Giả Hủ thật gian xảo, lại để hắn chạy thoát!","Giả Hủ","",},
        [426] = {1106,"Tinh Thái","Chúa công, Mã Đằng, Hàn Toại dẫn binh đến thảo phạt Lý Thôi Quách Tị!","Chúa công, Mã Đằng, Hàn Toại dẫn binh đến thảo phạt Lý Thôi Quách Tị!","Nhân vật","",},
        [427] = {1106,"Tinh Thái","Đó là Mã Siêu con trai của Mã Đằng, ta nghĩ hắn đã hiểu lầm chúng ta!","Đó là Mã Siêu con trai của Mã Đằng, ta nghĩ hắn đã hiểu lầm chúng ta!","Nhân vật","",},
        [428] = {1106,"Ngân Bình","Mã Siêu gương mặt tú tuấn, mắt sáng như sao, thần hình cao to oai vệ, chỉ mới 17 tuổi, quả thật anh dũng!","Mã Siêu gương mặt tú tuấn, mắt sáng như sao, thần hình cao to oai vệ, chỉ mới 17 tuổi, quả thật anh dũng!","Tinh Thái","",},
        [429] = {1106,"Tinh Thái","Ngân Bình tỉ tỉ, chẳng lẽ người động lòng?","Ngân Bình tỉ tỉ, chẳng lẽ người động lòng?","Ngân Bình","",},
        [430] = {1106,"Ngân Bình","Ta chỉ thích đối thủ có thực lực mạnh mẽ!","Ta chỉ thích đối thủ có thực lực mạnh mẽ!","Tinh Thái","",},
