        [432] = {1106,"Nhân vật","Lại một vị danh tướng. Chúng ta hãy ra gặp hắn!","Lại một vị danh tướng. Chúng ta hãy ra gặp hắn!","Mã Siêu","",},
        [433] = {1106,"Tinh Thái","Mã Siêu ngươi đến thảo phạt Lý Thôi Quách Tị đúng không? Chúng ta là liên minh, chẳng lẽ ngươi không biết chúa công ta?","Mã Siêu ngươi đến thảo phạt Lý Thôi Quách Tị đúng không? Chúng ta là liên minh, chẳng lẽ ngươi không biết chúa công ta?","Mã Siêu","",},
        [434] = {1106,"Ngân Bình","Đánh bại hắn ta nói tiếp!","Đánh bại hắn ta nói tiếp!","Mã Siêu","",},
        [435] = {1107,"Nhân vật","Trận đấu thật sảng khoái!","Trận đấu thật sảng khoái!","Mã Siêu","",},
        [436] = {1107,"Mã Siêu","Thì ra người chính là vị anh hùng diệt Đổng Trác, danh chấn 18 chư hầu! Mã Siêu có thể quen biết tướng quân quả là vinh dự của Mã Siêu!","Thì ra người chính là vị anh hùng diệt Đổng Trác, danh chấn 18 chư hầu! Mã Siêu có thể quen biết tướng quân quả là vinh dự của Mã Siêu!","Nhân vật","",},
        [437] = {1107,"Nhân vật","Nhưng tiếc để Mạnh Khởi huynh đến công không rồi! Lý Thôi Quách Tị Giả Hủ đều đã bị ta đánh bại!","Nhưng tiếc để Mạnh Khởi huynh đến công không rồi! Lý Thôi Quách Tị Giả Hủ đều đã bị ta đánh bại!","Mã Siêu","",},
        [438] = {1107,"Mã Siêu","Ta có thể quen biết người bằng hữu như người đã là không uổng phí đời người!","Ta có thể quen biết người bằng hữu như người đã là không uổng phí đời người!","Nhân vật","",},
        [439] = {1107,"Mã Siêu","Huynh đệ, đại quân ta sắp về Lương Châu! Danh Tướng Lệnh này ta tặng cho ngươi. Sau này nếu có cơ hội đến Lương Châu, Mã Mạnh sẽ đón tiếp chu đáo! (Mã Siêu cáo từ)","Huynh đệ, đại quân ta sắp về Lương Châu! Danh Tướng Lệnh này ta tặng cho ngươi. Sau này nếu có cơ hội đến Lương Châu, Mã Mạnh sẽ đón tiếp chu đáo! (Mã Siêu cáo từ)","Nhân vật","",},
        [440] = {1107,"Tinh Thái","Chúa công, Trung Nguyên tạm thời không có chiến sự, tiếp theo chúng ta đi đâu?","Chúa công, Trung Nguyên tạm thời không có chiến sự, tiếp theo chúng ta đi đâu?","Nhân vật","",},
        [441] = {1107,"Nhân vật","Vùng đông bắc của U Châu Thanh Châu Ký Châu đang có chiến sự lớn! Đại ca cùng Công Tôn Toản tại U Châu, không biết hiện giờ ra sao?","Vùng đông bắc của U Châu Thanh Châu Ký Châu đang có chiến sự lớn! Đại ca cùng Công Tôn Toản tại U Châu, không biết hiện giờ ra sao?","Tinh Thái","",},
        [442] = {1107,"Nhân vật","Chúng ta đến U Châu tìm Lưu Bị đại ca và cha của các ngươi!","Chúng ta đến U Châu tìm Lưu Bị đại ca và cha của các ngươi!","Tinh Thái","",},
        [443] = {1107,"Tinh Thái","Lúc trước cha bên cạnh, ta thường xuyên trách cha nói nhiều, giờ đã lâu không gặp, lại cảm thấy nhớ người!","Lúc trước cha bên cạnh, ta thường xuyên trách cha nói nhiều, giờ đã lâu không gặp, lại cảm thấy nhớ người!","Nhân vật","",},
        [444] = {1201,"Tinh Thái","Chúa công, đường đến U Châu còn xa, chúng ta hãy nghỉ ngợi tại Ký Châu, sau đó đi tiếp U Châu, ý người sao?","Chúa công, đường đến U Châu còn xa, chúng ta hãy nghỉ ngợi tại Ký Châu, sau đó đi tiếp U Châu, ý người sao?","Nhân vật","",},
        [445] = {1201,"Ngân Bình","Chúa công, Công Tôn Toản phái người mang thư đến!","Chúa công, Công Tôn Toản phái người mang thư đến!","Nhân vật","",},
        [446] = {1201,"Nhân vật","Trong thư nói, Viên Thiệu bảo Công Tôn Toản đánh Ký Châu, sau khi hạ Ký Châu sẽ chia đôi.","Trong thư nói, Viên Thiệu bảo Công Tôn Toản đánh Ký Châu, sau khi hạ Ký Châu sẽ chia đôi.","Ngân Bình","",},
        [447] = {1201,"Ngân Bình","Viên Thiệu thật gian xảo, hắn muốn đoạt Ký Châu nhưng lại để Công Tôn Toản xuất binh.","Viên Thiệu thật gian xảo, hắn muốn đoạt Ký Châu nhưng lại để Công Tôn Toản xuất binh.","Nhân vật","",},
        [448] = {1201,"Nhân vật","Ngân Bình nói đúng, Ký Châu Mục hiện tại là ai?","Ngân Bình nói đúng, Ký Châu Mục hiện tại là ai?","Ngân Bình","",},
        [449] = {1201,"Tinh Thái","Chúa công, là Hàn Phức! Công Tôn Toản đánh Ký Châu, phụ thân sẽ tham gia, chúng ta nhanh đi giúp ngài!","Chúa công, là Hàn Phức! Công Tôn Toản đánh Ký Châu, phụ thân sẽ tham gia, chúng ta nhanh đi giúp ngài!","Nhân vật","",},
        [450] = {1202,"Tinh Thái","Đến trễ rồi! Hàn Phức đã dâng Ký Châu đầu hàng Viên Thiệu!","Đến trễ rồi! Hàn Phức đã dâng Ký Châu đầu hàng Viên Thiệu!","Hàn Phức","",},
        [451] = {1202,"Nhân vật","Hàn Phức, sao ngươi lại dễ dàng dâng Ký Châu đầu hàng Viên Thiệu chứ?","Hàn Phức, sao ngươi lại dễ dàng dâng Ký Châu đầu hàng Viên Thiệu chứ?","Hàn Phức","",},
        [452] = {1202,"Ngân Bình","Viên Thiệu chỉ là một Thái Thú nho nhỏ, Ký Châu tài nguyên dồi dào, binh lực hùng hậu, có gì phải sợ?","Viên Thiệu chỉ là một Thái Thú nho nhỏ, Ký Châu tài nguyên dồi dào, binh lực hùng hậu, có gì phải sợ?","Hàn Phức","",},
        [453] = {1202,"Ngân Bình","Hàn Phức, chẳng lẽ ngươi không biết Công Tôn Toản đánh Ký Châu là gian kế của Viên Thiệu sao?","Hàn Phức, chẳng lẽ ngươi không biết Công Tôn Toản đánh Ký Châu là gian kế của Viên Thiệu sao?","Hàn Phức","",},
        [454] = {1202,"Hàn Phức","Cái gì? Kế của Viên Thiệu, ta trúng kế rồi!","Cái gì? Kế của Viên Thiệu, ta trúng kế rồi!","Ngân Bình","",},
        [455] = {1202,"Ngân Bình","Ngươi yếu đuối như thế, sao triều đình lại để ngươi làm Ký Châu Mục chứ?","Ngươi yếu đuối như thế, sao triều đình lại để ngươi làm Ký Châu Mục chứ?","Hàn Phức","",},
