        [474] = {1204,"Trương Phi","Tứ đệ tăng lực chiến rồi, đến đây cùng ta đánh thử nào!","Tứ đệ tăng lực chiến rồi, đến đây cùng ta đánh thử nào!","Nhân vật","",},
        [475] = {1205,"Lưu Bị","Công Tôn huynh đến Thảo Nguyên làm gì?","Công Tôn huynh đến Thảo Nguyên làm gì?","Công Tôn Toản","",},
        [476] = {1205,"Công Tôn Toản","Huyền Đức, nhanh giao tứ đệ ngươi ra đây, hắn ở Ký Châu tiêu diệt đệ đệ Công Tôn Việt của ta!","Huyền Đức, nhanh giao tứ đệ ngươi ra đây, hắn ở Ký Châu tiêu diệt đệ đệ Công Tôn Việt của ta!","Lưu Bị","",},
        [477] = {1205,"Nhân vật","Công Tôn Toản, chớ vu oan giá họa! Ta đấu với Công Tôn Việt nhưng không hạ sát thủ!","Công Tôn Toản, chớ vu oan giá họa! Ta đấu với Công Tôn Việt nhưng không hạ sát thủ!","Công Tôn Toản","",},
