        [481] = {1206,"Trương Phi","Đúng! Tứ đệ đã nói không là không!","Đúng! Tứ đệ đã nói không là không!","Công Tôn Toản","",},
        [482] = {1206,"Lưu Bị","Công Tôn huynh, ai nói với ngươi là ta diệt Công Tôn Việt!","Công Tôn huynh, ai nói với ngươi là ta diệt Công Tôn Việt!","Công Tôn Toản","",},
        [483] = {1206,"Công Tôn Toản","Viên Thiệu viết thư nói cho ta biết!","Viên Thiệu viết thư nói cho ta biết!","Lưu Bị","",},
        [484] = {1206,"Lưu Bị","Hừ! Đây rõ ràng là Viên Thiệu dùng kế đoạt Ký Châu nhưng không muốn chia phần cho ngươi, dùng kế để mượn tay tứ đệ ta diệt ngươi!","Hừ! Đây rõ ràng là Viên Thiệu dùng kế đoạt Ký Châu nhưng không muốn chia phần cho ngươi, dùng kế để mượn tay tứ đệ ta diệt ngươi!","Công Tôn Toản","",},
        [485] = {1206,"Trương Phi","Đại ca, thám tử báo lại, Viên Thiệu phái người giả mạ tứ đệ dùng loạn tiễn tiêu diệt Công Tôn Việt!","Đại ca, thám tử báo lại, Viên Thiệu phái người giả mạ tứ đệ dùng loạn tiễn tiêu diệt Công Tôn Việt!","Công Tôn Toản","",},
        [486] = {1206,"Công Tôn Toản","Viên Thiệu bỉ ổi vô liêm sỉ, ta phải tập hợp đại quân thảo phạt Viên Thiệu!","Viên Thiệu bỉ ổi vô liêm sỉ, ta phải tập hợp đại quân thảo phạt Viên Thiệu!","Trương Phi","",},
        [487] = {1206,"Công Tôn Toản","Công Tôn Toản đã hiểu lầm tướng quân! Danh Tướng Lệnh này xem như chuộc lỗi!","Công Tôn Toản đã hiểu lầm tướng quân! Danh Tướng Lệnh này xem như chuộc lỗi!","Trương Phi","",},
