        [489] = {1301,"Công Tôn Toản","Viên Thiệu, tên vô ơn nhà ngươi dám bán đứng ta?","Viên Thiệu, tên vô ơn nhà ngươi dám bán đứng ta?","Viên Thiệu","",},
        [490] = {1301,"Viên Thiệu","Hàn Phức bất tài, dâng Ký Châu cho ta, liên quan gì đến Công Tôn Toản nhà ngươi?","Hàn Phức bất tài, dâng Ký Châu cho ta, liên quan gì đến Công Tôn Toản nhà ngươi?","Công Tôn Toản","",},
        [491] = {1301,"Nhân vật","Viên Thiệu, khi xưa ta thấy ngươi là người trung nghĩa nên đề cử ngươi làm minh chủ, nay ngươi lại hãm hại ta sao!","Viên Thiệu, khi xưa ta thấy ngươi là người trung nghĩa nên đề cử ngươi làm minh chủ, nay ngươi lại hãm hại ta sao!","Viên Thiệu","",},
        [492] = {1301,"Nhân vật","Đầu hàng đi, ta sẽ tha mạng cho ngươi!","Đầu hàng đi, ta sẽ tha mạng cho ngươi!","Viên Thiệu","",},
        [493] = {1301,"Viên Thiệu","Nay ta có Ký Châu, văn thần võ tướng, lương thảo đầy đủ, sợ gì ngươi chứ!","Nay ta có Ký Châu, văn thần võ tướng, lương thảo đầy đủ, sợ gì ngươi chứ!","Nhân vật","",},
