        [508] = {1304,"Viên Thiệu","Không ngờ Viên Thiệu có ngày thất bại! Công Tôn Toản, hoàng thượng có chỉ.","Không ngờ Viên Thiệu có ngày thất bại! Công Tôn Toản, hoàng thượng có chỉ.","Công Tôn Toản","",},
        [509] = {1304,"Công Tôn Toản","Không ngờ hoàng thượng phái người đến giảng hòa, không xong rồi!","Không ngờ hoàng thượng phái người đến giảng hòa, không xong rồi!","Viên Thiệu","",},
        [510] = {1304,"Viên Thiệu","Danh Tướng Lệnh này tặng tướng quân để đền bù!","Danh Tướng Lệnh này tặng tướng quân để đền bù!","Nhân vật","",},
        [511] = {1304,"Nhân vật","Hừ! Danh Tướng Lệnh này xem như đổi một mạng của ngươi. (Công Tôn Toản và Viên Thiệu rời đi)","Hừ! Danh Tướng Lệnh này xem như đổi một mạng của ngươi. (Công Tôn Toản và Viên Thiệu rời đi)","Viên Thiệu","",},
        [512] = {1304,"Tinh Thái","Lúc trước ta cho rằng Công Tôn Toản là một anh hùng, nay xem ra vẫn kém xa Viên Thiệu.","Lúc trước ta cho rằng Công Tôn Toản là một anh hùng, nay xem ra vẫn kém xa Viên Thiệu.","Nhân vật","",},
        [513] = {1304,"Ngân Bình","Đúng thế! Lúc trước nói hay lắm, nay triều đình phái thuyết khách đến nói vài câu đã lui binh!","Đúng thế! Lúc trước nói hay lắm, nay triều đình phái thuyết khách đến nói vài câu đã lui binh!","Nhân vật","",},
        [514] = {1304,"Tinh Thái","May mà nhận được Danh Tướng Lệnh! Chúa công, giờ chúng ta đi đâu?","May mà nhận được Danh Tướng Lệnh! Chúa công, giờ chúng ta đi đâu?","Nhân vật","",},
        [515] = {1304,"Nhân vật","Giặc Hoàng Cân đang làm loạn Thanh Châu, Tào Tháo bị bao vây, chúng ta đi cứu giúp thôi!","Giặc Hoàng Cân đang làm loạn Thanh Châu, Tào Tháo bị bao vây, chúng ta đi cứu giúp thôi!","Tinh Thái","",},
        [516] = {1401,"Tào Tháo","May mà có anh hùng giúp đỡ ta mới đoạt được Thanh Châu!","May mà có anh hùng giúp đỡ ta mới đoạt được Thanh Châu!","Nhân vật","",},
        [517] = {1401,"Nhân vật","Mạnh Đức huynh, Hoàng Cân giải tán sẽ còn gây họa tại Thanh Châu, huynh thấy đúng chứ!","Mạnh Đức huynh, Hoàng Cân giải tán sẽ còn gây họa tại Thanh Châu, huynh thấy đúng chứ!","Tào Tháo","",},
        [518] = {1401,"Nhân vật","Chi bằng Mạnh Đức huynh hãy thu nạp bọn họ, tạo phúc cho bá tánh, triều đình an tâm sẽ có ban thưởng!","Chi bằng Mạnh Đức huynh hãy thu nạp bọn họ, tạo phúc cho bá tánh, triều đình an tâm sẽ có ban thưởng!","Tào Tháo","",},
        [519] = {1401,"Tào Tháo","Hay! Kế của anh hùng quá hay!","Hay! Kế của anh hùng quá hay!","Nhân vật","",},
        [520] = {1401,"Nhân vật","Tuy ta không ham vinh hoa phú quý nhưng không có nghĩa là từ chối","Tuy ta không ham vinh hoa phú quý nhưng không có nghĩa là từ chối","Tào Tháo","",},
        [521] = {1401,"Tinh Thái","Chúa công, Tinh Thái xin bái phục!","Chúa công, Tinh Thái xin bái phục!","Nhân vật","",},
        [522] = {1401,"Tào Tháo","Ta sẽ thu nạp Thanh Châu Hoàng Cân, sau này gọi là quân Thanh Châu!","Ta sẽ thu nạp Thanh Châu Hoàng Cân, sau này gọi là quân Thanh Châu!","Nhân vật","",},
        [523] = {1401,"Tinh Thái","Chúa công, tin ngài ở Duyện Châu đã truyền ra ngoài, nhiều danh sĩ đến xin gặp!","Chúa công, tin ngài ở Duyện Châu đã truyền ra ngoài, nhiều danh sĩ đến xin gặp!","Nhân vật","",},
        [524] = {1401,"Nhân vật","Gì chứ, Mạnh Đức huynh cùng ta ra xem sao!","Gì chứ, Mạnh Đức huynh cùng ta ra xem sao!","Tinh Thái","",},
        [525] = {1402,"Điển Vi","Nghe nói ngươi giỏi võ, Điển Vi ta đến thỉnh giáo!","Nghe nói ngươi giỏi võ, Điển Vi ta đến thỉnh giáo!","Ngân Bình","",},
        [526] = {1402,"Ngân Bình","Gà nhép mà gáy to quá!","Gà nhép mà gáy to quá!","Điển Vi","",},
        [527] = {1402,"Điển Vi","Tiểu cô nương đừng nói bừa, gọi chúa công ngươi ra đây!","Tiểu cô nương đừng nói bừa, gọi chúa công ngươi ra đây!","Ngân Bình","",},
        [528] = {1402,"Nhân vật","Tráng sĩ đừng dọa người của ta, ta và ngươi đánh cược vậy!","Tráng sĩ đừng dọa người của ta, ta và ngươi đánh cược vậy!","Điển Vi","",},
        [529] = {1402,"Nhân vật","Nếu ngươi thắng thì mạng ta do ngươi quyết định, nếu thua thì phải làm một việc cho ta!","Nếu ngươi thắng thì mạng ta do ngươi quyết định, nếu thua thì phải làm một việc cho ta!","Điển Vi","",},
        [530] = {1402,"Điển Vi","Có gì không dám!","Có gì không dám!","Nhân vật","",},
        [531] = {1402,"Nhân vật","Được, chiến một trận nào!","Được, chiến một trận nào!","Điển Vi","",},
        [532] = {1403,"Điển Vi","Anh hùng rất giỏi! Điển Vi bái phục!","Anh hùng rất giỏi! Điển Vi bái phục!","Nhân vật","",},
        [533] = {1403,"Điển Vi","Ngươi muốn Điển Vi ta làm gì!","Ngươi muốn Điển Vi ta làm gì!","Nhân vật","",},
        [534] = {1403,"Nhân vật","Tào Tháo là một người bậc kiêu hùng, ta muốn ngươi đi theo, ngươi có đồng ý không!","Tào Tháo là một người bậc kiêu hùng, ta muốn ngươi đi theo, ngươi có đồng ý không!","Điển Vi","",},
        [535] = {1403,"Điển Vi","Được, sau này ta sẽ đi theo Tào Tháo, ý ngươi thế nào!","Được, sau này ta sẽ đi theo Tào Tháo, ý ngươi thế nào!","Nhân vật","",},
        [536] = {1403,"Tào Tháo","Được!","Được!","Điển Vi","",},
        [537] = {1404,"Tuân Úc","Tướng quân có tài dùng binh, Tuân Úc xin đi theo ngài, đây là Danh Tướng Lệnh, xin dâng cho tướng quân.","Tướng quân có tài dùng binh, Tuân Úc xin đi theo ngài, đây là Danh Tướng Lệnh, xin dâng cho tướng quân.","Nhân vật","",},
        [538] = {1404,"Nhân vật","Danh Tướng Lệnh ta sẽ thu, nhưng chí hướng của ta là trở thành danh tướng đệ nhất tam quốc!","Danh Tướng Lệnh ta sẽ thu, nhưng chí hướng của ta là trở thành danh tướng đệ nhất tam quốc!","Tuân Úc","",},
        [539] = {1404,"Nhân vật","Ta đề cử chúa công cho ngươi, hắn còn giỏi hơn ta nhiều!","Ta đề cử chúa công cho ngươi, hắn còn giỏi hơn ta nhiều!","Tuân Úc","",},
        [540] = {1404,"Tuân Úc","Không biết người tướng quân nói là ai!","Không biết người tướng quân nói là ai!","Nhân vật","",},
        [541] = {1404,"Nhân vật","Đó là Tào Tháo, Tào Mạnh Đức!","Đó là Tào Tháo, Tào Mạnh Đức!","Tuân Úc","",},
        [542] = {1404,"Tào Tháo","Thì ra là danh sĩ Dĩnh Xuyên Tuân Úc! Ngươi đến giúp ta thật sao!","Thì ra là danh sĩ Dĩnh Xuyên Tuân Úc! Ngươi đến giúp ta thật sao!","Tuân Úc","",},
        [543] = {1404,"Tào Tháo","Đa tạ anh hùng đã giới thiệu quân sư cho ta!","Đa tạ anh hùng đã giới thiệu quân sư cho ta!","Tuân Úc","",},
        [544] = {1404,"Nhân vật","Không có gì! Ta chuẩn bị rời khỏi Duyện Châu đi du ngoạn, cáo từ!","Không có gì! Ta chuẩn bị rời khỏi Duyện Châu đi du ngoạn, cáo từ!","Tào Tháo","",},
        [545] = {1404,"Tinh Thái","Từ đó Tào Tháo có đầy đủ văn thần võ tướng, chiếm Duyện Châu, trở thành thế lực lớn sánh ngang Viên Thiệu Viên Thuật!","Từ đó Tào Tháo có đầy đủ văn thần võ tướng, chiếm Duyện Châu, trở thành thế lực lớn sánh ngang Viên Thiệu Viên Thuật!","Nhân vật","",},
        [546] = {1404,"Ngân Bình","Chúa công, Tào Tháo thế lực lớn, sau này phản lại chúng ta thì sao?","Chúa công, Tào Tháo thế lực lớn, sau này phản lại chúng ta thì sao?","Nhân vật","",},
        [547] = {1404,"Nhân vật","Đã nghi ngờ thì còn kết bạn làm gì, đã kết bạn thì nên tin tưởng lẫn nhau!","Đã nghi ngờ thì còn kết bạn làm gì, đã kết bạn thì nên tin tưởng lẫn nhau!","Ngân Bình","",},
        [548] = {1404,"Ngân Bình","Chúa công nói đúng, Ngân Bình đã hiểu!","Chúa công nói đúng, Ngân Bình đã hiểu!","Nhân vật","",},
        [549] = {1404,"Tinh Thái","Chúa công, Từ Châu có manh mối của Danh Tướng Lệnh, chúng ta đến đó xem thử!","Chúa công, Từ Châu có manh mối của Danh Tướng Lệnh, chúng ta đến đó xem thử!","Nhân vật","",},
        [550] = {1501,"Ngân Bình","Đến Từ Châu rồi! Phía trước là ai, sao mang nhiều châu báu thế!","Đến Từ Châu rồi! Phía trước là ai, sao mang nhiều châu báu thế!","Tào Tung","",},
        [551] = {1501,"Tinh Thái","Từ nhỏ cha đã dạy ta rằng tiền của để lộ ra ngoài là đại kỵ. Ông ta làm vậy nhất định sẽ chuốc họa vào thân.","Từ nhỏ cha đã dạy ta rằng tiền của để lộ ra ngoài là đại kỵ. Ông ta làm vậy nhất định sẽ chuốc họa vào thân.","Tào Tung","",},
        [552] = {1501,"Tào Tung","Các ngươi đang nói gì thế? Ai dám đụng đến Tào Tung ta chứ? Cho các ngươi biết, con trai ta là Tào Tháo đấy!","Các ngươi đang nói gì thế? Ai dám đụng đến Tào Tung ta chứ? Cho các ngươi biết, con trai ta là Tào Tháo đấy!","Ngân Bình","",},
        [553] = {1501,"Ngân Bình","Nhìn bộ dạng kiêu căng của ông kìa! Nếu như không nhờ chúa công ta thì Tào Tháo nhà ông đã bỏ mạng ở Huỳnh Dương rồi!","Nhìn bộ dạng kiêu căng của ông kìa! Nếu như không nhờ chúa công ta thì Tào Tháo nhà ông đã bỏ mạng ở Huỳnh Dương rồi!","Tào Tung","",},
        [554] = {1501,"Tào Tung","Nói bừa! Tiện dân nơi nào mà dám làm càn ở đây!","Nói bừa! Tiện dân nơi nào mà dám làm càn ở đây!","Ngân Bình","",},
        [555] = {1501,"Ngân Bình","Dám nói chúa công của bọn ta là tiện dân sao, xem đây!","Dám nói chúa công của bọn ta là tiện dân sao, xem đây!","Tào Tung","",},
        [556] = {1502,"Ngân Bình","Đào Khiêm, ngươi là Từ Châu Thái Thú, có Danh Tướng Lệnh không?","Đào Khiêm, ngươi là Từ Châu Thái Thú, có Danh Tướng Lệnh không?","Đào Khiêm","",},
        [557] = {1502,"Đào Khiêm","Cô nương minh giám, lão không biết Từ Châu này ở đâu có Danh Tướng Lệnh cả.","Cô nương minh giám, lão không biết Từ Châu này ở đâu có Danh Tướng Lệnh cả.","Ngân Bình","",},
        [558] = {1502,"Ngân Bình","Không có sao?","Không có sao?","Đào Khiêm","",},
        [559] = {1502,"Đào Khiêm","Lão từ nhỏ học đạo lý Khổng, Mạnh nên không dám nói dối đâu.","Lão từ nhỏ học đạo lý Khổng, Mạnh nên không dám nói dối đâu.","Ngân Bình","",},
        [560] = {1502,"Nhân vật","Đào Khiêm này xem ra là người tốt. Nếu không có thì chúng ta đến nơi khác xem sao.","Đào Khiêm này xem ra là người tốt. Nếu không có thì chúng ta đến nơi khác xem sao.","Đào Khiêm","",},
        [561] = {1503,"Tinh Thái","Chúa công, nguy rồi!","Chúa công, nguy rồi!","Nhân vật","",},
        [562] = {1503,"Nhân vật","Tinh Thái, có chuyện gì từ từ nói nào!","Tinh Thái, có chuyện gì từ từ nói nào!","Tinh Thái","",},
        [563] = {1503,"Tinh Thái","Tào Tháo phái người đến Trần Lưu đón Tào Tung. Khi qua Từ Châu, Đào Khiêm đã tiếp đãi họ nồng hậu, còn phái người hộ tống nữa...","Tào Tháo phái người đến Trần Lưu đón Tào Tung. Khi qua Từ Châu, Đào Khiêm đã tiếp đãi họ nồng hậu, còn phái người hộ tống nữa...","Nhân vật","",},
        [564] = {1503,"Ngân Bình","Hừ, Đào Khiêm kia chắc muốn lấy lòng Tào Tháo đây mà. Tiếp đãi Tào Tung cũng chẳng phải là chuyện lớn lao gì.","Hừ, Đào Khiêm kia chắc muốn lấy lòng Tào Tháo đây mà. Tiếp đãi Tào Tung cũng chẳng phải là chuyện lớn lao gì.","Tinh Thái","",},
        [565] = {1503,"Tinh Thái","Nghe ta nói hết đã! Trương Khải, người hộ tống Tào Tung là Giặc Hoàng Cân. Hắn thấy Tào Tung mang theo nhiều của cải nên đã diệt hết cả nhà Tào Tung để cướp tài sản.","Nghe ta nói hết đã! Trương Khải, người hộ tống Tào Tung là Giặc Hoàng Cân. Hắn thấy Tào Tung mang theo nhiều của cải nên đã diệt hết cả nhà Tào Tung để cướp tài sản.","Ngân Bình","",},
        [566] = {1503,"Nhân vật","Đào Khiêm kia có ý tốt nhưng không ngờ lại ra nông nỗi. Lần này Tào Tháo nhất định sẽ trả thù...","Đào Khiêm kia có ý tốt nhưng không ngờ lại ra nông nỗi. Lần này Tào Tháo nhất định sẽ trả thù...","Tinh Thái","",},
        [567] = {1503,"Ngân Bình","Tào Tung quả nhiên mất mạng vì tiền của. Tinh Thái, dự đoán của ngươi đều rất chính xác. Hãy cho ta biết, ngươi có bí quyết gì?","Tào Tung quả nhiên mất mạng vì tiền của. Tinh Thái, dự đoán của ngươi đều rất chính xác. Hãy cho ta biết, ngươi có bí quyết gì?","Tinh Thái","",},
        [568] = {1503,"Tinh Thái","Quan Ngân Bình, đừng ngắt lời chứ!","Quan Ngân Bình, đừng ngắt lời chứ!","Ngân Bình","",},
        [569] = {1503,"Tinh Thái","Chúa công, tin tức truyền đến Duyện Châu, Tào Tháo phái mười mấy vạn binh mã công đánh Từ Châu, còn nói là phải diệt sạch người ở Từ Châu để báo thù nữa!","Chúa công, tin tức truyền đến Duyện Châu, Tào Tháo phái mười mấy vạn binh mã công đánh Từ Châu, còn nói là phải diệt sạch người ở Từ Châu để báo thù nữa!","Nhân vật","",},
        [570] = {1503,"Nhân vật","Bá tánh Từ Châu vô tội, chúng ta mau về Duyện Châu để ngăn Tào Tháo!","Bá tánh Từ Châu vô tội, chúng ta mau về Duyện Châu để ngăn Tào Tháo!","Tinh Thái","",},
        [571] = {1504,"Trần Cung","Tướng quân, đã lâu không gặp!","Tướng quân, đã lâu không gặp!","Nhân vật","",},
        [572] = {1504,"Nhân vật","Trần Cung, ta đang muốn hỏi ngươi. Lúc còn ở Trung Mâu Huyện, ta thấy ngươi rất ngưỡng mộ Tào Tháo, tại sao sau này lại rời bỏ y?","Trần Cung, ta đang muốn hỏi ngươi. Lúc còn ở Trung Mâu Huyện, ta thấy ngươi rất ngưỡng mộ Tào Tháo, tại sao sau này lại rời bỏ y?","Trần Cung","",},
        [573] = {1504,"Trần Cung","Là vì Lữ Bá Xa cả... Chuyện này không nhắc sẽ tốt hơn.","Là vì Lữ Bá Xa cả... Chuyện này không nhắc sẽ tốt hơn.","Nhân vật","",},
        [574] = {1504,"Trần Cung","Lúc đó ta thấy Tào Tháo là người có chí lớn nên mới từ quan theo y nhưng không ngờ y là người đa nghi, lại không nhân từ nên ta mới bỏ đi, không theo y nữa.","Lúc đó ta thấy Tào Tháo là người có chí lớn nên mới từ quan theo y nhưng không ngờ y là người đa nghi, lại không nhân từ nên ta mới bỏ đi, không theo y nữa.","Nhân vật","",},
        [575] = {1504,"Trần Cung","Chuyện Từ Châu lần này, ta vốn có giao tình với Đào Khiêm nên định đến Duyện Châu để khuyên ngăn Tào Tháo.","Chuyện Từ Châu lần này, ta vốn có giao tình với Đào Khiêm nên định đến Duyện Châu để khuyên ngăn Tào Tháo.","Nhân vật","",},
        [576] = {1504,"Trần Cung","Chuyện nguy cấp, ta phải đi ngay đây, cáo từ.","Chuyện nguy cấp, ta phải đi ngay đây, cáo từ.","Nhân vật","",},
        [577] = {1504,"Tinh Thái","Trần Cung chạy nhanh thật. Chúa công, người thấy y có thể khuyên ngăn Tào Tháo không?","Trần Cung chạy nhanh thật. Chúa công, người thấy y có thể khuyên ngăn Tào Tháo không?","Nhân vật","",},
        [578] = {1504,"Nhân vật","Khó nói lắm, e là ta phải ra mặt mới có thể giải quyết được.","Khó nói lắm, e là ta phải ra mặt mới có thể giải quyết được.","Tinh Thái","",},
        [579] = {1505,"Tào Tháo","Trần Cung, năm xưa ngươi rời bỏ ta, giờ còn mặt mũi đến gặp ta sao!","Trần Cung, năm xưa ngươi rời bỏ ta, giờ còn mặt mũi đến gặp ta sao!","Trần Cung","",},
        [580] = {1505,"Tào Tháo","Đào Khiêm sát hại cả nhà ta, ta phải diệt y để báo thù! Ngươi nói gì cũng vô dụng thôi!","Đào Khiêm sát hại cả nhà ta, ta phải diệt y để báo thù! Ngươi nói gì cũng vô dụng thôi!","Trần Cung","",},
        [581] = {1505,"Trần Cung","Ta còn mặt mũi nào để gặp Đào Khiêm nữa chứ! (Trần Cung buồn bã rời đi)","Ta còn mặt mũi nào để gặp Đào Khiêm nữa chứ! (Trần Cung buồn bã rời đi)","Tào Tháo","",},
        [582] = {1505,"Tào Tháo","Thù hại cha quyết phải báo. Nếu không, Tào Tháo ta còn mặt mũi nào nữa chứ!","Thù hại cha quyết phải báo. Nếu không, Tào Tháo ta còn mặt mũi nào nữa chứ!","Trần Cung","",},
        [583] = {1505,"Tào Tháo","Tào Nhân, ta phái ngươi thống lĩnh đại quân diệt sạch Từ Châu! Ta phải bắt cả Từ Châu kia an táng theo phụ thân ta!","Tào Nhân, ta phái ngươi thống lĩnh đại quân diệt sạch Từ Châu! Ta phải bắt cả Từ Châu kia an táng theo phụ thân ta!","Trần Cung","",},
        [584] = {1505,"Tào Nhân","Tào Nhân nhận lệnh!","Tào Nhân nhận lệnh!","Tào Tháo","",},
        [585] = {1506,"Tào Nhân","Tướng quân quả nhiên anh hùng xuất thiếu niên. Tào Nhân xin nhận thua! Hãy nhận lấy Danh Tướng Lệnh này!","Tướng quân quả nhiên anh hùng xuất thiếu niên. Tào Nhân xin nhận thua! Hãy nhận lấy Danh Tướng Lệnh này!","Nhân vật","",},
        [586] = {1506,"Nhân vật","Tào Tháo đâu? Tại sao lại tránh mặt ta?","Tào Tháo đâu? Tại sao lại tránh mặt ta?","Tào Nhân","",},
        [587] = {1506,"Tào Nhân","Chúa công biết người đến giảng hòa nên mới tránh mặt người và chỉ phái ta dẫn binh đi đánh thôi! (Tào Nhân lui xuống)","Chúa công biết người đến giảng hòa nên mới tránh mặt người và chỉ phái ta dẫn binh đi đánh thôi! (Tào Nhân lui xuống)","Nhân vật","",},
        [588] = {1506,"Ngân Bình","Hừ, Tào Tháo thật xảo quyệt, dám tránh mặt ta!","Hừ, Tào Tháo thật xảo quyệt, dám tránh mặt ta!","Nhân vật","",},
        [589] = {1506,"Tinh Thái","Chúa công, theo lời Trần Cung nói thì Tào Tháo là người đa nghi và bất nhân thật sao?","Chúa công, theo lời Trần Cung nói thì Tào Tháo là người đa nghi và bất nhân thật sao?","Ngân Bình","",},
        [590] = {1506,"Ngân Bình","Báo thù cho cha chỉ là cái cớ thôi! Ta thấy Tào Tháo muốn đoạt Từ Châu của Đào Khiêm và lấy Danh Tướng Lệnh thôi!","Báo thù cho cha chỉ là cái cớ thôi! Ta thấy Tào Tháo muốn đoạt Từ Châu của Đào Khiêm và lấy Danh Tướng Lệnh thôi!","Tinh Thái","",},
        [591] = {1506,"Nhân vật","Nếu Tào Tháo không muốn gặp ta vậy thì chúng ta đến giúp Từ Châu. Dù sao thì bá tánh đều là người vô tội cả!","Nếu Tào Tháo không muốn gặp ta vậy thì chúng ta đến giúp Từ Châu. Dù sao thì bá tánh đều là người vô tội cả!","Ngân Bình","",},
        [592] = {1601,"Đào Khiêm","(Khóc to) Ta vốn muốn nhân dịp này có thể kết giao với Tào Tháo, không ngờ lại rước họa cho Từ Châu! Ta là tội nhân của Từ Châu!","(Khóc to) Ta vốn muốn nhân dịp này có thể kết giao với Tào Tháo, không ngờ lại rước họa cho Từ Châu! Ta là tội nhân của Từ Châu!","Nhân vật","",},
        [593] = {1601,"Nhân vật","Đào Công đừng sợ! Ta nguyện giúp người đẩy lui quân Tào!","Đào Công đừng sợ! Ta nguyện giúp người đẩy lui quân Tào!","Đào Khiêm","",},
        [594] = {1601,"Đào Khiêm","(Lại khóc to hơn) Anh hùng quả thật nhân nghĩa! Không ngờ thời khắc nguy khốn thế này vẫn có người chịu giúp Từ Châu!","(Lại khóc to hơn) Anh hùng quả thật nhân nghĩa! Không ngờ thời khắc nguy khốn thế này vẫn có người chịu giúp Từ Châu!","Nhân vật","",},
        [595] = {1601,"Tinh Thái","Chúa công ta là một anh hùng phóng khoáng, hiệp nghĩa, là thiên hạ đệ nhất anh hùng đấy!","Chúa công ta là một anh hùng phóng khoáng, hiệp nghĩa, là thiên hạ đệ nhất anh hùng đấy!","Nhân vật","",},
        [596] = {1601,"Nhân vật","Hi hi, Tinh Thái thật biết ăn nói!","Hi hi, Tinh Thái thật biết ăn nói!","Tinh Thái","",},
        [597] = {1602,"My Trúc","Đào Công, ta có kế này!","Đào Công, ta có kế này!","Đào Khiêm","",},
        [598] = {1602,"Đào Khiêm","Mau nói xem nào!","Mau nói xem nào!","My Trúc","",},
        [599] = {1602,"My Trúc","Chi bằng phái người đến Bì Hải cầu viện! Thái Thú Bì Hải, Khổng Dung là con cháu đời thứ 20 của Khổng Tử, ông ấy nhất định giúp ông.","Chi bằng phái người đến Bì Hải cầu viện! Thái Thú Bì Hải, Khổng Dung là con cháu đời thứ 20 của Khổng Tử, ông ấy nhất định giúp ông.","Đào Khiêm","",},
        [600] = {1602,"Nhân vật","Khổng Dung là Thái Thú Bì Hải sao! Ta sẽ đích thân đến đó!","Khổng Dung là Thái Thú Bì Hải sao! Ta sẽ đích thân đến đó!","My Trúc","",},
        [601] = {1602,"Nhân vật","Ha ha, đúng lúc có thể đến đó xem con cháu đời thứ 20 của Khổng Tử ra sao luôn!","Ha ha, đúng lúc có thể đến đó xem con cháu đời thứ 20 của Khổng Tử ra sao luôn!","My Trúc","",},
        [602] = {1603,"Khổng Dung","Các ngươi là ai? Đến Bì Hải có việc chi?","Các ngươi là ai? Đến Bì Hải có việc chi?","Tinh Thái","",},
        [603] = {1603,"Tinh Thái","Này, Khổng Dung kia, gặp chúa công ta phải khách sáo tí chứ! Chúa công nhà ta là anh hùng hào kiệt, tương lai là tướng mạnh nhất trong Tam Quốc đấy!","Này, Khổng Dung kia, gặp chúa công ta phải khách sáo tí chứ! Chúa công nhà ta là anh hùng hào kiệt, tương lai là tướng mạnh nhất trong Tam Quốc đấy!","Khổng Dung","",},
        [604] = {1603,"Khổng Dung","Ta không tin! Từ xưa anh hùng xuất thiếu niên! Đừng thấy ta còn nhỏ mà khinh thường nhé!","Ta không tin! Từ xưa anh hùng xuất thiếu niên! Đừng thấy ta còn nhỏ mà khinh thường nhé!","Tinh Thái","",},
        [605] = {1603,"Ngân Bình","Tiểu đệ đệ, bản lĩnh ngươi thế nào đánh với ta rồi nói nhé!","Tiểu đệ đệ, bản lĩnh ngươi thế nào đánh với ta rồi nói nhé!","Khổng Dung","",},
        [606] = {1604,"Khổng Dung","A, là người, vị anh hùng diệt Giặc Hoàng Cân, dương danh Hổ Lao Quan, diệt Đổng Trác đây mà! Hay quá, ta ngưỡng mộ người từ rất lâu rồi!","A, là người, vị anh hùng diệt Giặc Hoàng Cân, dương danh Hổ Lao Quan, diệt Đổng Trác đây mà! Hay quá, ta ngưỡng mộ người từ rất lâu rồi!","Nhân vật","",},
        [607] = {1604,"Nhân vật","Không dám, không dám! Tào Tháo hiện đang muốn công đánh Từ Châu. Bì Hải và Từ Châu rất gần nhau, xin hãy xuất binh cứu viện Từ Châu!","Không dám, không dám! Tào Tháo hiện đang muốn công đánh Từ Châu. Bì Hải và Từ Châu rất gần nhau, xin hãy xuất binh cứu viện Từ Châu!","Khổng Dung","",},
        [608] = {1604,"Khổng Dung","Anh hùng à, ta lực bất tòng tâm! Bì Hải vốn đang bị Giặc Hoàng Cân tấn công, may là có vị nghĩa sĩ võ nghệ cao cường nọ đến tương trợ nên mới tạm ngăn được bọn chúng!","Anh hùng à, ta lực bất tòng tâm! Bì Hải vốn đang bị Giặc Hoàng Cân tấn công, may là có vị nghĩa sĩ võ nghệ cao cường nọ đến tương trợ nên mới tạm ngăn được bọn chúng!","Nhân vật","",},
        [609] = {1604,"Nhân vật","Nghĩa sĩ võ nghệ cao cường sao! Là ai nhỉ, ta đang rất muốn tỉ thí với cao thủ đây!","Nghĩa sĩ võ nghệ cao cường sao! Là ai nhỉ, ta đang rất muốn tỉ thí với cao thủ đây!","Khổng Dung","",},
        [610] = {1605,"Thái Sử Từ","Anh hùng quả nhiên lợi hại, Thái Sử Từ xin nhận thua! Đây là Danh Tướng Lệnh, mong người nhận cho!","Anh hùng quả nhiên lợi hại, Thái Sử Từ xin nhận thua! Đây là Danh Tướng Lệnh, mong người nhận cho!","Nhân vật","",},
        [611] = {1605,"Nhân vật","Đa tạ! Hi hi, ta còn tưởng lần này đi sẽ không có thu hoạch gì nữa chứ.","Đa tạ! Hi hi, ta còn tưởng lần này đi sẽ không có thu hoạch gì nữa chứ.","Thái Sử Từ","",},
        [612] = {1605,"Khổng Dung","Hai vị tuy anh dũng nhưng cũng khó chống lại đại quân hùng mạnh! Nghe nói Lưu Bị là đại anh hùng, hay là mời Lưu Huyền Đức cứu viện Từ Châu! Chỉ là không biết ông ấy có đồng ý giúp không.","Hai vị tuy anh dũng nhưng cũng khó chống lại đại quân hùng mạnh! Nghe nói Lưu Bị là đại anh hùng, hay là mời Lưu Huyền Đức cứu viện Từ Châu! Chỉ là không biết ông ấy có đồng ý giúp không.","Nhân vật","",},
        [613] = {1605,"Nhân vật","Lưu Huyền Đức là đại ca kết nghĩa của ta! Đại ca tính tình nhân hậu, nhất định sẽ giúp! Để ta đi mời!","Lưu Huyền Đức là đại ca kết nghĩa của ta! Đại ca tính tình nhân hậu, nhất định sẽ giúp! Để ta đi mời!","Khổng Dung","",},
        [614] = {1605,"Nhân vật","Thái Sử Từ huynh có muốn đi cùng ta không?","Thái Sử Từ huynh có muốn đi cùng ta không?","Khổng Dung","",},
        [615] = {1605,"Thái Sử Từ","Xin thứ lỗi, hiện còn mẹ già đang cần chăm sóc nên Thái Sử Từ không thể đi được. (Thái Sử Từ từ biệt rời khỏi)","Xin thứ lỗi, hiện còn mẹ già đang cần chăm sóc nên Thái Sử Từ không thể đi được. (Thái Sử Từ từ biệt rời khỏi)","Nhân vật","",},
        [616] = {1605,"Nhân vật","Vậy ta đi mời đại ca vậy. Sự việc cấp bách, chúng ta đi thôi!","Vậy ta đi mời đại ca vậy. Sự việc cấp bách, chúng ta đi thôi!","Thái Sử Từ","",},
        [617] = {2018101,"Điêu Thuyền","Hữu mỹ nhất nhân, thanh dương uyển hề... Là thơ trong Kinh Thi, là ai tặng vậy nhỉ, sao không để tên?","Hữu mỹ nhất nhân, thanh dương uyển hề... Là thơ trong Kinh Thi, là ai tặng vậy nhỉ, sao không để tên?","","",},
        [618] = {2018101,"Điêu Thuyền","Vũ Nương nói ta xinh đẹp như tiên, lại múa hay, không biết sau này ai có phúc cưới được ta đây...","Vũ Nương nói ta xinh đẹp như tiên, lại múa hay, không biết sau này ai có phúc cưới được ta đây...","","",},
        [619] = {2018101,"Điêu Thuyền","(Mong mỏi) Đương nhiên là ta muốn gả cho một anh hùng giỏi nhất thế gian rồi...","(Mong mỏi) Đương nhiên là ta muốn gả cho một anh hùng giỏi nhất thế gian rồi...","","",},
        [620] = {2018101,"Trương Lương","Ha ha ha, tiểu mỹ nhân, ta theo nàng lâu rồi đấy!","Ha ha ha, tiểu mỹ nhân, ta theo nàng lâu rồi đấy!","Điêu Thuyền","",},
        [621] = {2018101,"Điêu Thuyền","A? Ngươi là ai, sao lại theo ta?","A? Ngươi là ai, sao lại theo ta?","","",},
        [622] = {2018101,"Trương Lương","Nàng nói xem sao ta lại theo nàng? Trời lạnh như vậy, nàng mặc mỏng manh thế, hay là để ta sưởi ấm cho nhé!","Nàng nói xem sao ta lại theo nàng? Trời lạnh như vậy, nàng mặc mỏng manh thế, hay là để ta sưởi ấm cho nhé!","","",},
        [623] = {2018101,"Điêu Thuyền","A, ngươi là kẻ xấu! Ngươi đừng qua đây, ta sẽ la lên đấy!","A, ngươi là kẻ xấu! Ngươi đừng qua đây, ta sẽ la lên đấy!","","",},
        [624] = {2018101,"Trương Lương","Ở đây không có ai hết, nàng có hét to thế nào cũng vô ích thôi, ha ha ha. (Trương Lương xông đến, Điêu Thuyền vô cùng kinh hãi)","Ở đây không có ai hết, nàng có hét to thế nào cũng vô ích thôi, ha ha ha. (Trương Lương xông đến, Điêu Thuyền vô cùng kinh hãi)","","",},
        [625] = {2018101,"Lữ Bố","Dừng tay lại!","Dừng tay lại!","","",},
        [626] = {2018102,"Điêu Thuyền","Đa tạ anh hùng đã tương cứu. Tiểu nữ tên Điêu Thuyền, dám hỏi danh tính của anh hùng là gì ạ? (Điêu Thuyền hành lễ đa tạ Lữ Bố, khi cô ngẩng đầu lên, Lữ Bố nhìn cô ngơ ngẩn)","Đa tạ anh hùng đã tương cứu. Tiểu nữ tên Điêu Thuyền, dám hỏi danh tính của anh hùng là gì ạ? (Điêu Thuyền hành lễ đa tạ Lữ Bố, khi cô ngẩng đầu lên, Lữ Bố nhìn cô ngơ ngẩn)","Lữ Bố","",},
        [627] = {2018102,"Lữ Bố","Ta họ Lữ, tên Bố, tự là Phụng Tiên. Hiện Giặc Hoàng Cân đang hoành hành khắp nơi, sao cô nương lại chỉ có một mình?","Ta họ Lữ, tên Bố, tự là Phụng Tiên. Hiện Giặc Hoàng Cân đang hoành hành khắp nơi, sao cô nương lại chỉ có một mình?","","",},
        [628] = {2018102,"Điêu Thuyền","Nghĩa phụ ta đang bị bệnh, nghe nói ở ngoài thành có danh y nổi tiếng tên Hoa Đà nên ta muốn mời ông ấy đến chữa bệnh cho nghĩa phụ.","Nghĩa phụ ta đang bị bệnh, nghe nói ở ngoài thành có danh y nổi tiếng tên Hoa Đà nên ta muốn mời ông ấy đến chữa bệnh cho nghĩa phụ.","","",},
        [629] = {2018102,"Lữ Bố","(Trong lòng rung động) Thì ra là vậy, cô nương quả nhiên rất hiếu thuận. Dám hỏi, cha mẹ ruột của cô nương đâu, sao chỉ có nghĩa phụ thôi?","(Trong lòng rung động) Thì ra là vậy, cô nương quả nhiên rất hiếu thuận. Dám hỏi, cha mẹ ruột của cô nương đâu, sao chỉ có nghĩa phụ thôi?","","",},
        [630] = {2018102,"Điêu Thuyền","Điêu Thuyền từ nhỏ đã mất đi song thân, nghĩa phụ một tay nuôi ta khôn lớn.","Điêu Thuyền từ nhỏ đã mất đi song thân, nghĩa phụ một tay nuôi ta khôn lớn.","","",},
        [631] = {2018102,"Lữ Bố","Thật là có duyên. Lữ Bố ta từ nhỏ cũng mất cha mất mẹ, may là võ nghê cao cường nên đi theo phò nghĩa phụ.","Thật là có duyên. Lữ Bố ta từ nhỏ cũng mất cha mất mẹ, may là võ nghê cao cường nên đi theo phò nghĩa phụ.","","",},
        [632] = {2018102,"Điêu Thuyền","A, không ngờ Lữ công tử cũng giống như Điêu Thuyền vậy...","A, không ngờ Lữ công tử cũng giống như Điêu Thuyền vậy...","","",},
        [633] = {2018102,"Lữ Bố","Cô nương, đừng khách sáo, hãy gọi ta là Phụng Tiên. Thời buổi loạn lạc, nếu không chê, Phụng Tiên ta sẽ hộ tống cô nương đến mời danh y.","Cô nương, đừng khách sáo, hãy gọi ta là Phụng Tiên. Thời buổi loạn lạc, nếu không chê, Phụng Tiên ta sẽ hộ tống cô nương đến mời danh y.","","",},
        [634] = {2018102,"Điêu Thuyền","Đa tạ Lữ công tử... Phụng Tiên!","Đa tạ Lữ công tử... Phụng Tiên!","","",},
        [635] = {2018103,"Điêu Thuyền","Phụng Tiên, chiếc khăn trên đầu chàng cũng đã cũ nên thiếp đã làm một chiếc khác, chàng xem có thích không?","Phụng Tiên, chiếc khăn trên đầu chàng cũng đã cũ nên thiếp đã làm một chiếc khác, chàng xem có thích không?","Lữ Bố","",},
        [636] = {2018103,"Lữ Bố","Rất đẹp, đa tạ Thuyền Nhi! Thuyền Nhi, tay nàng sao lại bị thương thế kia, có đau không?","Rất đẹp, đa tạ Thuyền Nhi! Thuyền Nhi, tay nàng sao lại bị thương thế kia, có đau không?","","",},
        [637] = {2018103,"Điêu Thuyền","(E thẹn) Không sao đâu, vì không muốn bị phát hiện nên mới bị kim đâm trúng thôi. Phụng Tiên, khi nào chàng mới đến hỏi cưới thiếp?","(E thẹn) Không sao đâu, vì không muốn bị phát hiện nên mới bị kim đâm trúng thôi. Phụng Tiên, khi nào chàng mới đến hỏi cưới thiếp?","","",},
        [638] = {2018103,"Lữ Bố","Thuyền Nhi, nghĩa phụ của nàng là Tư Đồ, ta làm sao có thể để nghĩa phụ nàng xem thường ta chứ! Nàng hãy chờ ta, sau khi ta thăng quan tiến tước làm đại tướng quân ta sẽ đến hỏi cưới nàng.","Thuyền Nhi, nghĩa phụ của nàng là Tư Đồ, ta làm sao có thể để nghĩa phụ nàng xem thường ta chứ! Nàng hãy chờ ta, sau khi ta thăng quan tiến tước làm đại tướng quân ta sẽ đến hỏi cưới nàng.","","",},
        [639] = {2018103,"Điêu Thuyền","Phụng Tiên, nghĩa phụ rất thương thiếp, chỉ cần chàng đối xử tốt với thiếp thì nghĩa phụ cũng không chê chàng đâu.","Phụng Tiên, nghĩa phụ rất thương thiếp, chỉ cần chàng đối xử tốt với thiếp thì nghĩa phụ cũng không chê chàng đâu.","","",},
        [640] = {2018103,"Lữ Bố","Nam tử hán đại trượng phu, ta làm sao có thể để người ta yêu thương chịu khổ được!","Nam tử hán đại trượng phu, ta làm sao có thể để người ta yêu thương chịu khổ được!","","",},
        [641] = {2018103,"Lữ Bố","Thuyền Nhi, nàng hãy chờ ta. Chờ ta trở thành đại tướng quân rồi ta sẽ mang kiệu hoa đến rước nàng về!","Thuyền Nhi, nàng hãy chờ ta. Chờ ta trở thành đại tướng quân rồi ta sẽ mang kiệu hoa đến rước nàng về!","","",},
        [642] = {2018104,"Điêu Thuyền","Phụng Tiên nói rằng gần đây chàng được đề bạt làm Trung Lang Tướng, ta phải báo chuyện này cho nghĩa phụ biết mới được.","Phụng Tiên nói rằng gần đây chàng được đề bạt làm Trung Lang Tướng, ta phải báo chuyện này cho nghĩa phụ biết mới được.","Vương Doãn","",},
        [643] = {2018104,"Vương Doãn","Điêu Thuyền, con đến tìm ta có việc gì?","Điêu Thuyền, con đến tìm ta có việc gì?","","",},
        [644] = {2018104,"Điêu Thuyền","Nghĩa phụ, con... (Vương Doãn đột nhiên quỵ xuống, Điêu Thuyền vội chạy đến đỡ ông ngồi xuống) Nghĩa phụ, người sao thế?","Nghĩa phụ, con... (Vương Doãn đột nhiên quỵ xuống, Điêu Thuyền vội chạy đến đỡ ông ngồi xuống) Nghĩa phụ, người sao thế?","","",},
        [645] = {2018104,"Vương Doãn","(Mặt trắng bệch, khóc) Giang sơn Hán Thất đang lâm nguy! Tặc thần Đổng Trác muốn soán ngôi, triều đình không làm gì được, ta thật hổ thẹn mà...","(Mặt trắng bệch, khóc) Giang sơn Hán Thất đang lâm nguy! Tặc thần Đổng Trác muốn soán ngôi, triều đình không làm gì được, ta thật hổ thẹn mà...","","",},
        [646] = {2018104,"Điêu Thuyền","Điêu Thuyền nguyện chia sẻ nỗi lòng của nghĩa phụ.","Điêu Thuyền nguyện chia sẻ nỗi lòng của nghĩa phụ.","","",},
        [647] = {2018104,"Vương Doãn","(Đột nhiên quỳ trước Điêu Thuyền) Nào ngờ thiên hạ nhà Hán lại nằm trong tay con! Điêu Thuyền, ta có một mỹ nhân kế, chỉ có con mới diệt trừ được Đổng Trác.","(Đột nhiên quỳ trước Điêu Thuyền) Nào ngờ thiên hạ nhà Hán lại nằm trong tay con! Điêu Thuyền, ta có một mỹ nhân kế, chỉ có con mới diệt trừ được Đổng Trác.","","",},
        [648] = {2018104,"Điêu Thuyền","Ơn dưỡng dục của nghĩa phụ, Điêu Thuyền dù hi sinh thân mình cũng khó báo đáp.","Ơn dưỡng dục của nghĩa phụ, Điêu Thuyền dù hi sinh thân mình cũng khó báo đáp.","","",},
        [649] = {2018105,"Vương Doãn","Đổng Trác có rất nhiều thủ hạ, người thường rất khó đến gần hắn, nhưng hắn lại là một kẻ háo sắc.","Đổng Trác có rất nhiều thủ hạ, người thường rất khó đến gần hắn, nhưng hắn lại là một kẻ háo sắc.","Điêu Thuyền","",},
        [650] = {2018105,"Vương Doãn","Hắn có một nghĩa tử tên Lữ Bố, rất dũng mãnh... Trước tiên ta hứa gả con cho Lữ Bố, lại hiến cho Đổng Trác, khiến hai phụ tử trở mặt, sau đó Lữ Bố sẽ diệt trừ Đổng Trác.","Hắn có một nghĩa tử tên Lữ Bố, rất dũng mãnh... Trước tiên ta hứa gả con cho Lữ Bố, lại hiến cho Đổng Trác, khiến hai phụ tử trở mặt, sau đó Lữ Bố sẽ diệt trừ Đổng Trác.","","",},
        [651] = {2018105,"Điêu Thuyền","(Điêu Thuyền hoảng sợ kêu lên) A!","(Điêu Thuyền hoảng sợ kêu lên) A!","","",},
        [652] = {2018105,"Vương Doãn","Điêu Thuyền, sắc mặt con trắng bệt, chẳng lẽ không muốn làm?","Điêu Thuyền, sắc mặt con trắng bệt, chẳng lẽ không muốn làm?","","",},
        [653] = {2018105,"Điêu Thuyền","Không phải, nghĩa phụ đừng lo.","Không phải, nghĩa phụ đừng lo.","","",},
        [654] = {2018105,"Vương Doãn","Con gái ngoan, ủy khuất cho con rồi. Nếu chuyện này bị lộ, cả nhà ta sẽ bị diệt đấy.","Con gái ngoan, ủy khuất cho con rồi. Nếu chuyện này bị lộ, cả nhà ta sẽ bị diệt đấy.","","",},
        [655] = {2018105,"Điêu Thuyền","Nghĩa phụ yên tâm, Điêu Thuyền sẽ cố gắng hết sức để báo đáp công ơn nghĩa phụ!","Nghĩa phụ yên tâm, Điêu Thuyền sẽ cố gắng hết sức để báo đáp công ơn nghĩa phụ!","","",},
        [656] = {2018201,"Hà Tiến","Đạo Sĩ, ta thấy ngươi đạo hạnh cao thâm, bói mệnh cho Hà Tiến ta xem!","Đạo Sĩ, ta thấy ngươi đạo hạnh cao thâm, bói mệnh cho Hà Tiến ta xem!","Tả Từ","",},
        [657] = {2018201,"Tả Từ","Ta thấy ngươi giữa trán sáng sủa, mắt có thần, sau này chắc chắn làm Đại Tướng Quân!","Ta thấy ngươi giữa trán sáng sủa, mắt có thần, sau này chắc chắn làm Đại Tướng Quân!","","",},
        [658] = {2018201,"Hà Tiến","Ha ha, ta là người mổ heo, còn có thể làm tướng quân?","Ha ha, ta là người mổ heo, còn có thể làm tướng quân?","","",},
        [659] = {2018201,"Tả Từ","Nếu không tin sao còn đến đây xem bói. Ngươi lật lọng như vậy, sau này dù làm Đại Tướng Quân cũng không sống thọ được!","Nếu không tin sao còn đến đây xem bói. Ngươi lật lọng như vậy, sau này dù làm Đại Tướng Quân cũng không sống thọ được!","","",},
        [660] = {2018201,"Hà Tiến","Đi đi đạo sĩ thúi, dám mắng ta không sống lâu được!","Đi đi đạo sĩ thúi, dám mắng ta không sống lâu được!","","",},
        [661] = {2018202,"Hà Thái Hậu","Từ khi cha mẹ mất đi, là ca ca đã cực khổ nuôi dưỡng cả nhà, giờ thời kỳ cực khổ đã qua, muội sắp vào cung rồi.","Từ khi cha mẹ mất đi, là ca ca đã cực khổ nuôi dưỡng cả nhà, giờ thời kỳ cực khổ đã qua, muội sắp vào cung rồi.","Hà Tiến","",},
        [662] = {2018202,"Hà Tiến","Muội vào cung rồi nhớ phải cẩn thận, nếu bị ức hiếp thì phải nói ta biết, ta sẽ làm chủ cho muội.","Muội vào cung rồi nhớ phải cẩn thận, nếu bị ức hiếp thì phải nói ta biết, ta sẽ làm chủ cho muội.","","",},
        [663] = {2018202,"Hà Thái Hậu","Ca ca yên tâm, muội không dễ bị ức hiếp vậy đâu. Đợi sau này muội có làm được hoàng hậu, nhất định sẽ cho ca ca làm quan lớn, không phải chịu cực nữa.","Ca ca yên tâm, muội không dễ bị ức hiếp vậy đâu. Đợi sau này muội có làm được hoàng hậu, nhất định sẽ cho ca ca làm quan lớn, không phải chịu cực nữa.","","",},
        [664] = {2018203,"Hà Thái Hậu","Ca ca, muội làm được hoàng hậu rồi! Đó là nhờ muội sinh cho hoàng đế một hoàng tử!","Ca ca, muội làm được hoàng hậu rồi! Đó là nhờ muội sinh cho hoàng đế một hoàng tử!","","",},
        [665] = {2018203,"Hà Tiến","Tốt quá, Hà gia chúng ta cuối cùng đã đổi đời rồi!","Tốt quá, Hà gia chúng ta cuối cùng đã đổi đời rồi!","Hà Thái Hậu","",},
        [666] = {2018203,"Hà Thái Hậu","Ca ca, nhờ có huynh lập công lớn, bắt được Mã Nguyên Nghĩa của giặc Hoàng Cân! Đại Tướng Quân thật lợi hại!","Ca ca, nhờ có huynh lập công lớn, bắt được Mã Nguyên Nghĩa của giặc Hoàng Cân! Đại Tướng Quân thật lợi hại!","","",},
        [667] = {2018203,"Hà Tiến","Ha ha, ta làm Đại Tướng Quân mới có thể bảo vệ muội chứ!","Ha ha, ta làm Đại Tướng Quân mới có thể bảo vệ muội chứ!","","",},
        [668] = {2018203,"Hà Thái Hậu","Ca ca, hoàng đế muốn lập con trai Lưu Hiệp của Vương Mỹ Nhân làm hoàng đế. Hu hu, huynh phải làm chủ cho muội đấy.","Ca ca, hoàng đế muốn lập con trai Lưu Hiệp của Vương Mỹ Nhân làm hoàng đế. Hu hu, huynh phải làm chủ cho muội đấy.","","",},
        [669] = {2018203,"Hà Tiến","Muội muội yên tâm, ca ca nhất định sẽ không để chuyện này xảy ra!","Muội muội yên tâm, ca ca nhất định sẽ không để chuyện này xảy ra!","","",},
        [670] = {2018204,"Hà Tiến","Muội muội, đám người Tào Tháo, Viên Thiệu đều khuyên ta tru sát Thập Thường Thị, ta phải nghĩ cách tiêu diệt Trương Nhượng!","Muội muội, đám người Tào Tháo, Viên Thiệu đều khuyên ta tru sát Thập Thường Thị, ta phải nghĩ cách tiêu diệt Trương Nhượng!","Hà Thái Hậu","",},
        [671] = {2018204,"Hà Thái Hậu","Không được! Trương Nhượng từng cứu mạng muội, nếu không có hắn, hoàng đế sớm đã phế muội rồi!","Không được! Trương Nhượng từng cứu mạng muội, nếu không có hắn, hoàng đế sớm đã phế muội rồi!","","",},
        [672] = {2018204,"Hà Tiến","Muội muội, Hà gia chúng ta muốn đứng vững thì phải diệt trừ Trương Nhượng, nếu không sẽ là mối họa sau này.","Muội muội, Hà gia chúng ta muốn đứng vững thì phải diệt trừ Trương Nhượng, nếu không sẽ là mối họa sau này.","","",},
        [673] = {2018204,"Hà Thái Hậu","Ca ca, dù như thế nào huynh cũng không thể diệt trừ Trương Nhượng.","Ca ca, dù như thế nào huynh cũng không thể diệt trừ Trương Nhượng.","","",},
        [674] = {2018204,"Hà Tiến","Được rồi, ta nghĩ cách khác xem!","Được rồi, ta nghĩ cách khác xem!","","",},
        [675] = {2018205,"Trương Nhượng","Nếu không nhờ Tiểu Thái Giám khác báo tin, ta thật sự không biết Hà Tiến lại muốn tru sát ta.","Nếu không nhờ Tiểu Thái Giám khác báo tin, ta thật sự không biết Hà Tiến lại muốn tru sát ta.","","",},
        [676] = {2018205,"Trương Nhượng","Nếu vậy đừng có trách ta. Người đâu, giả ý chỉ thái hậu lừa Hà Tiến vào cung rồi tru sát hắn!","Nếu vậy đừng có trách ta. Người đâu, giả ý chỉ thái hậu lừa Hà Tiến vào cung rồi tru sát hắn!","","",},
        [677] = {2018206,"Viên Thiệu","Chiếu chỉ này nhất định là mưu kế của Thập Thường Thị, đi chắc chắn gặp họa.","Chiếu chỉ này nhất định là mưu kế của Thập Thường Thị, đi chắc chắn gặp họa.","Hà Tiến","",},
        [678] = {2018206,"Hà Tiến","Thái hậu là muội muội ruột của ta, gặp họa gì chứ?","Thái hậu là muội muội ruột của ta, gặp họa gì chứ?","","",},
        [679] = {2018206,"Viên Thiệu","Đại Tướng Quân nếu nhất quyết vào cung thì ta với Viên Thiệu sẽ dẫn người bảo vệ, đề phòng bất trắc.","Đại Tướng Quân nếu nhất quyết vào cung thì ta với Viên Thiệu sẽ dẫn người bảo vệ, đề phòng bất trắc.","","",},
        [680] = {2018206,"Hà Tiến","Muội muội ta là thái hậu, ta là Đại Tướng Quân, ai dám hại ta! Nếu đây thật sự là âm mưu của Thập Thường Thị thì ta càng phải vào cung bảo vệ muội muội!","Muội muội ta là thái hậu, ta là Đại Tướng Quân, ai dám hại ta! Nếu đây thật sự là âm mưu của Thập Thường Thị thì ta càng phải vào cung bảo vệ muội muội!","","",},
        [681] = {2018301,"Hoa Hùng","Lý Thôi, tiểu tử thối, người đâu! Đi, đi uống rượu!","Lý Thôi, tiểu tử thối, người đâu! Đi, đi uống rượu!","Lý Thôi","",},
        [682] = {2018301,"Lý Thôi","Uống rượu thì dễ, nhưng tiền rượu...","Uống rượu thì dễ, nhưng tiền rượu...","","",},
        [683] = {2018301,"Hoa Hùng","Nhắc tiền trước mặt ca ca là quá khách khí rồi.","Nhắc tiền trước mặt ca ca là quá khách khí rồi.","","",},
        [684] = {2018302,"Đổng Trác","Chư Hầu đó đã đến bên ngoài Hổ Lao Quan, ai ra xuất chiến?","Chư Hầu đó đã đến bên ngoài Hổ Lao Quan, ai ra xuất chiến?","Hoa Hùng","",},
        [685] = {2018302,"Lữ Bố","Phụ thân đừng lo, chư hầu đó tựa như cỏ ven đường, ta nguyện ra ngoài trảm thủ lĩnh của họ.","Phụ thân đừng lo, chư hầu đó tựa như cỏ ven đường, ta nguyện ra ngoài trảm thủ lĩnh của họ.","","",},
        [686] = {2018302,"Hoa Hùng","Không cần Ôn Hầu đích thân ra tay. Ta lấy thủ cấp chư hầu như lấy đồ trong túi vậy!","Không cần Ôn Hầu đích thân ra tay. Ta lấy thủ cấp chư hầu như lấy đồ trong túi vậy!","","",},
        [687] = {2018303,"Đổng Trác","Hoa Hùng, ngươi có công lớn, trảm Bào Trung, Tổ Mậu, đánh bại Tôn Kiên. Người đâu, phong Hoa Hùng làm đô đốc!","Hoa Hùng, ngươi có công lớn, trảm Bào Trung, Tổ Mậu, đánh bại Tôn Kiên. Người đâu, phong Hoa Hùng làm đô đốc!","","",},
        [688] = {2018303,"Hoa Hùng","Đa tạ Thái Sư!","Đa tạ Thái Sư!","","",},
        [689] = {2018304,"Hoa Hùng","(Dùng gậy khều vải đầu của Tôn Kiên) Một đám nhu nhược, ai dám đến chiến?","(Dùng gậy khều vải đầu của Tôn Kiên) Một đám nhu nhược, ai dám đến chiến?","Viên Thiệu","",},
        [690] = {2018304,"Viên Thiệu","Tiếc là Nhan Lương, Văn Xú không ở đây, các vị, Hoa Hùng đó thật ngông cuồng, ai dám ra ứng chiến?","Tiếc là Nhan Lương, Văn Xú không ở đây, các vị, Hoa Hùng đó thật ngông cuồng, ai dám ra ứng chiến?","","",},
        [691] = {2018304,"Quan Vũ","Ta nguyện ra trảm Hoa Hùng! Rượu làm ấm trước, ta đi xong về ngay.","Ta nguyện ra trảm Hoa Hùng! Rượu làm ấm trước, ta đi xong về ngay.","","",},
        [692] = {2018305,"Hoa Hùng","Ta đã trảm Phan Phượng, ai dám tới chiến!","Ta đã trảm Phan Phượng, ai dám tới chiến!","","",},
        [693] = {2018305,"Quan Vũ","Hoa Hùng, đừng ngông cuồng!","Hoa Hùng, đừng ngông cuồng!","","",},
        [694] = {2018305,"Hoa Hùng","Sát khí nặng thật!","Sát khí nặng thật!","","",},
        [695] = {2018305,"Hoa Hùng","Đại hán mặt đỏ này đến nhanh như vậy... Gặp ngươi cũng là vận mệnh... Da dày cũng không đỡ được đao...!","Đại hán mặt đỏ này đến nhanh như vậy... Gặp ngươi cũng là vận mệnh... Da dày cũng không đỡ được đao...!","","",},
        [696] = {2018401,"Tuân Du","Thúc phụ, không tốt rồi!","Thúc phụ, không tốt rồi!","Tuân Úc","",},
        [697] = {2018401,"Tuân Úc","Công Đạt, có chuyện gì vậy?","Công Đạt, có chuyện gì vậy?","Tuân Du","",},
        [698] = {2018401,"Tuân Du","Năm đó Trung Thường Tự Đường Hoành hứa gả dưỡng nữ cho thúc, giờ cô gái đó đã đến tuổi gả rồi.","Năm đó Trung Thường Tự Đường Hoành hứa gả dưỡng nữ cho thúc, giờ cô gái đó đã đến tuổi gả rồi.","Tuân Úc","",},
        [699] = {2018401,"Tuân Du","Tuân thị chúng ta là thế gia đại tộc, nếu lấy con gái của hoạn quan chắc chắn sẽ bị chỉ trích? Thúc phụ, nay Đường Hoành đã mất, hay là hủy hôn?","Tuân thị chúng ta là thế gia đại tộc, nếu lấy con gái của hoạn quan chắc chắn sẽ bị chỉ trích? Thúc phụ, nay Đường Hoành đã mất, hay là hủy hôn?","Tuân Úc","",},
        [700] = {2018401,"Tuân Úc","Hoang đường! Hôn sự đã định thì sao hủy được, càng không thể thất tín!","Hoang đường! Hôn sự đã định thì sao hủy được, càng không thể thất tín!","Tuân Du","",},
        [701] = {2018401,"Tuân Úc","Đường Hoành tuy là Trung Thường Thị, nhưng con gái của hắn là vô tội. Tuân Úc ta không thẹn với lòng, sao phải lo lắng người khác chỉ trích.","Đường Hoành tuy là Trung Thường Thị, nhưng con gái của hắn là vô tội. Tuân Úc ta không thẹn với lòng, sao phải lo lắng người khác chỉ trích.","Tuân Du","",},
        [702] = {2018401,"Tuân Du","Thúc phụ nói rất đúng.","Thúc phụ nói rất đúng.","Tuân Úc","",},
        [703] = {2018402,"Tào Tháo","Nay xe ngựa thiên tử đã về Lạc Dương, các vị có ý kiến gì không?","Nay xe ngựa thiên tử đã về Lạc Dương, các vị có ý kiến gì không?","Tuân Úc","",},
        [704] = {2018402,"Tuân Úc","Xưa Tấn Văn Công thu nhận Chu Tương Vương khiến chư hầu phục tùng. Hán Cao Tổ để tang cho Nghĩa Đế khiến thiên hạ một lòng.","Xưa Tấn Văn Công thu nhận Chu Tương Vương khiến chư hầu phục tùng. Hán Cao Tổ để tang cho Nghĩa Đế khiến thiên hạ một lòng.","Tào Tháo","",},
        [705] = {2018402,"Tuân Úc","Nay thiên tử gặp nạn, tướng quân nên nhân lúc này khởi xướng nghĩa binh để thuận theo lòng bá tánh. Nếu không thực hiện sớm, người khác sẽ nhanh chân hơn chúng ta.","Nay thiên tử gặp nạn, tướng quân nên nhân lúc này khởi xướng nghĩa binh để thuận theo lòng bá tánh. Nếu không thực hiện sớm, người khác sẽ nhanh chân hơn chúng ta.","Tào Tháo","",},
        [706] = {2018402,"Tào Tháo","Ha ha, Tuân Lệnh Quân nói rất hợp ý ta. Giờ ta sẽ dẫn quân đi Lạc Dương, nghênh tiếp thiên tử dời đô đến Hứa Xương!","Ha ha, Tuân Lệnh Quân nói rất hợp ý ta. Giờ ta sẽ dẫn quân đi Lạc Dương, nghênh tiếp thiên tử dời đô đến Hứa Xương!","Tuân Úc","",},
        [707] = {2018403,"Tào Tháo","Hiện quân ta trấn giữ Quan Độ, quân lực yếu dần, lương thảo không đủ. Ta muốn từ bỏ Quan Độ lui về Hứa Xương, ý chư vị thế nào?","Hiện quân ta trấn giữ Quan Độ, quân lực yếu dần, lương thảo không đủ. Ta muốn từ bỏ Quan Độ lui về Hứa Xương, ý chư vị thế nào?","Tuân Du","",},
        [708] = {2018403,"Tuân Du","Sao tướng quân không viết thư hỏi thúc phụ ta?","Sao tướng quân không viết thư hỏi thúc phụ ta?","Tào Tháo","",},
        [709] = {2018403,"Tào Tháo","Người đâu, mau cho người mang bức thư này đến Hứa Xương.","Người đâu, mau cho người mang bức thư này đến Hứa Xương.","Tuân Du","",},
        [710] = {2018403,"Tuân Du","Tướng quân, đây là thư của thúc phụ, mời xem!","Tướng quân, đây là thư của thúc phụ, mời xem!","Tào Tháo","",},
        [711] = {2018403,"Tào Tháo","(Đọc thư của Tuân Úc) Minh công lấy yếu địch mạnh, nếu không thể chống đỡ sẽ tạo cơ hội cho kẻ địch, đây là cơ hội lớn quyết thắng thiên hạ.","(Đọc thư của Tuân Úc) Minh công lấy yếu địch mạnh, nếu không thể chống đỡ sẽ tạo cơ hội cho kẻ địch, đây là cơ hội lớn quyết thắng thiên hạ.","Tuân Du","",},
        [712] = {2018403,"Tào Tháo","Quân Viên Thiệu tuy đông nhưng không biết dùng binh. Nay minh công vạch đất trấn thủ khiến hắn không thể tiến công, đợi khí thế của hắn mất hết ắt sẽ có biến, đó là lúc dùng kỳ binh để quyết thắng.","Quân Viên Thiệu tuy đông nhưng không biết dùng binh. Nay minh công vạch đất trấn thủ khiến hắn không thể tiến công, đợi khí thế của hắn mất hết ắt sẽ có biến, đó là lúc dùng kỳ binh để quyết thắng.","Tuân Du","",},
        [713] = {2018403,"Tào Tháo","Ha ha, Tuân Lệnh Quân quả thật có tài. Chư vị nghe lệnh, trấn giữ Quan Độ bằng mọi giá, chờ đợi thời cơ!","Ha ha, Tuân Lệnh Quân quả thật có tài. Chư vị nghe lệnh, trấn giữ Quan Độ bằng mọi giá, chờ đợi thời cơ!","Tuân Du","",},
        [714] = {2018404,"Đổng Chiêu","Trước giờ chưa ai có công lớn như thừa tướng. Bôn ba hơn ba mươi năm, giúp bá tánh trừ hại, khôi phục nhà Hán. Người nên nhận vị trí Ngụy công, được ban Cửu Tích.","Trước giờ chưa ai có công lớn như thừa tướng. Bôn ba hơn ba mươi năm, giúp bá tánh trừ hại, khôi phục nhà Hán. Người nên nhận vị trí Ngụy công, được ban Cửu Tích.","Tuân Úc","",},
        [715] = {2018404,"Tuân Úc","Không thể, thừa tướng vốn là vì việc giúp triều yên nước mà chiêu tập nghĩa binh, từ trước đến nay giữ lòng trung trinh, giữ bề khiêm tốn. Người quân tử phải lấy đức yêu người, không nên như vậy.","Không thể, thừa tướng vốn là vì việc giúp triều yên nước mà chiêu tập nghĩa binh, từ trước đến nay giữ lòng trung trinh, giữ bề khiêm tốn. Người quân tử phải lấy đức yêu người, không nên như vậy.","Đổng Chiêu","",},
        [716] = {2018404,"Đổng Chiêu","Làm sao để một người cản trở ý muốn của nhiều người?","Làm sao để một người cản trở ý muốn của nhiều người?","Tuân Úc","",},
        [717] = {2018404,"Tuân Úc","Hôm nay ta không muốn thấy chuyện này!","Hôm nay ta không muốn thấy chuyện này!","Đổng Chiêu","",},
        [718] = {2018404,"Tào Tháo","...(Tháo tím mặt, trong lòng hận Tuân Úc)","...(Tháo tím mặt, trong lòng hận Tuân Úc)","Tuân Úc","",},
        [719] = {2018405,"Tào Tháo","Tuân Lệnh Quân mượn cớ bệnh ở Thọ Xuyến. Người đâu, mau mang hộp thức ăn đến Thọ Xuyến!","Tuân Lệnh Quân mượn cớ bệnh ở Thọ Xuyến. Người đâu, mau mang hộp thức ăn đến Thọ Xuyến!","","",},
        [720] = {2018405,"Tuân Du","Thúc phụ, Tào Tháo sai ngươi đến đưa hộp đồ ăn, trên hộp có niêm phong của ngài ấy.","Thúc phụ, Tào Tháo sai ngươi đến đưa hộp đồ ăn, trên hộp có niêm phong của ngài ấy.","","",},
        [721] = {2018405,"Tuân Úc","(Mở hộp thức ăn, bên trong trống rỗng) Ta đi theo Tào Tháo nhiều năm, hai bên đã quá hiểu nhau rồi.","(Mở hộp thức ăn, bên trong trống rỗng) Ta đi theo Tào Tháo nhiều năm, hai bên đã quá hiểu nhau rồi.","Tuân Du","",},
        [722] = {2018405,"Tuân Du","Thúc phụ, đây là ý gì?","Thúc phụ, đây là ý gì?","Tuân Úc","",},
        [723] = {2018405,"Tuân Úc","Tuân Úc nay không có bổng lộc, Tuân Úc hiểu rõ, cần chi phải làm thêm việc như vậy. (Tuân Úc đuổi hết người đi, dùng thuộc độc tự sát, thọ 49 tuổi.)","Tuân Úc nay không có bổng lộc, Tuân Úc hiểu rõ, cần chi phải làm thêm việc như vậy. (Tuân Úc đuổi hết người đi, dùng thuộc độc tự sát, thọ 49 tuổi.)","Tuân Du","",},
        [724] = {2018501,"Sa Ma Kha","Khụ khụ, mấy hôm trước bị bệnh, xin tiểu nương tử bố thí cho ta vài đồng tiền để đi khám đại phu.","Khụ khụ, mấy hôm trước bị bệnh, xin tiểu nương tử bố thí cho ta vài đồng tiền để đi khám đại phu.","Tiểu Nương Tử","",},
        [725] = {2018501,"Tiểu Nương Tử","Bệnh dịch lần này khá nghiêm trọng, nghe nói Nước Bùa của lương sư Trương Giác có thể trị bệnh, ngươi có thể đi tìm lương sư.","Bệnh dịch lần này khá nghiêm trọng, nghe nói Nước Bùa của lương sư Trương Giác có thể trị bệnh, ngươi có thể đi tìm lương sư.","Sa Ma Kha","",},
        [726] = {2018501,"Sa Ma Kha","Đa tạ tiểu nương tử chỉ điểm. Đại hiền lương sư, mong người hãy cứu ta!","Đa tạ tiểu nương tử chỉ điểm. Đại hiền lương sư, mong người hãy cứu ta!","Tiểu Nương Tử","",},
        [727] = {2018501,"Trương Giác","Trương Giác ta được Nam Hoa Lão Tiên truyền thụ Thái Bình Thuật, tự sáng lập Thái Bình Đạo, đã có 36 vạn đệ tử!","Trương Giác ta được Nam Hoa Lão Tiên truyền thụ Thái Bình Thuật, tự sáng lập Thái Bình Đạo, đã có 36 vạn đệ tử!","Sa Ma Kha","",},
        [728] = {2018501,"Trương Giác","Nếu các ngươi gia nhập Thái Bình Đạo của ta thì nhất định sẽ khỏi bệnh, đảm bảo bình an!","Nếu các ngươi gia nhập Thái Bình Đạo của ta thì nhất định sẽ khỏi bệnh, đảm bảo bình an!","Sa Ma Kha","",},
        [729] = {2018501,"Sa Ma Kha","Tốt quá, ta muốn gia nhập Thái Bình Đạo!","Tốt quá, ta muốn gia nhập Thái Bình Đạo!","Trương Giác","",},
        [730] = {2018502,"Trương Giác","Lòng dân là khó đạt nhất. Nay lòng dân đã thuận, nếu không thừa thế đoạt thiên hạ thì rất tiếc. Từ nay ta sẽ gọi là Thiên Công tướng quân.","Lòng dân là khó đạt nhất. Nay lòng dân đã thuận, nếu không thừa thế đoạt thiên hạ thì rất tiếc. Từ nay ta sẽ gọi là Thiên Công tướng quân.","","",},
        [731] = {2018502,"Trương Bảo","Đại ca, vậy còn ta?","Đại ca, vậy còn ta?","Trương Giác","",},
        [732] = {2018502,"Trương Giác","Ngươi là Địa Công tướng quân.","Ngươi là Địa Công tướng quân.","Trương Bảo","",},
        [733] = {2018502,"Trương Lương","Đại ca, vậy còn ta?","Đại ca, vậy còn ta?","Trương Giác","",},
        [734] = {2018502,"Trương Giác","Ngươi là Nhân Công tướng quân.","Ngươi là Nhân Công tướng quân.","Trương Lương","",},
        [735] = {2018502,"Trương Giác","Nay vận mệnh nhà Hán đã hết, chúng ta phải thuận theo ý trời để đem lại thái bình.","Nay vận mệnh nhà Hán đã hết, chúng ta phải thuận theo ý trời để đem lại thái bình.","Trương Lương","",},
        [736] = {2018502,"Trương Giác","Ha ha! Triều Hán đã sắp diệt vong, thay vào đó sẽ là quân Hoàng Cân!","Ha ha! Triều Hán đã sắp diệt vong, thay vào đó sẽ là quân Hoàng Cân!","Trương Lương","",},
        [737] = {2018503,"Nhân vật","Trương Giác, hôm nay sẽ là ngày tàn của ngươi, mau nộp mạng đi.","Trương Giác, hôm nay sẽ là ngày tàn của ngươi, mau nộp mạng đi.","Trương Giác","",},
        [738] = {2018503,"Trương Giác","Ông trời ơi...","Ông trời ơi...","Nhân vật","",},
        [739] = {2018601,"Viên Thuật","Nghe nói Tôn Kiên đó có công thảo phạt Hoàng Cân, không biết nội tình như thế nào?","Nghe nói Tôn Kiên đó có công thảo phạt Hoàng Cân, không biết nội tình như thế nào?","Viên Thuật Thủ Hạ","",},
        [740] = {2018601,"Viên Thuật Thủ Hạ","Tôn thị nhiều đời làm quan ở đất Ngô. Nghe nói trước khi Tôn Kiên ra đời, mộ tổ tiên xuất hiện ánh sáng lạ, mây ngũ sắc trải dài hàng dặm.","Tôn thị nhiều đời làm quan ở đất Ngô. Nghe nói trước khi Tôn Kiên ra đời, mộ tổ tiên xuất hiện ánh sáng lạ, mây ngũ sắc trải dài hàng dặm.","Viên Thuật","",},
        [741] = {2018601,"Viên Thuật","Ồ, còn có chuyện lạ như vậy.","Ồ, còn có chuyện lạ như vậy.","Viên Thuật Thủ Hạ","",},
        [742] = {2018601,"Viên Thuật Thủ Hạ","Bá tánh nơi đó đều nói đó là điềm báo, Tôn gia hẳn sẽ cường thịnh trở lại. Đến lúc Tôn Kiên ra đời, dung mạo quả nhiên bất phàm.","Bá tánh nơi đó đều nói đó là điềm báo, Tôn gia hẳn sẽ cường thịnh trở lại. Đến lúc Tôn Kiên ra đời, dung mạo quả nhiên bất phàm.","Viên Thuật","",},
        [743] = {2018601,"Viên Thuật Thủ Hạ","Tôn Kiên lúc trẻ từng nhận chức Huyện Thừa, rất có danh vọng, sau có công tiêu diệt phản quân nên thăng chức Phượng Tường Thái Thú.","Tôn Kiên lúc trẻ từng nhận chức Huyện Thừa, rất có danh vọng, sau có công tiêu diệt phản quân nên thăng chức Phượng Tường Thái Thú.","Viên Thuật","",},
        [744] = {2018601,"Viên Thuật","Dù hắn tài giỏi thế nào cũng không thể so được với Viên gia ta.","Dù hắn tài giỏi thế nào cũng không thể so được với Viên gia ta.","Viên Thuật Thủ Hạ","",},
        [745] = {2018601,"Viên Thuật Thủ Hạ","Chúa công, nếu người này có bản lĩnh, hay là thu làm thuộc hạ cùng đánh Đổng Trác.","Chúa công, nếu người này có bản lĩnh, hay là thu làm thuộc hạ cùng đánh Đổng Trác.","Viên Thuật","",},
        [746] = {2018601,"Viên Thuật","Cũng được, vậy sẽ đề nghị hắn làm Phá Lỗ tướng quân, kiêm lĩnh Dự Châu Thích Sử.","Cũng được, vậy sẽ đề nghị hắn làm Phá Lỗ tướng quân, kiêm lĩnh Dự Châu Thích Sử.","Viên Thuật Thủ Hạ","",},
        [747] = {2018602,"Lưu Biểu","Tôn Văn Đài, sao lại đi ngang Kinh Châu?","Tôn Văn Đài, sao lại đi ngang Kinh Châu?","Tôn Kiên","",},
        [748] = {2018602,"Tôn Kiên","Chư Hầu Nhĩ Ngu đấu đá nhau, Tôn mỗ quyết định trở về Giang Đông.","Chư Hầu Nhĩ Ngu đấu đá nhau, Tôn mỗ quyết định trở về Giang Đông.","Lưu Biểu","",},
        [749] = {2018602,"Tôn Kiên","Lúc trước ta cùng Viên Thiệu Viên Thuật khởi binh, mục đích là đẻ cứu giang sơn xã tắc. Nay địch đã dọn sạch nhưng nội bộ lại phân tranh, ta phải làm thế nào đây?","Lúc trước ta cùng Viên Thiệu Viên Thuật khởi binh, mục đích là đẻ cứu giang sơn xã tắc. Nay địch đã dọn sạch nhưng nội bộ lại phân tranh, ta phải làm thế nào đây?","Lưu Biểu","",},
        [750] = {2018602,"Lưu Biểu","Ngươi muốn về Giang Đông thì về, nhưng Ngọc Tỷ là bảo vật triều đình, sao ngươi lại mang theo?","Ngươi muốn về Giang Đông thì về, nhưng Ngọc Tỷ là bảo vật triều đình, sao ngươi lại mang theo?","Tôn Kiên","",},
