        [752] = {2018603,"Hàn Đương","Tướng quân, hôm nay gió lớn e rằng là điềm chẳng lành.","Tướng quân, hôm nay gió lớn e rằng là điềm chẳng lành.","Tôn Kiên","",},
        [753] = {2018603,"Tôn Kiên","Chiếm Tương Dương chỉ là việc tương lai; Sao lại vì chuyện này mà lui binh được!","Chiếm Tương Dương chỉ là việc tương lai; Sao lại vì chuyện này mà lui binh được!","Hàn Đương","",},
        [754] = {2018603,"Khoái Lương","Đêm nọ nhìn trời thấy Tướng Tinh rơi ở chỗ Tôn Kiên. Chúa công nên gửi thử đến Viên Thiệu, nhờ cứu trợ.","Đêm nọ nhìn trời thấy Tướng Tinh rơi ở chỗ Tôn Kiên. Chúa công nên gửi thử đến Viên Thiệu, nhờ cứu trợ.","Tôn Kiên","",},
        [755] = {2018603,"Lưu Biểu","Nay Tương Dương bị vậy, ai dám phá vòng vây?","Nay Tương Dương bị vậy, ai dám phá vòng vây?","Tôn Kiên","",},
