        [764] = {2018604,"Quân Sĩ","Tướng quân! (Đá từ trên rơi xuống, tên từ rừng bắn ra, Tôn Kiên trúng tên hi sinh, hưởng thọ 37 tuổi)","Tướng quân! (Đá từ trên rơi xuống, tên từ rừng bắn ra, Tôn Kiên trúng tên hi sinh, hưởng thọ 37 tuổi)","Tôn Kiên","",},
        [765] = {2018701,"Hoàng Huyện Quận Thú","Hiện các châu quận đang đấu nhau, hãy để Hữu Tư (Quan lại chưởng hình thưởng) của Lạc Dương định đoạt.","Hiện các châu quận đang đấu nhau, hãy để Hữu Tư (Quan lại chưởng hình thưởng) của Lạc Dương định đoạt.","Thị Thiếp","",},
        [766] = {2018701,"Thị Thiếp","Ai có thể đưa tấu chương cho Hữu Tư trước thì người đó có lợi.","Ai có thể đưa tấu chương cho Hữu Tư trước thì người đó có lợi.","Hoàng Huyện Quận Thú","",},
