        [770] = {2018702,"Thái Sử Từ","(Đợi lâu trước công môn) Ngươi cũng đến muốn được thông chương sao?","(Đợi lâu trước công môn) Ngươi cũng đến muốn được thông chương sao?","Châu Lại","",},
