        [779] = {2018703,"Thái Sử Từ","Ta là Đông Lai Thái Sử Từ, đến để bắt Tôn Sách! Ai là Tôn Sách?","Ta là Đông Lai Thái Sử Từ, đến để bắt Tôn Sách! Ai là Tôn Sách?","Tôn Sách","",},
        [780] = {2018703,"Tôn Sách","Là ta đây. Ngươi tưởng đi 2 người là ta sợ sao! Nếu sợ thì không còn là Tiêu Bá Phù nữa!","Là ta đây. Ngươi tưởng đi 2 người là ta sợ sao! Nếu sợ thì không còn là Tiêu Bá Phù nữa!","Thái Sử Từ","",},
