        [795] = {2018803,"Tôn Sách","Cô nương còn nhớ ta không? Hôm đó sau khi gặp cô nương ta không thể quên được, không biết nàng có muốn gả cho ta không?","Cô nương còn nhớ ta không? Hôm đó sau khi gặp cô nương ta không thể quên được, không biết nàng có muốn gả cho ta không?","Đại Kiều","",},
        [796] = {2018803,"Tôn Sách","Cô nương yên tâm, nếu cô nương không đồng ý thì Tôn Bá Phù tuyệt đối không làm khó nàng.","Cô nương yên tâm, nếu cô nương không đồng ý thì Tôn Bá Phù tuyệt đối không làm khó nàng.","Đại Kiều","",},
