        [798] = {2018803,"Tôn Sách","Nếu cô nương không muốn nói thì hãy gật đầu để đồng ý, lắc đầu để từ chối.","Nếu cô nương không muốn nói thì hãy gật đầu để đồng ý, lắc đầu để từ chối.","Đại Kiều","",},
        [799] = {2018803,"Đại Kiều","(Nhẹ nhàng gật đầu) Công tử có tâm, Đại Kiều cũng nguyện đi cùng!","(Nhẹ nhàng gật đầu) Công tử có tâm, Đại Kiều cũng nguyện đi cùng!","Tôn Sách","",},
        [800] = {2018901,"Trác Quận Thái Thú","Con gái ngoan, hôm nay ta đã chọn cho con một nhà tốt!","Con gái ngoan, hôm nay ta đã chọn cho con một nhà tốt!","Phu Nhân","",},
        [801] = {2018901,"Phu Nhân","Cha, chuyện đại sự của con gái sao có thể tùy tiện như vậy!","Cha, chuyện đại sự của con gái sao có thể tùy tiện như vậy!","Trác Quận Thái Thú","",},
        [802] = {2018901,"Trác Quận Thái Thú","Người ta chọn chắc chắn con sẽ đồng ý. Người này tên Công Tôn Toản, xuất thân Liêu Tây quý tộc Công Tôn Thị.","Người ta chọn chắc chắn con sẽ đồng ý. Người này tên Công Tôn Toản, xuất thân Liêu Tây quý tộc Công Tôn Thị.","Phu Nhân","",},
        [803] = {2018901,"Trác Quận Thái Thú","Công Tôn Toản này tuấn tú tài giỏi, tương lai vô hạn.","Công Tôn Toản này tuấn tú tài giỏi, tương lai vô hạn.","Phu Nhân","",},
        [804] = {2018901,"Phu Nhân","Nếu vậy thì cha hãy làm chủ cho con!","Nếu vậy thì cha hãy làm chủ cho con!","Trác Quận Thái Thú","",},
