        [1344] = {20184101,"Nguyệt Anh","Di mẫu mời ta đến phủ làm khách, không biết có dụng ý gì?","Di mẫu mời ta đến phủ làm khách, không biết có dụng ý gì?","Thái Phu Nhân","",},
        [1345] = {20184101,"Thái Phu Nhân","Nguyệt Anh, dì nhớ con. Con nhìn con suốt ngày lấy khăn che mặt, người ta cứ tưởng con xấu, thật ra Nguyệt Anh của ta xinh đẹp tuyệt trần.","Nguyệt Anh, dì nhớ con. Con nhìn con suốt ngày lấy khăn che mặt, người ta cứ tưởng con xấu, thật ra Nguyệt Anh của ta xinh đẹp tuyệt trần.","Nguyệt Anh","",},
        [1346] = {20184101,"Nguyệt Anh","Dì lại chọc ghẹo người ta rồi, Nguyệt Anh không thích gặp người ngoài.","Dì lại chọc ghẹo người ta rồi, Nguyệt Anh không thích gặp người ngoài.","Thái Phu Nhân","",},
        [1347] = {20184101,"Thái Phu Nhân","Đến xem này, cậu Thái Mạo có tặng mấy tấm Thục Cẩm, xem coi con thích cái nào?","Đến xem này, cậu Thái Mạo có tặng mấy tấm Thục Cẩm, xem coi con thích cái nào?","Nguyệt Anh","",},
        [1348] = {20184101,"Nguyệt Anh","Nguyệt Anh suốt ngày đọc sách, chỉ thích nghề mộc, e rằng sẽ lãng phí Thục Cẩm đầy giá trị.","Nguyệt Anh suốt ngày đọc sách, chỉ thích nghề mộc, e rằng sẽ lãng phí Thục Cẩm đầy giá trị.","Thái Phu Nhân","",},
        [1349] = {20184101,"Thái Phu Nhân","Thục Cẩm này là do cựu cựu Thái Mạo phái người đưa đến, con đừng phụ lòng ngài ấy. Hơn nữa con sắp thành thân rồi, phải mặc đồ màu sặc sỡ hơn mới phải.","Thục Cẩm này là do cựu cựu Thái Mạo phái người đưa đến, con đừng phụ lòng ngài ấy. Hơn nữa con sắp thành thân rồi, phải mặc đồ màu sặc sỡ hơn mới phải.","Nguyệt Anh","",},
        [1350] = {20184101,"Nguyệt Anh","Dì cứ thay con chọn là được.","Dì cứ thay con chọn là được.","Thái Phu Nhân","",},
        [1351] = {20184102,"Lưu Biểu","Triều đình bổ nhiệm ta làm Kinh Châu Thích Sử. Kinh Châu trộm cướp lộng hành, Thái Mạo, Thái gia là danh tộc Tương Dương, ngươi có kế gì có thể trị loạn không?","Triều đình bổ nhiệm ta làm Kinh Châu Thích Sử. Kinh Châu trộm cướp lộng hành, Thái Mạo, Thái gia là danh tộc Tương Dương, ngươi có kế gì có thể trị loạn không?","Thái Mạo","",},
        [1352] = {20184102,"Thái Mạo","Thủ lĩnh bọn trộm cướp tham lam, tàn bạo. Ta có một số người tài có thể phái đến gặp hắn cho chút lợi lộc, hắn ắt sẽ gọi mọi người đến đây. Sau đó người có thể diệt hắn rồi chiêu hàng thủ hạ của hắn.","Thủ lĩnh bọn trộm cướp tham lam, tàn bạo. Ta có một số người tài có thể phái đến gặp hắn cho chút lợi lộc, hắn ắt sẽ gọi mọi người đến đây. Sau đó người có thể diệt hắn rồi chiêu hàng thủ hạ của hắn.","Lưu Biểu","",},
        [1353] = {20184102,"Thái Mạo","Khi đó, người ở Kinh Châu biết ngài nhân đức, chắc chắn sẽ kéo đến. Sau đó tập hợp binh lính, nam cứ Giang Lăng, bắc thủ Tương Dương, Kinh Châu tám quận đều sẽ được yên ổn.","Khi đó, người ở Kinh Châu biết ngài nhân đức, chắc chắn sẽ kéo đến. Sau đó tập hợp binh lính, nam cứ Giang Lăng, bắc thủ Tương Dương, Kinh Châu tám quận đều sẽ được yên ổn.","Lưu Biểu","",},
        [1354] = {20184102,"Lưu Biểu","Nếu việc này xong, ta sẽ bẩm báo triều đình ban thưởng cho ngươi!","Nếu việc này xong, ta sẽ bẩm báo triều đình ban thưởng cho ngươi!","Thái Mạo","",},
        [1355] = {20184103,"Lưu Tông","Cha ta đã mất, ta có người anh ở Giang Hạ, thúc phụ Huyền Đức ở Tân Dã. Các ngươi lập ta làm vua, nếu anh ta và thúc phụ khởi binh hỏi tội, ta phải làm sao?","Cha ta đã mất, ta có người anh ở Giang Hạ, thúc phụ Huyền Đức ở Tân Dã. Các ngươi lập ta làm vua, nếu anh ta và thúc phụ khởi binh hỏi tội, ta phải làm sao?","","",},
        [1356] = {20184103,"Lý Khuê","Hãy gửi thư báo tang đến Giang Hạ, xin đại công tử làm chủ Kinh Châu, lệnh Huyền Đức cùng quản lý, bắc có thể địch Tào Tháo, nam có thể chống lại Tôn Quyền. Đây là kế vẹn toàn.","Hãy gửi thư báo tang đến Giang Hạ, xin đại công tử làm chủ Kinh Châu, lệnh Huyền Đức cùng quản lý, bắc có thể địch Tào Tháo, nam có thể chống lại Tôn Quyền. Đây là kế vẹn toàn.","Lưu Tông","",},
        [1357] = {20184103,"Thái Mạo","Ngươi là ai, sao dám nói điều trái lời chúa công!","Ngươi là ai, sao dám nói điều trái lời chúa công!","Lý Khuê","",},
        [1358] = {20184103,"Lý Khuê","Ngươi nội ứng ngoại hợp, giả truyền di mệnh, phế trưởng lập ấu, Kinh Tương chín quận dâng cả cho Thái Thị! Cố chủ có linh thiêng sẽ không tha cho ngươi!","Ngươi nội ứng ngoại hợp, giả truyền di mệnh, phế trưởng lập ấu, Kinh Tương chín quận dâng cả cho Thái Thị! Cố chủ có linh thiêng sẽ không tha cho ngươi!","Thái Mạo","",},
        [1359] = {20184103,"Thái Mạo","Người đâu, kẻ này dám ăn nói xằng bậy, lôi ra trảm thủ! Chúa công trước khi mất đã dặn cho con thứ Lưu Tông làm chủ Kinh Châu!","Người đâu, kẻ này dám ăn nói xằng bậy, lôi ra trảm thủ! Chúa công trước khi mất đã dặn cho con thứ Lưu Tông làm chủ Kinh Châu!","Lý Khuê","",},
        [1360] = {20184104,"Thái Mạo","Muội muội, Lưu Bị phái ba tướng đi, một mình ở Kinh Châu, chắc chắn sẽ gặp họa.","Muội muội, Lưu Bị phái ba tướng đi, một mình ở Kinh Châu, chắc chắn sẽ gặp họa.","Thái Phu Nhân","",},
        [1361] = {20184104,"Thái Phu Nhân","Ta nghe nói người ở Kinh Châu thường qua lại với Lưu Bị, không thể không đề phòng.","Ta nghe nói người ở Kinh Châu thường qua lại với Lưu Bị, không thể không đề phòng.","Thái Mạo","",},
        [1362] = {20184104,"Thái Mạo","Hay là hãy diệt hắn trước rồi bẩm báo lại với chúa công.","Hay là hãy diệt hắn trước rồi bẩm báo lại với chúa công.","Thái Phu Nhân","",},
        [1363] = {20184104,"Y Tịch","Thái Mạo nghĩ kế hại người, ba nơi đông, nam, bắc ngoài thành đều có quân lính trấn giữ. Chỉ có cửa tây có thể đi được, ngài hãy mau chạy!","Thái Mạo nghĩ kế hại người, ba nơi đông, nam, bắc ngoài thành đều có quân lính trấn giữ. Chỉ có cửa tây có thể đi được, ngài hãy mau chạy!","Thái Mạo","",},
        [1364] = {20184104,"Thái Mạo","Sao ngài lại phải chạy trốn?","Sao ngài lại phải chạy trốn?","Y Tịch","",},
        [1365] = {20184104,"Lưu Bị","Ta và ngươi không thù không oán, sao lại hại ta? Cũng may ta có Đích Lô Mã, nếu không đã mất mạng!","Ta và ngươi không thù không oán, sao lại hại ta? Cũng may ta có Đích Lô Mã, nếu không đã mất mạng!","Thái Mạo","",},
        [1366] = {20184105,"Thái Mạo","Chúa công, Tào Tháo dẫn đại quân hướng đến Tương Dương! Chúa công hay là dâng Kinh Tương chín quận cho Tào Tháo, Tào Tháo sẽ trọng dụng chúa công.","Chúa công, Tào Tháo dẫn đại quân hướng đến Tương Dương! Chúa công hay là dâng Kinh Tương chín quận cho Tào Tháo, Tào Tháo sẽ trọng dụng chúa công.","Lưu Kỳ","",},
        [1367] = {20184105,"Lưu Kỳ","Ta vừa kế thừa cơ nghiệp tiên quân, sao có thể dâng cho người khác?","Ta vừa kế thừa cơ nghiệp tiên quân, sao có thể dâng cho người khác?","Thái Mạo","",},
        [1368] = {20184105,"Thái Mạo","Nay Tào Tháo lấy danh nghĩa triều đình, chúa công từ chối sẽ không đúng lẽ. Hơn nữa chúa công mới lên ngôi, giặc trong giặc ngoài, biết phải làm sao chống chọi? Chúa công không nên do dự.","Nay Tào Tháo lấy danh nghĩa triều đình, chúa công từ chối sẽ không đúng lẽ. Hơn nữa chúa công mới lên ngôi, giặc trong giặc ngoài, biết phải làm sao chống chọi? Chúa công không nên do dự.","Lưu Kỳ","",},
        [1369] = {20184105,"Lưu Kỳ","Vậy cứ theo lời tướng quân, dâng Kinh Châu cho Tào Tháo.","Vậy cứ theo lời tướng quân, dâng Kinh Châu cho Tào Tháo.","Thái Mạo","",},
        [1370] = {20184106,"Tào Tháo","Trận chiến Tam Giang Khẩu, Đông Ngô tuy quân ít nhưng lại chiến thắng đó là do chúng ta bất cẩn!","Trận chiến Tam Giang Khẩu, Đông Ngô tuy quân ít nhưng lại chiến thắng đó là do chúng ta bất cẩn!","Thái Mạo","",},
        [1371] = {20184106,"Thái Mạo","Kinh Châu Thủy Quân lâu không rèn luyện, quân Thanh, Từ lại không quen thủy chiến vì vậy mới bại trận. Nay nên lập Thủy Trại, lệnh quân Thanh, Từ ở giữa, quân Kinh Châu ở ngoài, ngày ngày luyện tập.","Kinh Châu Thủy Quân lâu không rèn luyện, quân Thanh, Từ lại không quen thủy chiến vì vậy mới bại trận. Nay nên lập Thủy Trại, lệnh quân Thanh, Từ ở giữa, quân Kinh Châu ở ngoài, ngày ngày luyện tập.","Tào Tháo","",},
        [1372] = {20184106,"Tào Tháo","Ngươi là đô đốc thủy quân, cứ làm theo ý ngươi, không cần bẩm báo!","Ngươi là đô đốc thủy quân, cứ làm theo ý ngươi, không cần bẩm báo!","Thái Mạo","",},
        [1373] = {20184106,"Thái Mạo","Thủy quân giao cho Thái Mạo, thừa tướng cứ yên tâm, Thái Mạo sẽ cố gắng rèn luyện, việc bắt Chu Du chỉ làm sớm muộn!","Thủy quân giao cho Thái Mạo, thừa tướng cứ yên tâm, Thái Mạo sẽ cố gắng rèn luyện, việc bắt Chu Du chỉ làm sớm muộn!","Tào Tháo","",},
        [1374] = {20184107,"Tưởng Can","Thừa tướng, lần này ta đi Đông Ngô, tuy chưa thể thuyết phục Chu Du, nhưng đã thăm dò được một việc, Thái Mạo, Trương Doãn hai người đang âm thầm cấu kết với Chu Du, e rằng sẽ bất lợi cho thừa tướng!","Thừa tướng, lần này ta đi Đông Ngô, tuy chưa thể thuyết phục Chu Du, nhưng đã thăm dò được một việc, Thái Mạo, Trương Doãn hai người đang âm thầm cấu kết với Chu Du, e rằng sẽ bất lợi cho thừa tướng!","Tào Tháo","",},
        [1375] = {20184107,"Tào Tháo","Thật to gan! Thái Mạo, Trương Doãn! Ta sẽ cho hai người các ngươi tiến quân.","Thật to gan! Thái Mạo, Trương Doãn! Ta sẽ cho hai người các ngươi tiến quân.","Tưởng Can","",},
        [1376] = {20184107,"Thái Mạo","Thừa tướng, thủy quân vẫn chưa thuần thục, không thể khinh suất.","Thừa tướng, thủy quân vẫn chưa thuần thục, không thể khinh suất.","Tào Tháo","",},
        [1377] = {20184107,"Tào Tháo","Đợi nữa, thủ cấp của ta đã phải dâng cho Chu Lang rồi! Người đâu, lôi Thái Mạo, Trương Doãn ra trảm thủ!","Đợi nữa, thủ cấp của ta đã phải dâng cho Chu Lang rồi! Người đâu, lôi Thái Mạo, Trương Doãn ra trảm thủ!","Thái Mạo","",},
        [1378] = {20184107,"Thái Mạo","Thừa Tướng! Thái Mạo một lòng trung thành!","Thừa Tướng! Thái Mạo một lòng trung thành!","Tào Tháo","",},
        [1379] = {20184201,"Điển Vi","Lý Vĩnh, ngươi diệt Lưu Thị, ta sẽ lấy mạng ngươi để báo thù.","Lý Vĩnh, ngươi diệt Lưu Thị, ta sẽ lấy mạng ngươi để báo thù.","Lý Vĩnh","",},
        [1380] = {20184201,"Lý Vĩnh","Ta là huyện lệnh Phú Xuân, trong nhà canh phòng nghiêm ngặt, sao phải sợ ngươi.","Ta là huyện lệnh Phú Xuân, trong nhà canh phòng nghiêm ngặt, sao phải sợ ngươi.","Điển Vi","",},
        [1381] = {20184201,"Điển Vi","Ngươi hãy đợi đấy, ta thề sẽ lấy mạng ngươi! (Sau Điển Vi mang gà và rượu đến, cải trang thành người hỏi thăm Lý Vĩnh, sau khi mở cửa, hắn xông vào diệt Lý Vĩnh, vì vậy được hào kiệt biết đến.)","Ngươi hãy đợi đấy, ta thề sẽ lấy mạng ngươi! (Sau Điển Vi mang gà và rượu đến, cải trang thành người hỏi thăm Lý Vĩnh, sau khi mở cửa, hắn xông vào diệt Lý Vĩnh, vì vậy được hào kiệt biết đến.)","Lý Vĩnh","",},
        [1382] = {20184202,"Trương Mạc","Ngươi là ai? Cờ nha môn vừa lớn vừa nặng, không ngờ ngươi một tay có thể cầm lên.","Ngươi là ai? Cờ nha môn vừa lớn vừa nặng, không ngờ ngươi một tay có thể cầm lên.","Điển Vi","",},
        [1383] = {20184202,"Điển Vi","Ta là Điển Vi.","Ta là Điển Vi.","Trương Mạc","",},
        [1384] = {20184202,"Trương Mạc","Thì ra ngươi chính là Điển Vi! Ta là Trần Lưu Thái Thú Trương Mạc, cùng Tào Tháo khởi nghĩa diệt trừ Đổng Trác, ta đã nghe danh ngươi từ lâu, nay đến chiêu mộ ngươi, ý ngươi thế nào?","Thì ra ngươi chính là Điển Vi! Ta là Trần Lưu Thái Thú Trương Mạc, cùng Tào Tháo khởi nghĩa diệt trừ Đổng Trác, ta đã nghe danh ngươi từ lâu, nay đến chiêu mộ ngươi, ý ngươi thế nào?","Điển Vi","",},
        [1385] = {20184202,"Điển Vi","Lão tặc Đổng Trác gây loạn triều đình Điển Vi nguyện góp sức cho triều đình!","Lão tặc Đổng Trác gây loạn triều đình Điển Vi nguyện góp sức cho triều đình!","Trương Mạc","",},
        [1386] = {20184202,"Trương Mạc","Tốt!","Tốt!","Điển Vi","",},
        [1387] = {20184203,"Tào Tháo","Điển Vi trung thành, dũng võ! Ba quân đều khen ngợi ngươi.","Điển Vi trung thành, dũng võ! Ba quân đều khen ngợi ngươi.","Điển Vi","",},
        [1388] = {20184203,"Điển Vi","Ha ha, họ chỉ trêu đùa thôi.","Ha ha, họ chỉ trêu đùa thôi.","Tào Tháo","",},
        [1389] = {20184203,"Tào Tháo","Không cần phải khiêm tốn, ta biết ngươi thích uống rượu, người đâu, mau dâng rượu!","Không cần phải khiêm tốn, ta biết ngươi thích uống rượu, người đâu, mau dâng rượu!","Điển Vi","",},
        [1390] = {20184203,"Điển Vi","Đa tạ chúa công!","Đa tạ chúa công!","Tào Tháo","",},
        [1391] = {20184204,"Tào Tháo","Chuyện gì ở bên ngoài?","Chuyện gì ở bên ngoài?","Điển Vi","",},
        [1392] = {20184204,"Điển Vi","Chúa công, e là Trương Tú làm phản, ta đi ngăn chúng, ngài hãy mau rút lui!","Chúa công, e là Trương Tú làm phản, ta đi ngăn chúng, ngài hãy mau rút lui!","Tào Tháo","",},
        [1393] = {20184204,"Tào Tháo","Trương Tú tại sao đầu hàng rồi lại làm phản?","Trương Tú tại sao đầu hàng rồi lại làm phản?","Điển Vi","",},
        [1394] = {20184204,"Điển Vi","Đông người quá! Chúa công..., mau đi!","Đông người quá! Chúa công..., mau đi!","Tào Tháo","",},
        [1395] = {20184301,"Đổng Trác","Lý Thôi, ngươi xưa nay dũng mãnh, lại giỏi dụng binh, có tài hùng biện. Nay Tôn Kiên quá lợi hại, quân ta nhiều lần bại trận, ngươi có kế sách gì không?","Lý Thôi, ngươi xưa nay dũng mãnh, lại giỏi dụng binh, có tài hùng biện. Nay Tôn Kiên quá lợi hại, quân ta nhiều lần bại trận, ngươi có kế sách gì không?","Lý Thôi","",},
        [1396] = {20184301,"Lý Thôi","Thừa tướng, nghe nói Tôn Kiên có người con trai, tuổi trẻ tài cao, hay người gả con gái út cho con trai của Tôn Kiên. Như vậy, Tôn Kiên sẽ làm việc cho thừa tướng.","Thừa tướng, nghe nói Tôn Kiên có người con trai, tuổi trẻ tài cao, hay người gả con gái út cho con trai của Tôn Kiên. Như vậy, Tôn Kiên sẽ làm việc cho thừa tướng.","Đổng Trác","",},
        [1397] = {20184301,"Đổng Trác","Được, vậy ngươi hãy đi cầu thân, nếu việc thành, ta sẽ trọng thưởng cho ngươi.","Được, vậy ngươi hãy đi cầu thân, nếu việc thành, ta sẽ trọng thưởng cho ngươi.","Lý Thôi","",},
        [1398] = {20184301,"Lý Thôi","Thừa tướng hãy đợi tin tốt!","Thừa tướng hãy đợi tin tốt!","Đổng Trác","",},
        [1399] = {20184302,"Lý Thôi","Đổng thái sư đã mất, bọn ta chạy trốn về Tây Lương, không chừng có cơ hội sống sót.","Đổng thái sư đã mất, bọn ta chạy trốn về Tây Lương, không chừng có cơ hội sống sót.","Giả Hủ","",},
        [1400] = {20184302,"Giả Hủ","Mọi người đừng vội chay, ta có một kế, nếu thành sẽ làm việc cho triều đình, còn không thì mới chạy cũng chưa muộn.","Mọi người đừng vội chay, ta có một kế, nếu thành sẽ làm việc cho triều đình, còn không thì mới chạy cũng chưa muộn.","Lý Thôi","",},
        [1401] = {20184302,"Lý Thôi","Văn Hòa, ngươi có cách gì không?","Văn Hòa, ngươi có cách gì không?","Giả Hủ","",},
        [1402] = {20184302,"Giả Hủ","Các vị nếu tự ý bỏ trốn, chỉ cần một nhóm lính nhỏ cũng có thể bắt được, chi bằng tập trung binh lính tấn công Trường An, trả thù cho Đổng Trác.","Các vị nếu tự ý bỏ trốn, chỉ cần một nhóm lính nhỏ cũng có thể bắt được, chi bằng tập trung binh lính tấn công Trường An, trả thù cho Đổng Trác.","Lý Thôi","",},
        [1403] = {20184302,"Lý Thôi","Kế này được, Vương Doãn đang ở Trường An tiêu diệt thuộc hạ cũ của Đổng Trác. Chúng ta loan tin nói Vương Doãn muốn diệt hết người Tây Lương, như thế có thể tập trung binh lính tấn công về Trường An.","Kế này được, Vương Doãn đang ở Trường An tiêu diệt thuộc hạ cũ của Đổng Trác. Chúng ta loan tin nói Vương Doãn muốn diệt hết người Tây Lương, như thế có thể tập trung binh lính tấn công về Trường An.","Giả Hủ","",},
        [1404] = {20184303,"Hán Hiến Đế","Ngôi vua này của trẫm sao mà khổ thế. Lý Thôi, Quách Tị như hổ như sói, khiến cả Trường An tan hoang.","Ngôi vua này của trẫm sao mà khổ thế. Lý Thôi, Quách Tị như hổ như sói, khiến cả Trường An tan hoang.","Giả Hủ","",},
        [1405] = {20184303,"Giả Hủ","Bệ hạ, hay là đến Lạc Dương, rời khỏi Trường An thị phi.","Bệ hạ, hay là đến Lạc Dương, rời khỏi Trường An thị phi.","Hán Hiến Đế","",},
        [1406] = {20184303,"Hán Hiến Đế","E rằng Lý Thôi, Quách Tị sẽ không đồng ý.","E rằng Lý Thôi, Quách Tị sẽ không đồng ý.","Giả Hủ","",},
        [1407] = {20184303,"Giả Hủ","Bệ hạ đừng lo! Lý Thôi luôn nghe lời ta, ta có diệu kế này. Nghe nói thê tử của Quách Tị hay đố kỵ, có thể dùn kế phản gián, khiến Lý Thôi, Quách Tị tương tàn.","Bệ hạ đừng lo! Lý Thôi luôn nghe lời ta, ta có diệu kế này. Nghe nói thê tử của Quách Tị hay đố kỵ, có thể dùn kế phản gián, khiến Lý Thôi, Quách Tị tương tàn.","Hán Hiến Đế","",},
        [1408] = {20184304,"Quách Tị","Lý Thôi, ngươi mời ta dự tiệc, nhưng bỏ độc trong rượu, ngươi định hại ta sao?","Lý Thôi, ngươi mời ta dự tiệc, nhưng bỏ độc trong rượu, ngươi định hại ta sao?","Lý Thôi","",},
        [1409] = {20184304,"Quách Tị","Cũng may ta mạng lớn","Cũng may ta mạng lớn","Lý Thôi","",},
        [1410] = {20184304,"Lý Thôi","Ngươi dám dụ dỗ thê tử của ta! Hôm nay không lấy được mạng ngươi, xem như ngươi may mắn.","Ngươi dám dụ dỗ thê tử của ta! Hôm nay không lấy được mạng ngươi, xem như ngươi may mắn.","Quách Tị","",},
        [1411] = {20184304,"Quách Tị","Ngươi tin lời phù thủy, suốt ngày nói nhảm. Ta dụ dỗ thê tử của ngươi? Thê tử ngươi vừa xấu vừa ngốc, ai thèm chứ!","Ngươi tin lời phù thủy, suốt ngày nói nhảm. Ta dụ dỗ thê tử của ngươi? Thê tử ngươi vừa xấu vừa ngốc, ai thèm chứ!","Lý Thôi","",},
        [1412] = {20184304,"Lý Thôi","Ngươi dám sỉ nhục ta? Đợi ta tậ hợp binh mã sẽ lấy thủ cấp của ngươi!","Ngươi dám sỉ nhục ta? Đợi ta tậ hợp binh mã sẽ lấy thủ cấp của ngươi!","Quách Tị","",},
        [1413] = {20184305,"Quách Tị","Sao? Trước đây cứ chống đối với ta, giờ lại nghĩ đến ta sao?","Sao? Trước đây cứ chống đối với ta, giờ lại nghĩ đến ta sao?","Lý Thôi","",},
        [1414] = {20184305,"Lý Thôi","Quách huynh, trên đời làm gì có kẻ địch vĩnh viễn, chỉ có lợi ích là quan trọng. Giờ chúng ta không đồng lòng, e rằng sẽ gặp nguy đấy.","Quách huynh, trên đời làm gì có kẻ địch vĩnh viễn, chỉ có lợi ích là quan trọng. Giờ chúng ta không đồng lòng, e rằng sẽ gặp nguy đấy.","Quách Tị","",},
        [1415] = {20184305,"Lý Thôi","Dương Phụng, Đổng Thừa đã đưa Hiến Đế rời khỏi Trường An, đến chỗ Hoằng Nông. Nếu họ đến Sơn Đông, sau khi ổn định, chắc chắn sẽ kêu gọi chư hầu thảo phạt chúng ta, khi đó e rằng khó mà giữ mạng.","Dương Phụng, Đổng Thừa đã đưa Hiến Đế rời khỏi Trường An, đến chỗ Hoằng Nông. Nếu họ đến Sơn Đông, sau khi ổn định, chắc chắn sẽ kêu gọi chư hầu thảo phạt chúng ta, khi đó e rằng khó mà giữ mạng.","Quách Tị","",},
        [1416] = {20184305,"Quách Tị","Được, nay Trương Tế đóng quân ở Trường An, không thể xem thường. Hai ta thừa cơ liên kết đến Hoằng Nông diệt vua Hán, phân thiên hạ! Nhưng Lý Nhạc, Dương Phụng đông người, Lý huynh có diệu kế gì?","Được, nay Trương Tế đóng quân ở Trường An, không thể xem thường. Hai ta thừa cơ liên kết đến Hoằng Nông diệt vua Hán, phân thiên hạ! Nhưng Lý Nhạc, Dương Phụng đông người, Lý huynh có diệu kế gì?","Lý Thôi","",},
        [1417] = {20184305,"Lý Thôi","Ha ha! Kẻ già yếu cứ diệt, trai trẻ cường tráng thì sung quân, khi địch đến cho dân lên trước, sau lệnh quân lính bỏ lại đồ vật giữa đường để nhiễu loạn quân địch, khi đó chúng ta sẽ chiến thắng.","Ha ha! Kẻ già yếu cứ diệt, trai trẻ cường tráng thì sung quân, khi địch đến cho dân lên trước, sau lệnh quân lính bỏ lại đồ vật giữa đường để nhiễu loạn quân địch, khi đó chúng ta sẽ chiến thắng.","Quách Tị","",},
        [1418] = {20184305,"Quách Tị","Lý huynh dũng mãnh đa mưu, quả nhiên lợi hại!","Lý huynh dũng mãnh đa mưu, quả nhiên lợi hại!","Lý Thôi","",},
        [1419] = {20184401,"Tiểu Kiều","Tỉ tỉ, tỉ tặng muội con thỏ của tỉ được không?","Tỉ tỉ, tỉ tặng muội con thỏ của tỉ được không?","Đại Kiều","",},
        [1420] = {20184401,"Đại Kiều","Đây là do cha tặng cho ta, ta sao có thể tặng cho muội? Muội từ nhỏ đã thích tranh giành với ta, lần này ta sẽ không cho muội đâu.","Đây là do cha tặng cho ta, ta sao có thể tặng cho muội? Muội từ nhỏ đã thích tranh giành với ta, lần này ta sẽ không cho muội đâu.","Tiểu Kiều","",},
        [1421] = {20184401,"Tiểu Kiều","Tỉ không cho, muội sẽ bỏ mặc tỉ.","Tỉ không cho, muội sẽ bỏ mặc tỉ.","Đại Kiều","",},
        [1422] = {20184401,"Đại Kiều","Được thôi, vậy tỉ được yên tĩnh rồi...","Được thôi, vậy tỉ được yên tĩnh rồi...","Tiểu Kiều","",},
        [1423] = {20184401,"Tiểu Kiều","Tỉ không cho, muội sẽ khóc cho tỉ xem...","Tỉ không cho, muội sẽ khóc cho tỉ xem...","Đại Kiều","",},
        [1424] = {20184401,"Đại Kiều","Được rồi, tặng muội này, vừa khóc vừa cười, thật chẳng ra sao. Sau này lấy phu quân thì phải làm sao đây?","Được rồi, tặng muội này, vừa khóc vừa cười, thật chẳng ra sao. Sau này lấy phu quân thì phải làm sao đây?","Tiểu Kiều","",},
        [1425] = {20184401,"Tiểu Kiều","(Cười tươi) Đa tạ tỉ tỉ, Tỉ là thương muội nhất! Tiểu Kiều sau này sẽ bảo vệ tỉ tỉ!","(Cười tươi) Đa tạ tỉ tỉ, Tỉ là thương muội nhất! Tiểu Kiều sau này sẽ bảo vệ tỉ tỉ!","Đại Kiều","",},
        [1426] = {20184402,"Tiểu Kiều","Công Cẩn, từ khi tỉ phu đi khỏi, tỉ tỉ luôn cô đơn một mình, thật tội nghiệp.","Công Cẩn, từ khi tỉ phu đi khỏi, tỉ tỉ luôn cô đơn một mình, thật tội nghiệp.","Chu Du","",},
        [1427] = {20184402,"Chu Du","Ngươi hãy thường xuyên cùng cô ấy trò chuyện, cũng may cô ấy vẫn còn một hảo muội muội.","Ngươi hãy thường xuyên cùng cô ấy trò chuyện, cũng may cô ấy vẫn còn một hảo muội muội.","Tiểu Kiều","",},
