        [1430] = {20184403,"Gia Cát Lượng","Lập song đài hai bên trái phải, có ngọc long và kim phụng. Nhị kiều ở đông nam, vui vẻ ngày đêm.","Lập song đài hai bên trái phải, có ngọc long và kim phụng. Nhị kiều ở đông nam, vui vẻ ngày đêm.","Tiểu Kiều","",},
        [1431] = {20184403,"Tiểu Kiều","Gia Cát tiên sinh đang ngâm Đồng Tước Đài Phú của Tào Thực.","Gia Cát tiên sinh đang ngâm Đồng Tước Đài Phú của Tào Thực.","Gia Cát Lượng","",},
        [1432] = {20184403,"Gia Cát Lượng","Đúng vậy, không ngờ Tiểu Kiều phu nhân cũn biết bài phú này.","Đúng vậy, không ngờ Tiểu Kiều phu nhân cũn biết bài phú này.","Tiểu Kiều","",},
        [1433] = {20184403,"Tiểu Kiều","Nếu Tiểu Kiều nhớ không lầm, nguyên văn phải là Nhị kiều ở đông tây, nhược trường không chi đế đông, tiên sinh làm vậy là muốn khích Chu Lang?","Nếu Tiểu Kiều nhớ không lầm, nguyên văn phải là Nhị kiều ở đông tây, nhược trường không chi đế đông, tiên sinh làm vậy là muốn khích Chu Lang?","Gia Cát Lượng","",},
        [1434] = {20184403,"Gia Cát Lượng","Không ngờ Tiểu Kiều phu nhân lại thông minh như vậy, Khổng Minh bái phục.","Không ngờ Tiểu Kiều phu nhân lại thông minh như vậy, Khổng Minh bái phục.","Tiểu Kiều","",},
        [1435] = {20184403,"Tiểu Kiều","Đại quân Tào Tháo nam hạ, vùng đất Kinh Châu, Giang Đông vốn có quan hệ mật thiết, nếu không liên kết, sớm muộn gì cũng sẽ bị Tào Tháo nuốt chửng.","Đại quân Tào Tháo nam hạ, vùng đất Kinh Châu, Giang Đông vốn có quan hệ mật thiết, nếu không liên kết, sớm muộn gì cũng sẽ bị Tào Tháo nuốt chửng.","Gia Cát Lượng","",},
        [1436] = {20184403,"Tiểu Kiều","Tiểu Kiều tuy là phận nữ nhi, cũng biết đạo lý tình thế nguy cấp, phải cùng kháng Tào Tháo. Gia Cát tiên sinh, Tiểu Kiều sẽ khuyên Chu Lang trợ giúp cho người.","Tiểu Kiều tuy là phận nữ nhi, cũng biết đạo lý tình thế nguy cấp, phải cùng kháng Tào Tháo. Gia Cát tiên sinh, Tiểu Kiều sẽ khuyên Chu Lang trợ giúp cho người.","Gia Cát Lượng","",},
        [1437] = {20184404,"Tiểu Kiều","Công Cẩn sao lại buồn bã thế này?","Công Cẩn sao lại buồn bã thế này?","Chu Du","",},
