        [1439] = {20184404,"Tiểu Kiều","Công Cẩn, hay là nghỉ ngơi chút, để Tiểu Kiều múa một khúc cho ngài xem.","Công Cẩn, hay là nghỉ ngơi chút, để Tiểu Kiều múa một khúc cho ngài xem.","Chu Du","",},
        [1440] = {20184404,"Chu Du","Tiểu Kiều ngày thường múa rất thướt tha, sao hôm nay lại múa bài Đại Phong Ca?","Tiểu Kiều ngày thường múa rất thướt tha, sao hôm nay lại múa bài Đại Phong Ca?","Tiểu Kiều","",},
        [1441] = {20184404,"Tiểu Kiều","Đại Phong Ca do Hán Cao Tổ mà ra, năm đó Lưu Bang và Hạng Vũ quyết chiến nhiều lần bị đánh bại ở phía bắc, sau khi liên kết các thế lực khác đã phản bại thành thắng, lập nên nhà Hán.","Đại Phong Ca do Hán Cao Tổ mà ra, năm đó Lưu Bang và Hạng Vũ quyết chiến nhiều lần bị đánh bại ở phía bắc, sau khi liên kết các thế lực khác đã phản bại thành thắng, lập nên nhà Hán.","Chu Du","",},
        [1442] = {20184404,"Chu Du","Ngươi muốn khuyên ta liên kết Lưu Bị kháng Thào. Ngươi yên tâm, ta đã sớm báo với chúa công, cùng Khổng Minh bàn định kế sách chống Tào Tháo.","Ngươi muốn khuyên ta liên kết Lưu Bị kháng Thào. Ngươi yên tâm, ta đã sớm báo với chúa công, cùng Khổng Minh bàn định kế sách chống Tào Tháo.","Tiểu Kiều","",},
