        [1457] = {20184503,"Vương Lãng","Ta đảm nhiệm chức vụ Ngự Sử, Tư Không, Tư Đồ, viết sách Chu Dịch, Xuân Thu, Hiếu Kinh, tổng 34 quyển.","Ta đảm nhiệm chức vụ Ngự Sử, Tư Không, Tư Đồ, viết sách Chu Dịch, Xuân Thu, Hiếu Kinh, tổng 34 quyển.","","",},
        [1458] = {20184503,"Vương Lãng","Cháu gái ta Vương Nguyên Cơ gả cho con thứ Tư Mã Ý là Tư Mã Chiêu, sinh ra Tây Tấn Võ Đế Tư Mã Viêm.","Cháu gái ta Vương Nguyên Cơ gả cho con thứ Tư Mã Ý là Tư Mã Chiêu, sinh ra Tây Tấn Võ Đế Tư Mã Viêm.","","",},
