        [1467] = {20184603,"Trương Nhượng","Lúc đầu lập mưu hại Đại Tướng Quân chỉ có một mình Tái Thạc, không liên quan đến chúng thần.","Lúc đầu lập mưu hại Đại Tướng Quân chỉ có một mình Tái Thạc, không liên quan đến chúng thần.","Hà Thái Hậu","",},
        [1468] = {20184603,"Trương Nhượng","Đại Tướng Quân nghe lời Viên Thiệu, muốn diệt chúng thần, xin nương nương cứu giúp.","Đại Tướng Quân nghe lời Viên Thiệu, muốn diệt chúng thần, xin nương nương cứu giúp.","Hà Thái Hậu","",},
