        [1618] = {20185604,"Tào Nhân","Tiếc là ta không thể đích thân tiến đánh Mạch Thành để bắt sống Quan Vũ!","Tiếc là ta không thể đích thân tiến đánh Mạch Thành để bắt sống Quan Vũ!","","",},
        [1619] = {20185604,"Tào Nhân","Ngược lại đã để cho tên Lữ Mông kia chớp được thời cơ, thật là đáng tiếc.","Ngược lại đã để cho tên Lữ Mông kia chớp được thời cơ, thật là đáng tiếc.","","",},
        [1620] = {20185701,"Ngô Quốc Thái","Trai lớn lấy vợ, gái lớn gả chồng là chuyện bình thường xưa nay. Phàm là con cái phải nghe theo lời mẫu thân.","Trai lớn lấy vợ, gái lớn gả chồng là chuyện bình thường xưa nay. Phàm là con cái phải nghe theo lời mẫu thân.","Tôn Quyền","",},
        [1621] = {20185701,"Ngô Quốc Thái","Ngươi muốn Lưu Huyền Đức làm rể ta, sao còn dám giấu ta? Đó là con gái ta mà!","Ngươi muốn Lưu Huyền Đức làm rể ta, sao còn dám giấu ta? Đó là con gái ta mà!","Tôn Quyền","",},
        [1622] = {20185701,"Tôn Quyền","Sao người lại biết được tin đó ạ?","Sao người lại biết được tin đó ạ?","Ngô Quốc Thái","",},
        [1623] = {20185701,"Ngô Quốc Thái","Nếu không muốn người khác biết thì tốt nhất đừng làm. Giờ tất cả mọi người đều biết hết nhưng ngươi lại giấu ta!","Nếu không muốn người khác biết thì tốt nhất đừng làm. Giờ tất cả mọi người đều biết hết nhưng ngươi lại giấu ta!","Tôn Quyền","",},
        [1624] = {20185701,"Tôn Quyền","Không phải vậy đâu. Đây là kế của Chu Du, vì muốn lấy Kinh Châu nên mới sắp đặt chuyện này. Chỉ cần dụ và bắt Lưu Bị thì nhất định Lưu Bị sẽ dùng Kinh Châu để đổi, lúc đó Kinh Châu sẽ thuộc về ta.","Không phải vậy đâu. Đây là kế của Chu Du, vì muốn lấy Kinh Châu nên mới sắp đặt chuyện này. Chỉ cần dụ và bắt Lưu Bị thì nhất định Lưu Bị sẽ dùng Kinh Châu để đổi, lúc đó Kinh Châu sẽ thuộc về ta.","Ngô Quốc Thái","",},
        [1625] = {20185701,"Tôn Quyền","Nếu Lưu Bị không đồng ý dùng Kinh Châu để đổi thì sẽ trảm y. Như thế kế này cũng chỉ là hữu danh vô thực thôi.","Nếu Lưu Bị không đồng ý dùng Kinh Châu để đổi thì sẽ trảm y. Như thế kế này cũng chỉ là hữu danh vô thực thôi.","Ngô Quốc Thái","",},
        [1626] = {20185701,"Ngô Quốc Thái","Ngươi là đô đốc của sáu quận và tám mươi châu nhưng giờ lại muốn dùng con gái ta để lấy Kinh Châu sao!","Ngươi là đô đốc của sáu quận và tám mươi châu nhưng giờ lại muốn dùng con gái ta để lấy Kinh Châu sao!","Tôn Quyền","",},
        [1627] = {20185701,"Ngô Quốc Thái","Nếu diệt Lưu Bị, con gái ta lại trở thành góa phụ, sau này còn gả cho ai khác được nữa? Như vậy chẳng phải là lỡ duyên con ta sao!","Nếu diệt Lưu Bị, con gái ta lại trở thành góa phụ, sau này còn gả cho ai khác được nữa? Như vậy chẳng phải là lỡ duyên con ta sao!","Tôn Quyền","",},
        [1628] = {20185701,"Ngô Quốc Thái","Nếu dùng kế này mà có được Kinh Châu, cũng sẽ bị thiên hạ cười chê. Chuyện này phải làm sao đây!","Nếu dùng kế này mà có được Kinh Châu, cũng sẽ bị thiên hạ cười chê. Chuyện này phải làm sao đây!","Tôn Quyền","",},
        [1629] = {20185701,"Ngô Quốc Thái","Ngày mai hẹn gặp ở Cam Lộ Tự: Nếu như không hợp ý ta thì các ngươi muốn làm gì cũng được, ngược lại thì ta sẽ tự động gả con gái ta cho y!","Ngày mai hẹn gặp ở Cam Lộ Tự: Nếu như không hợp ý ta thì các ngươi muốn làm gì cũng được, ngược lại thì ta sẽ tự động gả con gái ta cho y!","Tôn Quyền","",},
        [1630] = {20185702,"Tôn Quyền","Mẫu thân ta muốn gả muội muội ta cho Lưu Bị. Không ngờ biến giả thành thật. Chuyện này làm sao đây?","Mẫu thân ta muốn gả muội muội ta cho Lưu Bị. Không ngờ biến giả thành thật. Chuyện này làm sao đây?","Chu Du","",},
        [1631] = {20185702,"Chu Du","Nếu người đã quyết như vậy thì Du tôi cũng không dám phản đối.","Nếu người đã quyết như vậy thì Du tôi cũng không dám phản đối.","Tôn Quyền","",},
        [1632] = {20185702,"Chu Du","Lưu Bị là bậc kỳ tài, lại có  Quan Vũ, Trương Phi, Triệu Tử Long và Gia Cát Lượng đi theo phò tá. Y chắc chắn không phải là kẻ dễ khuất phục.","Lưu Bị là bậc kỳ tài, lại có  Quan Vũ, Trương Phi, Triệu Tử Long và Gia Cát Lượng đi theo phò tá. Y chắc chắn không phải là kẻ dễ khuất phục.","Tôn Quyền","",},
        [1633] = {20185702,"Chu Du","Ta thấy chi bằng bắt y trước, sau đó giam lỏng ở trong cung. Ngày ngày cho y uống rượu và vui chơi để làm nhụt chí y.","Ta thấy chi bằng bắt y trước, sau đó giam lỏng ở trong cung. Ngày ngày cho y uống rượu và vui chơi để làm nhụt chí y.","Tôn Quyền","",},
        [1634] = {20185702,"Chu Du","Cũng có thể dùng kế ly gián y với Quan Vũ, Trương Phi và Gia Cát Lượng, sau đó cho binh tiến đánh từng tên một, như vậy đại sự ắt thành.","Cũng có thể dùng kế ly gián y với Quan Vũ, Trương Phi và Gia Cát Lượng, sau đó cho binh tiến đánh từng tên một, như vậy đại sự ắt thành.","Tôn Quyền","",},
        [1635] = {20185702,"Chu Du","Nếu giờ chúng ta cứ bỏ mặc chúng không màng, e là sau này khó mà đối phó. Mong minh công suy xét.","Nếu giờ chúng ta cứ bỏ mặc chúng không màng, e là sau này khó mà đối phó. Mong minh công suy xét.","Tôn Quyền","",},
        [1636] = {20185702,"Tôn Quyền","Kế của Công Cẩn rất hợp ý ta. Lưu Bị tuy là dòng dõi Hán Thất nhưng xưa nay chưa từng hưởng vinh quang phú quý gì.","Kế của Công Cẩn rất hợp ý ta. Lưu Bị tuy là dòng dõi Hán Thất nhưng xưa nay chưa từng hưởng vinh quang phú quý gì.","Chu Du","",},
        [1637] = {20185702,"Tôn Quyền","Nếu bây giờ cho y hưởng vinh quang phú quý thì chắc chắn sẽ có thể ly gián y với Khổng Minh, Quan Vũ và Trương Phi.","Nếu bây giờ cho y hưởng vinh quang phú quý thì chắc chắn sẽ có thể ly gián y với Khổng Minh, Quan Vũ và Trương Phi.","Chu Du","",},
        [1638] = {20185702,"Tôn Quyền","Khiến họ ganh ghét nhau, sau đó chúng ta sẽ cho quân đoạt lấy Kinh Châu.","Khiến họ ganh ghét nhau, sau đó chúng ta sẽ cho quân đoạt lấy Kinh Châu.","Chu Du","",},
        [1639] = {20185703,"Thượng Hương","Nam tử hán đại trượng phu có gì mà buồn phiền vậy?","Nam tử hán đại trượng phu có gì mà buồn phiền vậy?","Lưu Bị","",},
        [1640] = {20185703,"Lưu Bị","Ta tha hương nơi xứ sở quê người, không thể chăm sóc song thân, bái tế tổ tiên, quả thật là đại nghịch bất hiếu.","Ta tha hương nơi xứ sở quê người, không thể chăm sóc song thân, bái tế tổ tiên, quả thật là đại nghịch bất hiếu.","Thượng Hương","",},
        [1641] = {20185703,"Lưu Bị","Năm mới lại gần kề, ta càng buồn phiền hơn.","Năm mới lại gần kề, ta càng buồn phiền hơn.","Thượng Hương","",},
        [1642] = {20185703,"Thượng Hương","Người đừng giấu ta! Lúc nãy nghe Triệu Tử Long nói là Kinh Châu lâm nguy nên người muốn quay về chứ gì.","Người đừng giấu ta! Lúc nãy nghe Triệu Tử Long nói là Kinh Châu lâm nguy nên người muốn quay về chứ gì.","Lưu Bị","",},
        [1643] = {20185703,"Lưu Bị","Nếu phu nhân đã biết thì ta cũng không giấu gì. Nếu ta không đi, lỡ như Kinh Châu có gì thì ta sẽ bị thiên hạ cười chê.","Nếu phu nhân đã biết thì ta cũng không giấu gì. Nếu ta không đi, lỡ như Kinh Châu có gì thì ta sẽ bị thiên hạ cười chê.","Thượng Hương","",},
        [1644] = {20185703,"Lưu Bị","Ta muốn đi nhưng lại không nỡ để phu nhân ở lại đây nên mới buồn phiền đến thế.","Ta muốn đi nhưng lại không nỡ để phu nhân ở lại đây nên mới buồn phiền đến thế.","Thượng Hương","",},
        [1645] = {20185703,"Thượng Hương","Thiếp đã gả cho người, người đi đâu thiếp theo đấy.","Thiếp đã gả cho người, người đi đâu thiếp theo đấy.","Lưu Bị","",},
        [1646] = {20185703,"Lưu Bị","Tâm ý phu nhân tuy là như vậy, nhưng Quốc Thái và Ngô Hầu an sao chịu để phu nhân đi? Nếu phu nhân thương xót Lưu Bị thì tạm thời từ biệt đi.","Tâm ý phu nhân tuy là như vậy, nhưng Quốc Thái và Ngô Hầu an sao chịu để phu nhân đi? Nếu phu nhân thương xót Lưu Bị thì tạm thời từ biệt đi.","Thượng Hương","",},
        [1647] = {20185703,"Thượng Hương","Người đừng buồn nữa. Thiếp sẽ đến nói với mẫu thân, mẫu thân nhất định sẽ cho chúng ta về Kinh Châu.","Người đừng buồn nữa. Thiếp sẽ đến nói với mẫu thân, mẫu thân nhất định sẽ cho chúng ta về Kinh Châu.","Lưu Bị","",},
        [1648] = {20185703,"Lưu Bị","Quốc Thái đồng ý nhưng quần thần sẽ ngăn cản thôi.","Quốc Thái đồng ý nhưng quần thần sẽ ngăn cản thôi.","Thượng Hương","",},
        [1649] = {20185703,"Thượng Hương","Hay là chúng ta mượn cớ đi bái tế tổ tiên sau đó âm thầm trốn đi, người thấy sao?","Hay là chúng ta mượn cớ đi bái tế tổ tiên sau đó âm thầm trốn đi, người thấy sao?","Lưu Bị","",},
        [1650] = {20185703,"Lưu Bị","Vậy phải hết sức cẩn thận, đừng cho ai biết.","Vậy phải hết sức cẩn thận, đừng cho ai biết.","Thượng Hương","",},
        [1651] = {20185704,"Triệu Vân","Sao tẩu phu nhân lại vội vàng trở về Đông Ngô vậy?","Sao tẩu phu nhân lại vội vàng trở về Đông Ngô vậy?","Thượng Hương","",},
        [1652] = {20185704,"Thượng Hương","Vừa có thư nhà gửi đến nói là mẫu thân bệnh nặng nên ta phải về thăm ngay.","Vừa có thư nhà gửi đến nói là mẫu thân bệnh nặng nên ta phải về thăm ngay.","Triệu Vân","",},
        [1653] = {20185704,"Triệu Vân","Về nhà thăm mẹ, sao tẩu phu nhân lại dẫn theo A Đẩu?","Về nhà thăm mẹ, sao tẩu phu nhân lại dẫn theo A Đẩu?","Thượng Hương","",},
        [1654] = {20185704,"Trương Phi","Mong tẩu phu nhân đừng làm khó chúng tôi. Người hãy về thăm mẫu thân một mình và để A Đẩu lại đây.","Mong tẩu phu nhân đừng làm khó chúng tôi. Người hãy về thăm mẫu thân một mình và để A Đẩu lại đây.","Thượng Hương","",},
        [1655] = {20185801,"Lưu Hồng","Trương Giác đang dấy binh khởi nghĩa ở Ký Châu, hiện đã có tám châu ủng hộ, triều đình và bá tánh đang vô cùng hoang mang.","Trương Giác đang dấy binh khởi nghĩa ở Ký Châu, hiện đã có tám châu ủng hộ, triều đình và bá tánh đang vô cùng hoang mang.","Lư Thực","",},
        [1656] = {20185801,"Lưu Hồng","Nghe nói Tử Can là người văn võ song toàn nên lệnh cho Tử Can làm Trung Lang Tướng.","Nghe nói Tử Can là người văn võ song toàn nên lệnh cho Tử Can làm Trung Lang Tướng.","Lư Thực","",},
        [1657] = {20185801,"Lưu Hồng","Thống lĩnh Bắc Quân Ngũ Hiệu Tướng Sĩ đến Ký Châu dẹp Giặc Hoàng Cân.","Thống lĩnh Bắc Quân Ngũ Hiệu Tướng Sĩ đến Ký Châu dẹp Giặc Hoàng Cân.","Lư Thực","",},
        [1658] = {20185801,"Lư Thực","Tử Can tuân lệnh!","Tử Can tuân lệnh!","Lưu Hồng","",},
        [1659] = {20185802,"Hà Tiến","Đổng Trác dâng sớ nói sở dĩ thiên hạ đại loạn là do bọn người Trương Lương nhũng nhiễu.","Đổng Trác dâng sớ nói sở dĩ thiên hạ đại loạn là do bọn người Trương Lương nhũng nhiễu.","Lư Thực","",},
        [1660] = {20185802,"Hà Tiến","Đổng Trác muốn dẫn binh vào Lạc Dương diệt Trương Nhượng trả lại sự yên bình cho bá tánh!","Đổng Trác muốn dẫn binh vào Lạc Dương diệt Trương Nhượng trả lại sự yên bình cho bá tánh!","Lư Thực","",},
        [1661] = {20185802,"Lư Thực","Đổng Trác là người lòng dạ nham hiểm. Một khi để y đạt được ý đồ thì đại họa khó tránh.","Đổng Trác là người lòng dạ nham hiểm. Một khi để y đạt được ý đồ thì đại họa khó tránh.","Hà Tiến","",},
        [1662] = {20185802,"Lư Thực","Chi bằng hãy ngăn cản Đổng Trác để tránh đại họa.","Chi bằng hãy ngăn cản Đổng Trác để tránh đại họa.","Hà Tiến","",},
        [1663] = {20185802,"Hà Tiến","Ngươi tính tình đa nghi nên sẽ không thể mưu cầu đại sự.","Ngươi tính tình đa nghi nên sẽ không thể mưu cầu đại sự.","Lư Thực","",},
        [1664] = {20185901,"Tôn Sách","Sơn tặc tấn công Tuyên Thành, Chu Thái xả thân cứu Tôn Quyền.","Sơn tặc tấn công Tuyên Thành, Chu Thái xả thân cứu Tôn Quyền.","Hoa Đà","",},
        [1665] = {20185901,"Tôn Sách","Trên đường chạy thoát, Chu Thái trọng thương, khó bảo toàn tính mạng.","Trên đường chạy thoát, Chu Thái trọng thương, khó bảo toàn tính mạng.","Hoa Đà","",},
        [1666] = {20185901,"Tôn Sách","Cầu xin Nguyên Hóa cứu giúp.","Cầu xin Nguyên Hóa cứu giúp.","Hoa Đà","",},
        [1667] = {20185901,"Hoa Đà","Cứu người là thiên chức của các thầy thuốc, xin phép cho ta xem bệnh.","Cứu người là thiên chức của các thầy thuốc, xin phép cho ta xem bệnh.","Tôn Sách","",},
        [1668] = {20185901,"Hoa Đà","Bệnh tình cũng không phức tạp đâu, sẽ cứu được thôi.","Bệnh tình cũng không phức tạp đâu, sẽ cứu được thôi.","Tôn Sách","",},
        [1669] = {20185902,"Tôn Quyền","Chu Thái anh dũng đánh lui quân Tào Tháo, ta phong cho làm Bình Lỗ Tướng.","Chu Thái anh dũng đánh lui quân Tào Tháo, ta phong cho làm Bình Lỗ Tướng.","Chu Thái","",},
        [1670] = {20185902,"Tôn Quyền","Trọng Mưu mời người một chén rượu.","Trọng Mưu mời người một chén rượu.","Chu Thái","",},
        [1671] = {20185902,"Chu Thái","Đa tạ Trọng Mưu.","Đa tạ Trọng Mưu.","Tôn Quyền","",},
        [1672] = {20185902,"Tôn Quyền","Uống xong chén rượu này, xin người hãy cởi bỏ xiêm y.","Uống xong chén rượu này, xin người hãy cởi bỏ xiêm y.","Chu Thái","",},
        [1673] = {20185902,"Chu Thái","Cởi bỏ xiêm y sao?","Cởi bỏ xiêm y sao?","Tôn Quyền","",},
        [1674] = {20185902,"Tôn Quyền","Nghe nói trong quân có người không phục Ấu Bình, hôm nay ta sẽ cho mọi người biết.","Nghe nói trong quân có người không phục Ấu Bình, hôm nay ta sẽ cho mọi người biết.","Chu Thái","",},
        [1675] = {20185902,"Tôn Quyền","Các vết thương trên người Ấu Bình có lai lịch như thế nào!","Các vết thương trên người Ấu Bình có lai lịch như thế nào!","Chu Thái","",},
        [1676] = {20185902,"Tôn Quyền","Làm sao mà ta không xem y như là cốt nhục của ta và ủy thác việc quân cho y chứ!","Làm sao mà ta không xem y như là cốt nhục của ta và ủy thác việc quân cho y chứ!","Chu Thái","",},
        [1677] = {20186001,"Tào Tháo","Hôm nay quan khách tề tựu nơi đây, sơn hào hải vị có đủ.","Hôm nay quan khách tề tựu nơi đây, sơn hào hải vị có đủ.","Tả Từ","",},
        [1678] = {20186001,"Tào Tháo","Chỉ thiếu món cá sạo ở Tùng Giang bên Ngô Quốc thôi.","Chỉ thiếu món cá sạo ở Tùng Giang bên Ngô Quốc thôi.","Tả Từ","",},
        [1679] = {20186001,"Tả Từ","Chuyện này rất dễ.","Chuyện này rất dễ.","Tào Tháo","",},
        [1680] = {20186001,"Tả Từ","Hãy cho ta một chiếc chậu đồng, đổ đầy nước vào đó. Ta chỉ cần cho mồi vào cần câu và để vào trong chậu là được. (Một lúc sau liền câu được một con cá sạo.)","Hãy cho ta một chiếc chậu đồng, đổ đầy nước vào đó. Ta chỉ cần cho mồi vào cần câu và để vào trong chậu là được. (Một lúc sau liền câu được một con cá sạo.)","Tào Tháo","",},
        [1681] = {20186001,"Tào Tháo","Một con sao đủ cho các quan khách ăn, hai con mới đủ.","Một con sao đủ cho các quan khách ăn, hai con mới đủ.","Tả Từ","",},
        [1682] = {20186001,"Tả Từ","Chuyện này cũng có khó gì đâu. (Lại cho cần câu vào chậu, một lúc sau lại câu được một con nữa.)","Chuyện này cũng có khó gì đâu. (Lại cho cần câu vào chậu, một lúc sau lại câu được một con nữa.)","Tào Tháo","",},
        [1683] = {20186001,"Tào Tháo","Nguyên Phóng tiên sinh đạo thuật quả nhiên tinh thông.","Nguyên Phóng tiên sinh đạo thuật quả nhiên tinh thông.","Tả Từ","",},
        [1684] = {20186002,"Tả Từ","Ta có ít quà muốn khao đội quân của người.","Ta có ít quà muốn khao đội quân của người.","Lưu Biểu","",},
        [1685] = {20186002,"Lưu Biểu","Quân ta một vạn, quan khách một ngàn, có mình ngươi liệu có khao nổi không?","Quân ta một vạn, quan khách một ngàn, có mình ngươi liệu có khao nổi không?","Tả Từ","",},
        [1686] = {20186002,"Tả Từ","Ta có một đấu rượu và một dây thịt khô.","Ta có một đấu rượu và một dây thịt khô.","Lưu Biểu","",},
        [1687] = {20186002,"Lưu Biểu","Một đấu rượu và một dây thịt khô sao đủ dùng?","Một đấu rượu và một dây thịt khô sao đủ dùng?","Tả Từ","",},
        [1688] = {20186002,"Tả Từ","Mỗi quân binh sẽ uống 3 chén rượu và ăn một miếng thịt khô. (Cứ theo đầu người mà chia.)","Mỗi quân binh sẽ uống 3 chén rượu và ăn một miếng thịt khô. (Cứ theo đầu người mà chia.)","Lưu Biểu","",},
        [1689] = {20186002,"Lưu Biểu","Ngươi làm sao mà rượu trong vò không vơi đi chút nào, thịt cũng ăn không hết vậy?","Ngươi làm sao mà rượu trong vò không vơi đi chút nào, thịt cũng ăn không hết vậy?","Tả Từ","",},
