        [1691] = {20186003,"Tôn Sách","Tả Từ lại giở yêu thuật rồi!","Tả Từ lại giở yêu thuật rồi!","Tả Từ","",},
        [1692] = {20186003,"Tôn Sách","Ta có thể trảm Vu Cát thì cũng có thể trảm ngươi!","Ta có thể trảm Vu Cát thì cũng có thể trảm ngươi!","Tả Từ","",},
        [1693] = {20186003,"Tả Từ","Ha ha ha. Ta sẽ đi chầm chậm thôi.","Ha ha ha. Ta sẽ đi chầm chậm thôi.","Tôn Sách","",},
        [1694] = {20186003,"Tả Từ","Ngươi có thể đuổi kịp ta thì cứ trảm ta đi.","Ngươi có thể đuổi kịp ta thì cứ trảm ta đi.","Tôn Sách","",},
        [1695] = {20186003,"Tôn Sách","Ngươi đã dùng yêu đạo gì mà ta không thể đuổi kịp ngươi thế kia!","Ngươi đã dùng yêu đạo gì mà ta không thể đuổi kịp ngươi thế kia!","Tả Từ","",},
        [1696] = {20186101,"Viên Thiệu","Đã đến lúc chọn người kế thừa, Viên Đàm hiếu sát, Viên Hi nhu nhược, Viên Thượng tuấn tú hơn, phải làm sao đây?","Đã đến lúc chọn người kế thừa, Viên Đàm hiếu sát, Viên Hi nhu nhược, Viên Thượng tuấn tú hơn, phải làm sao đây?","Thẩm Phối","",},
        [1697] = {20186101,"Thẩm Phối","Tướng quân sao không để Viên Đàm làm con thừa tự của vong huynh? Sẽ không ai nói tướng quân phế con trưởng lập con út.","Tướng quân sao không để Viên Đàm làm con thừa tự của vong huynh? Sẽ không ai nói tướng quân phế con trưởng lập con út.","Viên Thiệu","",},
        [1698] = {20186101,"Viên Thiệu","Nàng nói rất phải. Vậy thì phong Viên Đàm làm Đô Đốc, để hắn đi canh giữ Thanh Châu.","Nàng nói rất phải. Vậy thì phong Viên Đàm làm Đô Đốc, để hắn đi canh giữ Thanh Châu.","Thẩm Phối","",},
        [1699] = {20186102,"Viên Đàm","Ta là con cả của phụ thân, con thừa tự dòng chính, các ngươi đã lén nói gì với phụ thân?","Ta là con cả của phụ thân, con thừa tự dòng chính, các ngươi đã lén nói gì với phụ thân?","Thẩm Phối","",},
        [1700] = {20186102,"Thẩm Phối","Tướng quân trước khi đi đã chính miệng nói với ta, muốn Viên Thượng kế thừa đại nghiệp.","Tướng quân trước khi đi đã chính miệng nói với ta, muốn Viên Thượng kế thừa đại nghiệp.","Viên Đàm","",},
        [1701] = {20186102,"Viên Đàm","Hừ! Đừng hòng lừa ta, rõ ràng là ngươi chia rẽ quan hệ cha con!","Hừ! Đừng hòng lừa ta, rõ ràng là ngươi chia rẽ quan hệ cha con!","Thẩm Phối","",},
        [1702] = {20186102,"Thẩm Phối","Ngươi không được tướng quân thích, trách ta gì chứ?","Ngươi không được tướng quân thích, trách ta gì chứ?","Viên Đàm","",},
        [1703] = {20186102,"Viên Đàm","Lẽ nào lại như vậy!","Lẽ nào lại như vậy!","Thẩm Phối","",},
        [1704] = {20186103,"Viên Đàm","Giáp phe ta không tốt, nên lần trước bị Tào Tháo đánh bại, hiện Tào quân đã lui, không còn lòng dạ chiến đấu.","Giáp phe ta không tốt, nên lần trước bị Tào Tháo đánh bại, hiện Tào quân đã lui, không còn lòng dạ chiến đấu.","Viên Thượng","",},
        [1705] = {20186103,"Viên Đàm","Chúng ta nên tấn công bất ngờ, ta cần tăng Giáp và Lính.","Chúng ta nên tấn công bất ngờ, ta cần tăng Giáp và Lính.","Viên Thượng","",},
        [1706] = {20186103,"Viên Thượng","Không được, ta không có, không cho được.","Không được, ta không có, không cho được.","Viên Đàm","",},
        [1707] = {20186103,"Viên Đàm","Ngươi đừng quá đáng! Có tin là ta liên minh với Tào Tháo đánh ngươi không!","Ngươi đừng quá đáng! Có tin là ta liên minh với Tào Tháo đánh ngươi không!","Viên Thượng","",},
        [1708] = {20186201,"Tào Thực","Phiên nhược kinh hồng, uyển nhược du long, vinh diệu thu cúc, hoa mậu xuân tùng.","Phiên nhược kinh hồng, uyển nhược du long, vinh diệu thu cúc, hoa mậu xuân tùng.","Trương Nhượng","",},
        [1709] = {20186201,"Tào Thực","Phảng phất hề nhương khinh vân chi tế nguyệt, phiêu diêu hề nhược lưu phong chi hồi tuyết.","Phảng phất hề nhương khinh vân chi tế nguyệt, phiêu diêu hề nhược lưu phong chi hồi tuyết.","Tào Tháo","",},
        [1710] = {20186201,"Chân Cơ","A, đang nói ta đó sao?","A, đang nói ta đó sao?","Trương Nhượng","",},
        [1711] = {20186202,"Tào Phi","Đừng lo, ta là con cả Tào thừa tướng, xin bảo vệ gia đình nàng.","Đừng lo, ta là con cả Tào thừa tướng, xin bảo vệ gia đình nàng.","Chân Cơ","",},
        [1712] = {20186202,"Chân Cơ","Thiếp Chân thị, xin được nâng khăn sửa túi, hầu hạ thế tử.","Thiếp Chân thị, xin được nâng khăn sửa túi, hầu hạ thế tử.","Tào Phi","",},
        [1713] = {20186203,"Chân Cơ","Chúng khẩu thước hoàng kim, sử quân sinh biệt ly. Niệm quân khứ ngã thời, độc sầu thường khổ bi.","Chúng khẩu thước hoàng kim, sử quân sinh biệt ly. Niệm quân khứ ngã thời, độc sầu thường khổ bi.","","",},
        [1714] = {20186203,"Chân Cơ","Yêu nhau không hiểu nhau. Nếu có kiếp sau, ta không muốn gặp lại chàng nữa.","Yêu nhau không hiểu nhau. Nếu có kiếp sau, ta không muốn gặp lại chàng nữa.","","",},
        [1715] = {20186301,"Gia Cát Cẩn","Lần này tới, một là chúc mừng Huyền Đức chiếm được đất Thục.","Lần này tới, một là chúc mừng Huyền Đức chiếm được đất Thục.","Gia Cát Lượng","",},
        [1716] = {20186301,"Gia Cát Lượng","Khách sáo, khách sáo","Khách sáo, khách sáo","Gia Cát Cẩn","",},
        [1717] = {20186301,"Gia Cát Cẩn","Hai là muốn hỏi, khi nào thì trả Kinh Châu?","Hai là muốn hỏi, khi nào thì trả Kinh Châu?","Gia Cát Lượng","",},
        [1718] = {20186301,"Gia Cát Lượng","Nói hay lắm, nói hay lắm","Nói hay lắm, nói hay lắm","Gia Cát Cẩn","",},
        [1719] = {20186302,"Tôn Quyền","Trên cổ con lừa sao có treo bảng có chữ Gia Cát Tử Du?","Trên cổ con lừa sao có treo bảng có chữ Gia Cát Tử Du?","Gia Cát Khác","",},
        [1720] = {20186302,"Gia Cát Khác","Có lẽ thiếu đi hai chữ, để ta thêm lại lên.","Có lẽ thiếu đi hai chữ, để ta thêm lại lên.","Tôn Quyền","",},
        [1721] = {20186302,"Tôn Quyền","Ngươi viết gì thế? Lừa của... Gia Cát Tử Du, hay lắm.","Ngươi viết gì thế? Lừa của... Gia Cát Tử Du, hay lắm.","Gia Cát Khác","",},
        [1722] = {20186302,"Gia Cát Khác","Con lừa này là của ta! Ta phải dẫn đi!","Con lừa này là của ta! Ta phải dẫn đi!","Tôn Quyền","",},
        [1723] = {20186303,"Tôn Quyền","Phong Tử Du Tướng Soái, Tả Đô Hộ, quản lý Dự Châu, thế nào?","Phong Tử Du Tướng Soái, Tả Đô Hộ, quản lý Dự Châu, thế nào?","Gia Cát Cẩn","",},
        [1724] = {20186303,"Gia Cát Cẩn","Dự Châu hiện đang ở thuộc Ngụy, không trong tay của chúng ta.","Dự Châu hiện đang ở thuộc Ngụy, không trong tay của chúng ta.","Tôn Quyền","",},
        [1725] = {20186303,"Tôn Quyền","Vì vậy Tử Du hãy cố gắng giành lấy địa bàn này cho ta!","Vì vậy Tử Du hãy cố gắng giành lấy địa bàn này cho ta!","Gia Cát Cẩn","",},
        [1726] = {20186401,"Hoàng Thừa Ngạn","Con gái ta Anh Nguyệt còn có tên A Kiều, rất thông minh, không biết con trai nhà ai có phúc cưới được con gái ta.","Con gái ta Anh Nguyệt còn có tên A Kiều, rất thông minh, không biết con trai nhà ai có phúc cưới được con gái ta.","Nguyệt Anh","",},
        [1727] = {20186401,"Nguyệt Anh","Nghe nói Gia Cát Khổng Minh học thức hơn người, ta rất ngưỡng mộ, mong phụ thân hãy giúp mai mối.","Nghe nói Gia Cát Khổng Minh học thức hơn người, ta rất ngưỡng mộ, mong phụ thân hãy giúp mai mối.","Hoàng Thừa Ngạn","",},
        [1728] = {20186401,"Nguyệt Anh","Nhưng mong phụ thân hãy nói với hắn con xấu xí, xem phản ứng của hắn thế nào, như vậy chúng ta sẽ biết được nhân phẩm của hắn.","Nhưng mong phụ thân hãy nói với hắn con xấu xí, xem phản ứng của hắn thế nào, như vậy chúng ta sẽ biết được nhân phẩm của hắn.","Hoàng Thừa Ngạn","",},
        [1729] = {20186401,"Hoàng Thừa Ngạn","Kế hay, Khổng Minh nếu là người háo sắc, chúng ta sẽ không tiến hành hôn sự này.","Kế hay, Khổng Minh nếu là người háo sắc, chúng ta sẽ không tiến hành hôn sự này.","Nguyệt Anh","",},
        [1730] = {20186402,"Gia Cát Lượng","Nguyệt Anh phát minh cơ quan Mộc Cẩu, Mộc Hổ, Mộc Nhân, quả thật khiến ta khâm phục.","Nguyệt Anh phát minh cơ quan Mộc Cẩu, Mộc Hổ, Mộc Nhân, quả thật khiến ta khâm phục.","Nguyệt Anh","",},
        [1731] = {20186402,"Nguyệt Anh","Ta có một câu đố, khi chúng ta thành thân, ta không ngồi kiệu, không cưỡi ngựa, không ngồi thuyền thì ngươi có cách gì.","Ta có một câu đố, khi chúng ta thành thân, ta không ngồi kiệu, không cưỡi ngựa, không ngồi thuyền thì ngươi có cách gì.","Gia Cát Lượng","",},
        [1732] = {20186402,"Gia Cát Lượng","Đợi ta suy nghĩ.","Đợi ta suy nghĩ.","Nguyệt Anh","",},
        [1733] = {20186403,"Gia Cát Lượng","Cảm tạ Anh Nguyệt đã đưa ra câu đố khó, nhờ vậy ta phát minh ra Vận Chuyển, giải quyết vấn đề vận chuyển vật tư.","Cảm tạ Anh Nguyệt đã đưa ra câu đố khó, nhờ vậy ta phát minh ra Vận Chuyển, giải quyết vấn đề vận chuyển vật tư.","Nguyệt Anh","",},
        [1734] = {20186403,"Nguyệt Anh","Quá khen, có thể giúp được tiên sinh, ta cũng rất vui.","Quá khen, có thể giúp được tiên sinh, ta cũng rất vui.","Gia Cát Lượng","",},
        [1735] = {20186403,"Nguyệt Anh","Tặng tiên sinh một chiếc quạt lông, giúp tiên sinh công thành, trị quốc an dân.","Tặng tiên sinh một chiếc quạt lông, giúp tiên sinh công thành, trị quốc an dân.","Gia Cát Lượng","",},
        [1736] = {20186501,"Phan Chương","Phía trước Trương Liêu đột kích doanh trại quân ta, chúng ta không đánh lại mà bỏ chạy, sao lại thế này?","Phía trước Trương Liêu đột kích doanh trại quân ta, chúng ta không đánh lại mà bỏ chạy, sao lại thế này?","","",},
        [1737] = {20186501,"Phan Chương","Hai lính bỏ chạy đã bị ta diệt, những ai không muốn mất mạng hãy theo ta!","Hai lính bỏ chạy đã bị ta diệt, những ai không muốn mất mạng hãy theo ta!","","",},
        [1738] = {20186501,"Tôn Quyền","Phan Chương dũng mãnh, ta phong ngươi làm Thiên Tướng, đóng quân tại Bán Châu.","Phan Chương dũng mãnh, ta phong ngươi làm Thiên Tướng, đóng quân tại Bán Châu.","Phan Chương","",},
        [1739] = {20186502,"Quan Vũ","Ngươi là ai? Sao dám cản đường ta, mau tránh ra, ta sẽ tha mạng cho ngươi.","Ngươi là ai? Sao dám cản đường ta, mau tránh ra, ta sẽ tha mạng cho ngươi.","Phan Chương","",},
        [1740] = {20186502,"Phan Chương","Quả nhiên là Quan Vân Trường, ngươi quá xem thường thực lực của Đông Ngô ta rồi.","Quả nhiên là Quan Vân Trường, ngươi quá xem thường thực lực của Đông Ngô ta rồi.","Quan Vũ","",},
        [1741] = {20186502,"Quan Vũ","Nếu không phải vì ta vừa chiến đấu xong, người ngựa đều đã mệt thì ngươi làm sao có tư cách nói chuyện với ta?","Nếu không phải vì ta vừa chiến đấu xong, người ngựa đều đã mệt thì ngươi làm sao có tư cách nói chuyện với ta?","Phan Chương","",},
        [1742] = {20186502,"Phan Chương","Không nói nhiều, hãy thử lợi hại bẫy của bọn ta!","Không nói nhiều, hãy thử lợi hại bẫy của bọn ta!","Quan Vũ","",},
        [1743] = {20186601,"Tôn Sách","Tạo thiên hạ ngươi không bằng ta, giữ đại nghiệp ta không bằng ngươi.","Tạo thiên hạ ngươi không bằng ta, giữ đại nghiệp ta không bằng ngươi.","Tôn Quyền","",},
        [1744] = {20186601,"Tôn Quyền","Có huynh trưởng ở đây, sao lại lo sợ không thành công? Ta nhất định sẽ giúp huynh trưởng.","Có huynh trưởng ở đây, sao lại lo sợ không thành công? Ta nhất định sẽ giúp huynh trưởng.","Tôn Sách","",},
        [1745] = {20186601,"Tôn Sách","Ngươi từ nhỏ thông minh, sau này các quan viên đều là thuộc hạ của ngươi.","Ngươi từ nhỏ thông minh, sau này các quan viên đều là thuộc hạ của ngươi.","Tôn Quyền","",},
        [1746] = {20186602,"Trương Chiêu","Chủ nhân ta cảm thấy việc Tôn Lưu hợp sức cùng nhau chống Tào là rất tốt.","Chủ nhân ta cảm thấy việc Tôn Lưu hợp sức cùng nhau chống Tào là rất tốt.","Gia Cát Lượng","",},
        [1747] = {20186602,"Trương Chiêu","Không biết tiên sinh có thể ở lại Giang Đông, cùng nhau tạo nghiệp lớn?","Không biết tiên sinh có thể ở lại Giang Đông, cùng nhau tạo nghiệp lớn?","Gia Cát Lượng","",},
        [1748] = {20186602,"Gia Cát Lượng","Tôn tướng quân trọng dụng nhưng không thể khiến Gia Cát Lượng phát huy hết tài năng.","Tôn tướng quân trọng dụng nhưng không thể khiến Gia Cát Lượng phát huy hết tài năng.","Trương Chiêu","",},
        [1749] = {20186603,"Gia Cát Lượng","Nếu quân lực của quý quốc đủ để chống lại quân Tào thì nên sớm cắt đứt giao hữu với quân Tào. Nếu không thể chống lại quân Tào, thì nên sớm đầu hàng.","Nếu quân lực của quý quốc đủ để chống lại quân Tào thì nên sớm cắt đứt giao hữu với quân Tào. Nếu không thể chống lại quân Tào, thì nên sớm đầu hàng.","Tôn Quyền","",},
        [1750] = {20186603,"Tôn Quyền","Lưu Bị sao không đầu hàng Tào Tháo?","Lưu Bị sao không đầu hàng Tào Tháo?","Gia Cát Lượng","",},
        [1751] = {20186603,"Gia Cát Lượng","Chủ của ta Lưu Bị là hậu duệ của Hán Thất, nhiều người khâm phục nên đã đầu quân, bọn ta sao có thể đầu hàng?","Chủ của ta Lưu Bị là hậu duệ của Hán Thất, nhiều người khâm phục nên đã đầu quân, bọn ta sao có thể đầu hàng?","Tôn Quyền","",},
        [1752] = {20186603,"Tôn Quyền","Ta có lãnh thổ Giang Đông rộng lớn lại có hơn 10 vạn tinh binh, sao có thể mặc các ngươi muốn làm gì thì làm?","Ta có lãnh thổ Giang Đông rộng lớn lại có hơn 10 vạn tinh binh, sao có thể mặc các ngươi muốn làm gì thì làm?","Gia Cát Lượng","",},
        [1753] = {20186701,"Văn Xú","Bọn chúng luôn nhắc đến tên hai người bọn ta, nhưng không ai biết hai người bọn ta ai lợi hại hơn.","Bọn chúng luôn nhắc đến tên hai người bọn ta, nhưng không ai biết hai người bọn ta ai lợi hại hơn.","Nhan Lương","",},
