        [1757] = {20186702,"Thư Thụ","Chúa công, Nhan Lương thiện chiến nhưng nóng tính, không thể để hắn đơn độc tác chiến.","Chúa công, Nhan Lương thiện chiến nhưng nóng tính, không thể để hắn đơn độc tác chiến.","Viên Thiệu","",},
        [1758] = {20186702,"Viên Thiệu","Các ngươi thì biết gì? Nhan Lương là đại tướng, thực lực hơn hẳn các ngươi.","Các ngươi thì biết gì? Nhan Lương là đại tướng, thực lực hơn hẳn các ngươi.","Thư Thụ","",},
        [1759] = {20186703,"Lưu Bị","Nhị đệ Quan Vân Trường, thân hình cao lớn, mặc chiến bào xanh, dùng đại đao Thanh Long. Nếu có gặp xin hãy nhắn lại.","Nhị đệ Quan Vân Trường, thân hình cao lớn, mặc chiến bào xanh, dùng đại đao Thanh Long. Nếu có gặp xin hãy nhắn lại.","Nhan Lương","",},
        [1760] = {20186703,"Nhan Lương","Sao? Nếu vậy, gặp được hắn nhất định phải thử xem sao.","Sao? Nếu vậy, gặp được hắn nhất định phải thử xem sao.","Lưu Bị","",},
        [1761] = {20186801,"Triệu Ngang","Mã Siêu tăng cường tấn công, hiện trong thành thiếu lương thực, tình thế nguy cấp, Thứ Sử Vi Khang định đầu hàng Mã Siêu. Ta khuyên hắn không nên đầu hàng nhưng hắn không chịu nghe.","Mã Siêu tăng cường tấn công, hiện trong thành thiếu lương thực, tình thế nguy cấp, Thứ Sử Vi Khang định đầu hàng Mã Siêu. Ta khuyên hắn không nên đầu hàng nhưng hắn không chịu nghe.","Vương Dị","",},
        [1762] = {20186801,"Vương Dị","Ai nói cứu viện Trung Nguyên nhất định không đến kịp? Chúng ta phải khích lệ tướng sĩ đồng lòng quyết hi sinh báo quốc, không thể đầu hàng phản tặc.","Ai nói cứu viện Trung Nguyên nhất định không đến kịp? Chúng ta phải khích lệ tướng sĩ đồng lòng quyết hi sinh báo quốc, không thể đầu hàng phản tặc.","Triệu Ngang","",},
        [1763] = {20186802,"Triệu Ngang","Mã Siêu chiếm địa bàn của chúng ta, còn diệt Vi Khang, mối thù này phải trả!","Mã Siêu chiếm địa bàn của chúng ta, còn diệt Vi Khang, mối thù này phải trả!","Vương Dị","",},
        [1764] = {20186802,"Triệu Ngang","Hiện Triệu Nguyệt đang bị bắt làm con tin trong tay hắn, nếu chúng ta gây chuyện chắc chắn hắn sẽ hạ thủ với Triệu Nguyệt, phải làm sao đây?","Hiện Triệu Nguyệt đang bị bắt làm con tin trong tay hắn, nếu chúng ta gây chuyện chắc chắn hắn sẽ hạ thủ với Triệu Nguyệt, phải làm sao đây?","Vương Dị","",},
        [1765] = {20186802,"Vương Dị","Trung nghĩa là gốc con người, chúng ta nhất phải báo thù, dù có mất mạng cũng không từ.","Trung nghĩa là gốc con người, chúng ta nhất phải báo thù, dù có mất mạng cũng không từ.","Triệu Ngang","",},
