        [1767] = {20186901,"Quan Định","Đây là con trai thứ hai của ta Quan Bình, từ nhỏ luyện võ, hiện muốn theo Quan tướng quân, không biết ý người như thế nào?","Đây là con trai thứ hai của ta Quan Bình, từ nhỏ luyện võ, hiện muốn theo Quan tướng quân, không biết ý người như thế nào?","Lưu Bị","",},
