        [1772] = {20186902,"Quan Vũ","Con hãy nghe cho rõ đây, chỉ sức mạnh và dũng khí là chưa đủ, phải dùng trí tuệ và mưu lược mới có thể thành đại sự.","Con hãy nghe cho rõ đây, chỉ sức mạnh và dũng khí là chưa đủ, phải dùng trí tuệ và mưu lược mới có thể thành đại sự.","Quan Bình","",},
        [1773] = {20186902,"Quan Bình","Phụ thân nói đúng, vì thế phụ thân đừng manh động diệt sứ giả Gia Cát Cẩn nước Ngô phái đến!","Phụ thân nói đúng, vì thế phụ thân đừng manh động diệt sứ giả Gia Cát Cẩn nước Ngô phái đến!","Quan Vũ","",},
