        [1777] = {20187001,"Gia Cát Lượng","Nhưng giờ ngươi đã bị ta bắt, ngươi có phục hay không?","Nhưng giờ ngươi đã bị ta bắt, ngươi có phục hay không?","Mạnh Hoạch","",},
        [1778] = {20187001,"Mạnh Hoạch","Đường núi hiểm trở, ta bất cẩn trúng kế, sao có thể phục?","Đường núi hiểm trở, ta bất cẩn trúng kế, sao có thể phục?","Gia Cát Lượng","",},
