        [1782] = {20187002,"Gia Cát Lượng","Đừng tức giận, ta chỉ muốn dùng phu nhân để đổi hai vị tướng Trương Nghi và Mã Trung.","Đừng tức giận, ta chỉ muốn dùng phu nhân để đổi hai vị tướng Trương Nghi và Mã Trung.","Mạnh Hoạch","",},
