        [1786] = {20187003,"Mạnh Hoạch","Hu hu hu, bắt bảy lần, thả bảy lần, xưa nay chưa từng có chuyện như vậy. Ta tuy là người vùng xa nhưng cũng biết lễ nghi liêm sỉ, đừng nghĩ rằng ta không biết gì cả.","Hu hu hu, bắt bảy lần, thả bảy lần, xưa nay chưa từng có chuyện như vậy. Ta tuy là người vùng xa nhưng cũng biết lễ nghi liêm sỉ, đừng nghĩ rằng ta không biết gì cả.","Gia Cát Lượng","",},
