        [1796] = {20187102,"Hán Hiến Đế","Tào Tháo thao túng triều đình, quốc sự trẫm đều không thể làm chủ, nay được hoàng thúc anh dũng, trẫm được cứu rồi!","Tào Tháo thao túng triều đình, quốc sự trẫm đều không thể làm chủ, nay được hoàng thúc anh dũng, trẫm được cứu rồi!","Lưu Bị","",},
        [1797] = {20187103,"Hán Hiến Đế","Trẫm từ khi lên ngôi, gian hùng nổi dậy, trước là Đổng Trác, sau là Tào Tháo lộng quyền.","Trẫm từ khi lên ngôi, gian hùng nổi dậy, trước là Đổng Trác, sau là Tào Tháo lộng quyền.","","",},
