        [1799] = {20187103,"Hán Hiến Đế","Trẫm đã viết huyết thư, cất giữ mang cho Đổng Thừa, hy vọng hắn ta có thể triệu tập nghĩa sĩ, để diệt trừ phản loạn!","Trẫm đã viết huyết thư, cất giữ mang cho Đổng Thừa, hy vọng hắn ta có thể triệu tập nghĩa sĩ, để diệt trừ phản loạn!","","",},
        [1800] = {20187201,"Hàn Đương","Gió lớn thổi gãy cột cờ, là điềm không may, chúng ta hãy mau về!","Gió lớn thổi gãy cột cờ, là điềm không may, chúng ta hãy mau về!","Tôn Kiên","",},
        [1801] = {20187201,"Tôn Kiên","Ta liên tục chiến thắng sắp chiếm được Tương Dương, sao lại vì cột cờ bị thổi gãy mà quay về?","Ta liên tục chiến thắng sắp chiếm được Tương Dương, sao lại vì cột cờ bị thổi gãy mà quay về?","Hàn Đương","",},
