        [2091] = {1701,"Nhân vật","Lần này mang thư đến cho đại ca không chừng có thể gặp Triệu Tử Long tướng quân! Nghe nói Triệu Tử Long có quan hệ rất tốt với đại ca.","Lần này mang thư đến cho đại ca không chừng có thể gặp Triệu Tử Long tướng quân! Nghe nói Triệu Tử Long có quan hệ rất tốt với đại ca.","Ngân Bình","",},
        [2092] = {1701,"Ngân Bình","Thật sao? Có thể gặp được Triệu Tử Long, thật cảm động quá đi!","Thật sao? Có thể gặp được Triệu Tử Long, thật cảm động quá đi!","","",},
