        [2097] = {1701,"Nhân vật","Ha ha! Tinh Thái lại quá khen rồi! Yên tâm, ta sẽ không để ngươi thất vọng đâu!","Ha ha! Tinh Thái lại quá khen rồi! Yên tâm, ta sẽ không để ngươi thất vọng đâu!","","",},
        [2098] = {1702,"Nhân vật","Điền Khải, ngươi hẳn là biết chuyện Tào Tháo đánh Từ Châu, Bì Hải Khổng Dung đã đồng ý cứu viện Từ Châu, còn ngươi thì sao?","Điền Khải, ngươi hẳn là biết chuyện Tào Tháo đánh Từ Châu, Bì Hải Khổng Dung đã đồng ý cứu viện Từ Châu, còn ngươi thì sao?","Điền Khải","",},
        [2099] = {1702,"Điền Khải","Từ Châu và Thanh Châu gắn bó với nhau, theo lý phải giúp, nhưng hiện giờ Thanh Châu đang khai chiến với Viên Thiệu, nếu phân tán binh lực e là không ổn...","Từ Châu và Thanh Châu gắn bó với nhau, theo lý phải giúp, nhưng hiện giờ Thanh Châu đang khai chiến với Viên Thiệu, nếu phân tán binh lực e là không ổn...","","",},
        [2100] = {1702,"Điền Khải","Nhưng Tào Tháo đó đánh xong Từ Châu, không lâu sau chắc chắn sẽ đánh đến Thanh Châu... Phải làm sao đây...","Nhưng Tào Tháo đó đánh xong Từ Châu, không lâu sau chắc chắn sẽ đánh đến Thanh Châu... Phải làm sao đây...","","",},
        [2101] = {1702,"Nhân vật","Ta trước giờ luôn ghét người không quyết đoán, ngươi cứ nói đến hay không đến!","Ta trước giờ luôn ghét người không quyết đoán, ngươi cứ nói đến hay không đến!","","",},
        [2102] = {1702,"Điền Khải","Tiểu anh hùng đừng gấp, Tào Tháo đó hung hãn hơn Viên Thiệu, Điền Khải sẽ chi viện Từ Châu!","Tiểu anh hùng đừng gấp, Tào Tháo đó hung hãn hơn Viên Thiệu, Điền Khải sẽ chi viện Từ Châu!","","",},
