        [2106] = {1703,"Nhân vật","Đại ca đừng lo, chúng ta đi tìm Công Tôn Toản mượn ba ngàn binh mã, mượn luôn cả Triệu Tử Long tướng quân, được không?","Đại ca đừng lo, chúng ta đi tìm Công Tôn Toản mượn ba ngàn binh mã, mượn luôn cả Triệu Tử Long tướng quân, được không?","","",},
        [2107] = {1703,"Lưu Bị","Tứ đệ nghĩ thật chu đáo, chúng ta lập tức đi tìm Công Tôn Toản.","Tứ đệ nghĩ thật chu đáo, chúng ta lập tức đi tìm Công Tôn Toản.","","",},
        [2108] = {1704,"Công Tôn Toản","Tào Tháo không có thù với ngươi, sao phải ra sức thay người khác?","Tào Tháo không có thù với ngươi, sao phải ra sức thay người khác?","Lưu Bị","",},
        [2109] = {1704,"Lưu Bị","Lưu Bị đã đáp ứng Khổng Dung, không dám nuốt lời.","Lưu Bị đã đáp ứng Khổng Dung, không dám nuốt lời.","","",},
        [2110] = {1704,"Công Tôn Toản","Huyền Đức làm vậy sẽ tự tạo thêm kẻ địch đấy! Được rồi, ta đồng ý cho mượn ba ngàn tinh binh, còn Triệu Tử Long, ngươi phải vượt quả khảo nghiệm của hắn!","Huyền Đức làm vậy sẽ tự tạo thêm kẻ địch đấy! Được rồi, ta đồng ý cho mượn ba ngàn tinh binh, còn Triệu Tử Long, ngươi phải vượt quả khảo nghiệm của hắn!","","",},
        [2111] = {1704,"Nhân vật","Đại ca, khảo nghiệm của Tử Long tướng quân cứ để ta lo! Lâu rồi không đánh nhau với cao thủ, đang ngứa tay đây!","Đại ca, khảo nghiệm của Tử Long tướng quân cứ để ta lo! Lâu rồi không đánh nhau với cao thủ, đang ngứa tay đây!","","",},
        [2112] = {1705,"Triệu Vân","Lợi hại, tiểu anh hùng võ nghệ thật cao cường!","Lợi hại, tiểu anh hùng võ nghệ thật cao cường!","Nhân vật","",},
        [2113] = {1705,"Nhân vật","Quá khen! Triệu Tử Long tướng quân có nguyện ý cùng đại ca ta đi cứu viện Từ Châu?","Quá khen! Triệu Tử Long tướng quân có nguyện ý cùng đại ca ta đi cứu viện Từ Châu?","","",},
        [2114] = {1705,"Triệu Vân","Đương nhiên! Ngươi đã vượt qua khảo nghiệm, ta sao có thể nuốt lời!","Đương nhiên! Ngươi đã vượt qua khảo nghiệm, ta sao có thể nuốt lời!","","",},
        [2115] = {1705,"Triệu Vân","Hơn nữa, ta với Huyền Đức vốn không phải người ngoài. Ngươi là tứ đệ của Huyền Đức, Danh Tướng Lệnh xem như quà gặp mặt vậy! Ta đi chuẩn bị quân mã đây!","Hơn nữa, ta với Huyền Đức vốn không phải người ngoài. Ngươi là tứ đệ của Huyền Đức, Danh Tướng Lệnh xem như quà gặp mặt vậy! Ta đi chuẩn bị quân mã đây!","","",},
        [2116] = {1705,"Nhân vật","Tốt quá! Có Điền Khải, Khổng Dung trợ giúp, lại có Triệu Tử Long và binh mã của đại ca, chắc chắn sẽ đánh lui được Tào Tháo!","Tốt quá! Có Điền Khải, Khổng Dung trợ giúp, lại có Triệu Tử Long và binh mã của đại ca, chắc chắn sẽ đánh lui được Tào Tháo!","","",},
        [2117] = {1705,"Tinh Thái","Chúa công thật lợi hại! Trên đời này không có việc gì làm khó được chúa công cả!","Chúa công thật lợi hại! Trên đời này không có việc gì làm khó được chúa công cả!","","",},
        [2118] = {1705,"Nhân vật","Ha ha! Tinh Thái, Ngân Bình, chúng ta mau trở về Từ Châu!","Ha ha! Tinh Thái, Ngân Bình, chúng ta mau trở về Từ Châu!","","",},
        [2119] = {1801,"Tào Tháo","Lữ Bố, Tào Tháo ta với ngươi không thù oán, sao lại nhân lúc ta xuất binh đi Từ Châu mà đoạt hai thành Duyện Châu, Bộc Dương?","Lữ Bố, Tào Tháo ta với ngươi không thù oán, sao lại nhân lúc ta xuất binh đi Từ Châu mà đoạt hai thành Duyện Châu, Bộc Dương?","Lữ Bố","",},
        [2120] = {1801,"Lữ Bố","Thành trì nhà Hán ai cũng có phần. Trần Cung và Trương Mạc nguyện ý nghênh đón ta làm Duyện Châu Mục, đây là bản lĩnh của ta!","Thành trì nhà Hán ai cũng có phần. Trần Cung và Trương Mạc nguyện ý nghênh đón ta làm Duyện Châu Mục, đây là bản lĩnh của ta!","","",},
        [2121] = {1801,"Tào Tháo","Thật ngông cuồng! Đợi ta đánh vào thành Bộc Dương xong nhất định sẽ lấy thủ cấp của ngươi!","Thật ngông cuồng! Đợi ta đánh vào thành Bộc Dương xong nhất định sẽ lấy thủ cấp của ngươi!","","",},
        [2122] = {1801,"Lữ Bố","Chiến Thần Lữ Bố ta sao phải sợ ngươi chứ! Nếu ngươi dám vào Bộc Dương thì đừng mong trở ra nữa!","Chiến Thần Lữ Bố ta sao phải sợ ngươi chứ! Nếu ngươi dám vào Bộc Dương thì đừng mong trở ra nữa!","","",},
        [2123] = {1802,"Tinh Thái","Chúa công, Tào Tháo và Lữ Bố đánh nhau rồi! Tào Tháo vừa phái người đưa thư đến, hắn nới sẽ rút quân khỏi Từ Châu, mời chúng ta mau đi Bộc Dương cứu viện.","Chúa công, Tào Tháo và Lữ Bố đánh nhau rồi! Tào Tháo vừa phái người đưa thư đến, hắn nới sẽ rút quân khỏi Từ Châu, mời chúng ta mau đi Bộc Dương cứu viện.","Nhân vật","",},
        [2124] = {1802,"Nhân vật","Từ khi nào Lữ Bố có nhiều binh mã như vậy, đánh chiếm được hai thành của Tào Tháo!","Từ khi nào Lữ Bố có nhiều binh mã như vậy, đánh chiếm được hai thành của Tào Tháo!","","",},
        [2125] = {1802,"Tinh Thái","Sau khi Lý Thôi Quách Tị gây loạn Trường An, Lữ Bố vốn đi nhờ vả Viên Thuật, nào ngờ Viên Thuật hẹp hòi,, Lữ Bố thì không chịu dưới trướng người khác.","Sau khi Lý Thôi Quách Tị gây loạn Trường An, Lữ Bố vốn đi nhờ vả Viên Thuật, nào ngờ Viên Thuật hẹp hòi,, Lữ Bố thì không chịu dưới trướng người khác.","","",},
        [2126] = {1802,"Tinh Thái","Sau đó Tào Tháo đi đánh Từ Châu, Trần Lưu, Trương Mạc và Trần Cung bèn đón Lữ Bố qua đó làm Duyện Châu Mục.","Sau đó Tào Tháo đi đánh Từ Châu, Trần Lưu, Trương Mạc và Trần Cung bèn đón Lữ Bố qua đó làm Duyện Châu Mục.","","",},
        [2127] = {1802,"Nhân vật","Lúc đầu ta khuyên Tào Tháo rút khỏi Từ Châu, hắn tránh không gặp ta, nay lại đến nhờ vả! Thôi được, ta cứu hắn một lần nữa vậy!","Lúc đầu ta khuyên Tào Tháo rút khỏi Từ Châu, hắn tránh không gặp ta, nay lại đến nhờ vả! Thôi được, ta cứu hắn một lần nữa vậy!","","",},
        [2128] = {1803,"Trương Mạc","Ai đến Bộc Dương gây sự? Các ngươi có phải là gian tế của Tào Tháo?","Ai đến Bộc Dương gây sự? Các ngươi có phải là gian tế của Tào Tháo?","Nhân vật","",},
        [2129] = {1803,"Tinh Thái","Chúa công, Trương Mạc này nổi tiếng chính trực, là một trong Bát Trù, giao chiến với hắn phải cẩn thận.","Chúa công, Trương Mạc này nổi tiếng chính trực, là một trong Bát Trù, giao chiến với hắn phải cẩn thận.","","",},
        [2130] = {1803,"Nhân vật","Bát Trù? Chẳng lẽ hắn là một đầu bếp làm thức ăn rất ngon?","Bát Trù? Chẳng lẽ hắn là một đầu bếp làm thức ăn rất ngon?","","",},
        [2131] = {1803,"Tinh Thái","Ha ha, chúa công hiểu lầm rồi, Bát Trù này là tám người Trương Mạc, Độ Thượng... Ý là dùng tài phú giúp đỡ người khác.","Ha ha, chúa công hiểu lầm rồi, Bát Trù này là tám người Trương Mạc, Độ Thượng... Ý là dùng tài phú giúp đỡ người khác.","","",},
        [2132] = {1803,"Tinh Thái","Trương Mạc lúc trẻ nổi danh hiệp nghĩa, không tiếc tán gia bại sản để giúp đỡ người khác. Tào Tháo, Viên Thiệu đều từng là bằng hữu của Trương Mạc.","Trương Mạc lúc trẻ nổi danh hiệp nghĩa, không tiếc tán gia bại sản để giúp đỡ người khác. Tào Tháo, Viên Thiệu đều từng là bằng hữu của Trương Mạc.","","",},
        [2133] = {1803,"Tinh Thái","Trương Mạc ngày càng nổi danh, sau đó được triều đình ban cho chức quan Trần Lưu Thái Thú.","Trương Mạc ngày càng nổi danh, sau đó được triều đình ban cho chức quan Trần Lưu Thái Thú.","","",},
        [2134] = {1803,"Trương Mạc","Hừ! Đừng thấy ta cầm dao bếp mà nghĩ ta là đầu bếp! Đây là tuyệt thế bảo đao đấy!","Hừ! Đừng thấy ta cầm dao bếp mà nghĩ ta là đầu bếp! Đây là tuyệt thế bảo đao đấy!","","",},
        [2135] = {1803,"Nhân vật","Trương Mạc, xem ra ngươi cũng có bản lĩnh. Nhưng chẳng đáng là gì khi ở trước mặt ta cả!","Trương Mạc, xem ra ngươi cũng có bản lĩnh. Nhưng chẳng đáng là gì khi ở trước mặt ta cả!","","",},
        [2136] = {1804,"Trương Mạc","Tướng quân quả thật tài giỏi! Ta nguyện ý tặng bảo đao này cho tướng quân!","Tướng quân quả thật tài giỏi! Ta nguyện ý tặng bảo đao này cho tướng quân!","Nhân vật","",},
        [2137] = {1804,"Nhân vật","Thôi vậy, ngươi cứ giữ lại bảo đao để dùng đi!","Thôi vậy, ngươi cứ giữ lại bảo đao để dùng đi!","","",},
        [2138] = {1804,"Trương Mạc","Không ngờ Trương Mạc ta cũng có ngày bị người khác ghét bỏ!","Không ngờ Trương Mạc ta cũng có ngày bị người khác ghét bỏ!","","",},
        [2139] = {1805,"Vu Cấm","Chúa công còn ở ngoài thành, cho chúng ta vào thành trước đi!","Chúa công còn ở ngoài thành, cho chúng ta vào thành trước đi!","Tào Tháo","",},
        [2140] = {1805,"Tào Tháo","Ta không tự đi, ai dám đi trước!","Ta không tự đi, ai dám đi trước!","Vu Cấm","",},
        [2141] = {1805,"Nhân vật","Tào Tháo đâu? Lúc nãy còn nghe ông ta nói chuyện với người khác mà!","Tào Tháo đâu? Lúc nãy còn nghe ông ta nói chuyện với người khác mà!","Tinh Thái","",},
        [2142] = {1805,"Tinh Thái","Chúa công, Tào Tháo đó tin lầm gian kế của Trần Cung và Lữ Bố, nay đã dẫn binh xông vào thành rồi!","Chúa công, Tào Tháo đó tin lầm gian kế của Trần Cung và Lữ Bố, nay đã dẫn binh xông vào thành rồi!","","",},
        [2143] = {1805,"Tinh Thái","Bên trong khói lửa ngập trời, không biết Tào Tháo sao rồi! Lúc nãy thấy Nhạc Tiến, Lý Điển, Điển Vi đều đang tìm Tào Tháo!","Bên trong khói lửa ngập trời, không biết Tào Tháo sao rồi! Lúc nãy thấy Nhạc Tiến, Lý Điển, Điển Vi đều đang tìm Tào Tháo!","","",},
        [2144] = {1805,"Nhân vật","Chúng ta đánh bại Lữ Bố, mau đi cứu người.","Chúng ta đánh bại Lữ Bố, mau đi cứu người.","","",},
        [2145] = {1806,"Lữ Bố","Ha ha, trận đánh này thật sảng khoái! Tiểu anh hùng, nhờ ngươi mà ta với Điêu Thuyền mới có ngày hôm nay! Nể mặt ngươi, hôm nay ta sẽ tha cho Tào Tháo!","Ha ha, trận đánh này thật sảng khoái! Tiểu anh hùng, nhờ ngươi mà ta với Điêu Thuyền mới có ngày hôm nay! Nể mặt ngươi, hôm nay ta sẽ tha cho Tào Tháo!","Nhân vật","",},
        [2146] = {1806,"Nhân vật","Lữ Ôn Hầu quả nhiên sảng khoái!","Lữ Ôn Hầu quả nhiên sảng khoái!","Lữ Bố","",},
        [2147] = {1806,"Tào Tháo","Lúc nãy cây cột cổng thành đổ xuống trung tay ta, giờ cánh tay đã bị phỏng!","Lúc nãy cây cột cổng thành đổ xuống trung tay ta, giờ cánh tay đã bị phỏng!","Nhân vật","",},
        [2148] = {1806,"Tào Tháo","Nếu không nhờ tiểu anh hùng đánh bại Lữ Bố, hôm nay quả thật sẽ gặp nguy! Danh Tướng Lệnh này xin tặng cho các hạ!","Nếu không nhờ tiểu anh hùng đánh bại Lữ Bố, hôm nay quả thật sẽ gặp nguy! Danh Tướng Lệnh này xin tặng cho các hạ!","","",},
        [2149] = {1806,"Nhân vật","Mong Tào huynh hãy giữ lời rút quân khỏi Từ Châu!","Mong Tào huynh hãy giữ lời rút quân khỏi Từ Châu!","","",},
        [2150] = {1806,"Tào Tháo","Tiểu anh hùng yên tâm, ta đã sớm rút quân đi rồi, lần trước tránh mặt không gặp, mong tiểu anh hùng tha thứ.","Tiểu anh hùng yên tâm, ta đã sớm rút quân đi rồi, lần trước tránh mặt không gặp, mong tiểu anh hùng tha thứ.","","",},
        [2151] = {1806,"Nhân vật","Được, nếu Tào huynh đã giữa lời thì ta cũng không tính toán nhiều nữa. Giờ ta phải về Từ Châu tìm đại ca, xin cáo từ.","Được, nếu Tào huynh đã giữa lời thì ta cũng không tính toán nhiều nữa. Giờ ta phải về Từ Châu tìm đại ca, xin cáo từ.","","",},
        [2152] = {1806,"Tào Tháo","Cáo từ! Lần này trúng kế của bọn thất phu, sớm muộn ta cũng phải đoạt lại thành Bộc Dương!","Cáo từ! Lần này trúng kế của bọn thất phu, sớm muộn ta cũng phải đoạt lại thành Bộc Dương!","","",},
        [2153] = {1901,"Đào Khiêm","Ngươi đâu, mau mang Đại Ấn của Từ Châu ra đây!","Ngươi đâu, mau mang Đại Ấn của Từ Châu ra đây!","Lưu Bị","",},
        [2154] = {1901,"Lưu Bị","Đào công đây là có ý gì?","Đào công đây là có ý gì?","","",},
        [2155] = {1901,"Đào Khiêm","Huyền Đức, ngươi dang vẻ hiên ngang, có đức có tài, lại hán dòng dõi nhà Hán, ta nguyện nhường Từ Châu cho ngươi!","Huyền Đức, ngươi dang vẻ hiên ngang, có đức có tài, lại hán dòng dõi nhà Hán, ta nguyện nhường Từ Châu cho ngươi!","","",},
        [2156] = {1901,"Lưu Bị","Lưu Bị tuy là dòng họ nhà Hán, nhưng chưa có công lớn nên không dám nhận lệnh. Sao Đào công không nhường cho Viên Thiệu?","Lưu Bị tuy là dòng họ nhà Hán, nhưng chưa có công lớn nên không dám nhận lệnh. Sao Đào công không nhường cho Viên Thiệu?","","",},
        [2157] = {1901,"Đào Khiêm","Viên Thiệu kém cỏi, làm sao so được với ngươi!","Viên Thiệu kém cỏi, làm sao so được với ngươi!","","",},
        [2158] = {1901,"Lưu Bị","Lần này ta dẫn binh đến cứu Từ Châu là vì đại nghĩa, không hề có tư lợi. Lưu Bị quả thật không dám nhận ủy thác của Đào công.","Lần này ta dẫn binh đến cứu Từ Châu là vì đại nghĩa, không hề có tư lợi. Lưu Bị quả thật không dám nhận ủy thác của Đào công.","","",},
        [2159] = {1902,"Đào Khiêm","Tiểu anh hùng tuổi trẻ tài cao, lòng có chí lớn. Đào Khiêm muốn tiểu anh hùng giúp một việc.","Tiểu anh hùng tuổi trẻ tài cao, lòng có chí lớn. Đào Khiêm muốn tiểu anh hùng giúp một việc.","Nhân vật","",},
        [2160] = {1902,"Nhân vật","Đào công cứ nói.","Đào công cứ nói.","","",},
        [2161] = {1902,"Đào Khiêm","Nay thiên hạ đại loạn, lão phu đã già, con trai thì bất tài, ta muốn nhường chức Từ Châu Thái Thú cho Lưu Bị, nào ngờ Lưu Bị từ chối, mong tiểu anh hùng giúp khuyên nhủ Lưu Bị.","Nay thiên hạ đại loạn, lão phu đã già, con trai thì bất tài, ta muốn nhường chức Từ Châu Thái Thú cho Lưu Bị, nào ngờ Lưu Bị từ chối, mong tiểu anh hùng giúp khuyên nhủ Lưu Bị.","","",},
        [2162] = {1902,"Nhân vật","Kỳ lạ, sao người không truyền lại cho con trai!","Kỳ lạ, sao người không truyền lại cho con trai!","","",},
        [2163] = {1902,"Đào Khiêm","Tiểu anh hùng có điều không biết, hai đứa con ta thật sự không đủ tài năng đảm nhận, biết phải làm sao chứ? (Nói xong liền khóc)","Tiểu anh hùng có điều không biết, hai đứa con ta thật sự không đủ tài năng đảm nhận, biết phải làm sao chứ? (Nói xong liền khóc)","","",},
        [2164] = {1902,"Nhân vật","Đào công đừng gấp, ta sẽ đi khuyên đại ca ngay!","Đào công đừng gấp, ta sẽ đi khuyên đại ca ngay!","","",},
        [2165] = {1903,"Quan Vũ","Tứ đệ, đại ca biết ngươi đến khuyên nên bảo ta và Dực Đức cản ngươi lại.","Tứ đệ, đại ca biết ngươi đến khuyên nên bảo ta và Dực Đức cản ngươi lại.","Nhân vật","",},
        [2166] = {1903,"Nhân vật","Nhị ca, đắc tội rồi! Vừa hay nhân cơ hội này chúng ta tỉ thí xem.","Nhị ca, đắc tội rồi! Vừa hay nhân cơ hội này chúng ta tỉ thí xem.","","",},
        [2167] = {1904,"Tinh Thái","Gia gia, người thật hồ đồ, ngăn cản chúa công ta làm gì chứ?","Gia gia, người thật hồ đồ, ngăn cản chúa công ta làm gì chứ?","Trương Phi","",},
        [2168] = {1904,"Trương Phi","Nữ nhi ngoan, đây là quân lệnh, đừng trách gia gia.","Nữ nhi ngoan, đây là quân lệnh, đừng trách gia gia.","","",},
        [2169] = {1904,"Tinh Thái","Vậy đến lúc chiến đấu, người không được làm chúa công ta bị thương.","Vậy đến lúc chiến đấu, người không được làm chúa công ta bị thương.","","",},
        [2170] = {1904,"Trương Phi","Được rồi, lời của ngươi ta đều nghe theo hết!","Được rồi, lời của ngươi ta đều nghe theo hết!","","",},
        [2171] = {1905,"Nhân vật","Đại ca, Từ Châu là vùng đất tốt, Tào Tháo ở phía bắc và Viên Thuật ở phái nam đều muốn đoạt được. Nếu người không tiếp nhận Từ Châu, e là bá tánh Từ Châu sẽ gặp nguy.","Đại ca, Từ Châu là vùng đất tốt, Tào Tháo ở phía bắc và Viên Thuật ở phái nam đều muốn đoạt được. Nếu người không tiếp nhận Từ Châu, e là bá tánh Từ Châu sẽ gặp nguy.","Lưu Bị","",},
        [2172] = {1905,"Lưu Bị","Tứ đệ nói rất đúng, là đại ca quá cổ hủ rồi. Nhưng một mình đại ca làm sao đảm nhận trách nhiệm lớn này!","Tứ đệ nói rất đúng, là đại ca quá cổ hủ rồi. Nhưng một mình đại ca làm sao đảm nhận trách nhiệm lớn này!","","",},
        [2173] = {1905,"Nhân vật","Tồn Càn, My Trúc, Trần Đăng sẽ hỗ trợ người, còn có huynh đệ chúng ta nữa. Đại ca đừng chối từ nữa!","Tồn Càn, My Trúc, Trần Đăng sẽ hỗ trợ người, còn có huynh đệ chúng ta nữa. Đại ca đừng chối từ nữa!","","",},
        [2174] = {1905,"Lưu Bị","Được, ta sẽ nghe lời tứ đệ!","Được, ta sẽ nghe lời tứ đệ!","","",},
        [2175] = {1905,"Lưu Bị","Tứ đệ, đây là Danh Tướng Lệnh do Đào công tặng cho ta, đa tạ ngươi khiến Tào Tháo lui binh, giải nguy cho Từ Châu!","Tứ đệ, đây là Danh Tướng Lệnh do Đào công tặng cho ta, đa tạ ngươi khiến Tào Tháo lui binh, giải nguy cho Từ Châu!","","",},
        [2176] = {1905,"Nhân vật","Đa tạ đại ca! Tứ đệ xin nhận lấy vậy!","Đa tạ đại ca! Tứ đệ xin nhận lấy vậy!","","",},
        [2177] = {2001,"Nhân vật","Đại ca đã ổn định ở Từ Châu, ta đã từ biệt đại ca đến Trường An, muốn xem thử Trường An giờ như thế nào.","Đại ca đã ổn định ở Từ Châu, ta đã từ biệt đại ca đến Trường An, muốn xem thử Trường An giờ như thế nào.","","",},
        [2178] = {2001,"Nhân vật","A! Lần trước đến Trường An, nơi đây còn rất phồn hoa, sao giờ lại tan hoang suy tàn như vậy! Tinh Thái, ngươi biết chuyện gì xảy ra không?","A! Lần trước đến Trường An, nơi đây còn rất phồn hoa, sao giờ lại tan hoang suy tàn như vậy! Tinh Thái, ngươi biết chuyện gì xảy ra không?","Tinh Thái","",},
        [2179] = {2001,"Tinh Thái","Chúa công, sau khi Lý Thôi, Quách Tị chiếm Trường An bèn uy hiếp thiên tử, nắm giữ triều chính, nhưng hai người này lại nghi kỵ nhau và thường xảy ra tranh đấu nên Trường An mới trở nên như vậy.","Chúa công, sau khi Lý Thôi, Quách Tị chiếm Trường An bèn uy hiếp thiên tử, nắm giữ triều chính, nhưng hai người này lại nghi kỵ nhau và thường xảy ra tranh đấu nên Trường An mới trở nên như vậy.","","",},
        [2180] = {2001,"Ngân Bình","Lúc trước Giả Hủ đó không phải nói là sẽ kềm chế Lý Thôi, Quách Tị, bảo vệ bá quan và thiên tử sao?","Lúc trước Giả Hủ đó không phải nói là sẽ kềm chế Lý Thôi, Quách Tị, bảo vệ bá quan và thiên tử sao?","","",},
        [2181] = {2001,"Nhân vật","Đi, chúng ta đi tiêu diệt Lý Thôi, Quách Tị, tìm Giả Hủ và cứu thiên tử!","Đi, chúng ta đi tiêu diệt Lý Thôi, Quách Tị, tìm Giả Hủ và cứu thiên tử!","","",},
        [2182] = {2002,"Hán Hiến Đế","Tiểu anh hùng đã hiểu lầm Giả Hủ. Nếu không nhờ Giả Hủ đứng giữa hòa giải, trẫm đã sớm mất mạng rồi.","Tiểu anh hùng đã hiểu lầm Giả Hủ. Nếu không nhờ Giả Hủ đứng giữa hòa giải, trẫm đã sớm mất mạng rồi.","Nhân vật","",},
        [2183] = {2002,"Nhân vật","Thì ra là vậy. Giả Hủ, nể mặt hoàng đế nên ta tha cho ngươi.","Thì ra là vậy. Giả Hủ, nể mặt hoàng đế nên ta tha cho ngươi.","","",},
        [2184] = {2002,"Giả Hủ","Bệ hạ, Lý Thôi, Quách Tị đó đã có ý muốn diệt trừ ta, ta phải rời khỏi Trường An. (Giả Hủ rời đi)","Bệ hạ, Lý Thôi, Quách Tị đó đã có ý muốn diệt trừ ta, ta phải rời khỏi Trường An. (Giả Hủ rời đi)","","",},
        [2185] = {2002,"Hán Hiến Đế","Giả Hủ đã đi rồi, từ giờ ai sẽ bảo vệ trẫm? Hoàng đế như trẫm thật là bi thảm.","Giả Hủ đã đi rồi, từ giờ ai sẽ bảo vệ trẫm? Hoàng đế như trẫm thật là bi thảm.","","",},
