        [2203] = {2005,"Từ Hoảng","Ta biết Dương Phụng, Hàn Tiềm không phải người lập nghiệp lớn, chỉ là theo đã lâu, không nỡ bỏ đi.","Ta biết Dương Phụng, Hàn Tiềm không phải người lập nghiệp lớn, chỉ là theo đã lâu, không nỡ bỏ đi.","","",},
        [2204] = {2005,"Nhân vật","Chim khôn chọn cây mà đậu, hiền thần chọn chủ mà thờ, Duyện Châu Mục Tào Tháo là anh hùng đương thế, trọng hiền sĩ, ngươi có thể đến nương nhờ ông ta.","Chim khôn chọn cây mà đậu, hiền thần chọn chủ mà thờ, Duyện Châu Mục Tào Tháo là anh hùng đương thế, trọng hiền sĩ, ngươi có thể đến nương nhờ ông ta.","","",},
        [2205] = {2005,"Từ Hoảng","Đa tạ tiểu anh hùng, xin tặng Danh Tướng Lệnh này làm quà đáp tạ. (Từ Hoảng cáo từ)","Đa tạ tiểu anh hùng, xin tặng Danh Tướng Lệnh này làm quà đáp tạ. (Từ Hoảng cáo từ)","","",},
        [2206] = {2005,"Nhân vật","Mấy năm nay thiên tai liên miên, hoàng đế tuy về Lạc Dương, nhưng không có nơi nào phụng dưỡng, lại không có quân lính bảo vệ, chắc chắn không phải kế lâu dài.","Mấy năm nay thiên tai liên miên, hoàng đế tuy về Lạc Dương, nhưng không có nơi nào phụng dưỡng, lại không có quân lính bảo vệ, chắc chắn không phải kế lâu dài.","Ngân Bình","",},
        [2207] = {2005,"Ngân Bình","Làm hoàng đế như vậy thật khổ, chỉ có chúa công là sướng, tiêu dao tự tại, không ai dám bắt nạt.","Làm hoàng đế như vậy thật khổ, chỉ có chúa công là sướng, tiêu dao tự tại, không ai dám bắt nạt.","","",},
        [2208] = {2005,"Nhân vật","Ha ha, ai bảo ông ta là Hán Hiến Đế làm chi! Ta thì khác! Ta tuy không phải hoàng đế, nhưng ta mạnh nhất! Đây là thế giới dùng sức mạnh để nói chuyện!","Ha ha, ai bảo ông ta là Hán Hiến Đế làm chi! Ta thì khác! Ta tuy không phải hoàng đế, nhưng ta mạnh nhất! Đây là thế giới dùng sức mạnh để nói chuyện!","","",},
