        [2213] = {2101,"Tinh Thái","Chúa công, dường như ngài đã có dự tính trước, có phải đã đoán được suy nghĩ của Viên Thiệu và Tào Tháo không?","Chúa công, dường như ngài đã có dự tính trước, có phải đã đoán được suy nghĩ của Viên Thiệu và Tào Tháo không?","","",},
