        [2215] = {2101,"Ngân Bình","Ha ha, chúa công, chúng ta chuyện vui chắc là không sao, đi!","Ha ha, chúa công, chúng ta chuyện vui chắc là không sao, đi!","","",},
        [2216] = {2102,"Thư Thụ","Tướng quân dùng trung nghĩa cứu giúp thiên hạ. Nay hoàng thượng không chốn nương thân, tướng quân nên sớm nghênh đại giá, lập đô ở Nghiệp Thành.","Tướng quân dùng trung nghĩa cứu giúp thiên hạ. Nay hoàng thượng không chốn nương thân, tướng quân nên sớm nghênh đại giá, lập đô ở Nghiệp Thành.","Viên Thiệu","",},
        [2217] = {2102,"Thư Thụ","Khống chế thiên tử để lệnh cho chư hầu, tập hợp binh mã để dẹp kẻ không thần phục. Lúc đó, còn ai chống cự được!","Khống chế thiên tử để lệnh cho chư hầu, tập hợp binh mã để dẹp kẻ không thần phục. Lúc đó, còn ai chống cự được!","","",},
        [2218] = {2102,"Viên Thiệu","Quách Đồ, ngươi thấy thế nào?","Quách Đồ, ngươi thấy thế nào?","","",},
        [2219] = {2102,"Quách Đồ","Hán thất suy vi đã lâu, muốn chấn hưng thì không phải dễ!","Hán thất suy vi đã lâu, muốn chấn hưng thì không phải dễ!","","",},
        [2220] = {2102,"Quách Đồ","Nếu đón thiên tử, thì việc gì cũng phải dâng biểu thỉnh thị. Phục tùng mệnh lệnh sẽ mất đi quyền lực, không phục tùng thì mang tội kháng lệnh, đây không phải là cách hay.","Nếu đón thiên tử, thì việc gì cũng phải dâng biểu thỉnh thị. Phục tùng mệnh lệnh sẽ mất đi quyền lực, không phục tùng thì mang tội kháng lệnh, đây không phải là cách hay.","","",},
        [2221] = {2102,"Viên Thiệu","Lúc đầu Đổng Trác tàn sát Lạc Dương để hại ta, Viên Thiệu ta đơn thương độc mã, lập nghiệp từ Bột Hải, hiệu triệu anh hùng thiên hạ thảo phạt Đổng Trác, thống lĩnh chư hầu.","Lúc đầu Đổng Trác tàn sát Lạc Dương để hại ta, Viên Thiệu ta đơn thương độc mã, lập nghiệp từ Bột Hải, hiệu triệu anh hùng thiên hạ thảo phạt Đổng Trác, thống lĩnh chư hầu.","","",},
        [2222] = {2102,"Viên Thiệu","Nay ta đã làm chủ Ký Châu, tiêu diệt Hắc Sơn Hoàng Cân, thống nhất Hà Bắc nằm trong tầm tay, cớ sao phải đón thiên tử để bị sai khiến.","Nay ta đã làm chủ Ký Châu, tiêu diệt Hắc Sơn Hoàng Cân, thống nhất Hà Bắc nằm trong tầm tay, cớ sao phải đón thiên tử để bị sai khiến.","","",},
        [2223] = {2102,"Thư Thụ","Tướng quân, thắng nằm ở chỗ không bỏ lỡ thời cơ, nếu chúng ta không ra tay trước, nhất định sẽ có người làm trước!","Tướng quân, thắng nằm ở chỗ không bỏ lỡ thời cơ, nếu chúng ta không ra tay trước, nhất định sẽ có người làm trước!","","",},
        [2224] = {2102,"Viên Thiệu","Thư Thụ không cần nhiều lời, ý ta đã quyết!","Thư Thụ không cần nhiều lời, ý ta đã quyết!","","",},
        [2225] = {2103,"Tuân Úc","Xưa Tấn Văn Công thu nhận Chu Tương Vương khiến chư hầu phục tùng. Hán Cao Tổ để tang cho Nghĩa Đế khiến thiên hạ một lòng.","Xưa Tấn Văn Công thu nhận Chu Tương Vương khiến chư hầu phục tùng. Hán Cao Tổ để tang cho Nghĩa Đế khiến thiên hạ một lòng.","Tào Tháo","",},
        [2226] = {2103,"Tuân Úc","Nay thiên tử gặp nạn, tướng quân phụng thiên tử để theo nguyện vọng của dân chúng, sách lược rất hay.","Nay thiên tử gặp nạn, tướng quân phụng thiên tử để theo nguyện vọng của dân chúng, sách lược rất hay.","","",},
        [2227] = {2103,"Đổng Chiêu","Nay chư tướng ở Lạc Dương nhiều ý kiến, không nhất thiết phải phục tùng, muốn làm nên chuyện lớn, không những phải đón thiên tử, phải dời đô Hứa Xương mới là thượng sách!","Nay chư tướng ở Lạc Dương nhiều ý kiến, không nhất thiết phải phục tùng, muốn làm nên chuyện lớn, không những phải đón thiên tử, phải dời đô Hứa Xương mới là thượng sách!","","",},
        [2228] = {2103,"Tào Tháo","Theo ý của ta, chuyện không nên chậm trễ, ta sẽ đi bẩm báo thiên tử để đến Hứa Đô.","Theo ý của ta, chuyện không nên chậm trễ, ta sẽ đi bẩm báo thiên tử để đến Hứa Đô.","","",},
        [2229] = {2104,"Tào Tháo","Thần luôn một lòng muốn báo quốc. Nay Giác, Tị tội ác tày trời; thần có hơn 20 vạn tinh binh, dùng để diệt giặc. Bệ hạ hãy giữ gìn long thể, lấy xã tắc làm trọng!","Thần luôn một lòng muốn báo quốc. Nay Giác, Tị tội ác tày trời; thần có hơn 20 vạn tinh binh, dùng để diệt giặc. Bệ hạ hãy giữ gìn long thể, lấy xã tắc làm trọng!","Hán Hiến Đế","",},
        [2230] = {2104,"Hán Hiến Đế","Vất vả cho ái khanh quá!","Vất vả cho ái khanh quá!","","",},
        [2231] = {2104,"Tào Tháo","Bệ hạ, Đông Đô hoang phế đã lâu, không thể sửa chữa, khó vận chuyển lương thực. Hứa Đô thành quách cung thất, tiền của lương thực dồi dào. Thần dám xin dời đến Hứa Đô, mong bệ hạ chấp thuận.","Bệ hạ, Đông Đô hoang phế đã lâu, không thể sửa chữa, khó vận chuyển lương thực. Hứa Đô thành quách cung thất, tiền của lương thực dồi dào. Thần dám xin dời đến Hứa Đô, mong bệ hạ chấp thuận.","","",},
        [2232] = {2104,"Hán Hiến Đế","Cuộc sống khó khăn, nay dời đô nữa e sẽ chê trách.","Cuộc sống khó khăn, nay dời đô nữa e sẽ chê trách.","","",},
        [2233] = {2104,"Tào Tháo","Bệ hạ, chuyện không nên chậm trễ, xin bệ hạ mau dời đến Hứa Đô!","Bệ hạ, chuyện không nên chậm trễ, xin bệ hạ mau dời đến Hứa Đô!","","",},
        [2234] = {2104,"Hán Hiến Đế","Vậy, sẽ theo ý khanh, dời đô đến Hứa Xương!","Vậy, sẽ theo ý khanh, dời đô đến Hứa Xương!","","",},
        [2235] = {2105,"Nhân vật","Ha ha, quả nhiên không ngoài dự đoán của ta, Tào Tháo mới là anh hùng dám khống chế thiên tử!","Ha ha, quả nhiên không ngoài dự đoán của ta, Tào Tháo mới là anh hùng dám khống chế thiên tử!","Ngân Bình","",},
        [2236] = {2105,"Ngân Bình","Viên Thiệu hồ đồ, không nghe lời của Thư Thụ.","Viên Thiệu hồ đồ, không nghe lời của Thư Thụ.","","",},
        [2237] = {2105,"Tinh Thái","Chúa công, Tào Tháo khống chế thiên tử để lệnh cho chư hầu, từ đó đại quyền thuộc về Tào thị, thiên hạ Hán gia đã hữu danh vô thực, đến lúc đó Hiến Đế e rằng...","Chúa công, Tào Tháo khống chế thiên tử để lệnh cho chư hầu, từ đó đại quyền thuộc về Tào thị, thiên hạ Hán gia đã hữu danh vô thực, đến lúc đó Hiến Đế e rằng...","","",},
        [2238] = {2105,"Nhân vật","Tinh Thái lo xa quá! Cục diện hiện giờ, Tào Tháo muốn thời cuộc ổn định, chắc sẽ không hại Hiến Đế, Hiến Đế kiếp này sống đến già cũng không có vấn đề gì.","Tinh Thái lo xa quá! Cục diện hiện giờ, Tào Tháo muốn thời cuộc ổn định, chắc sẽ không hại Hiến Đế, Hiến Đế kiếp này sống đến già cũng không có vấn đề gì.","","",},
        [2239] = {2105,"Tinh Thái","Chúa công nói rất có lý.","Chúa công nói rất có lý.","","",},
        [2240] = {2105,"Nhân vật","Hiến Đế phong ta làm Dương Uy Tướng, ta cũng hộ tống ông ấy về Hứa Đô! Đi, đi Hứa Xương!","Hiến Đế phong ta làm Dương Uy Tướng, ta cũng hộ tống ông ấy về Hứa Đô! Đi, đi Hứa Xương!","","",},
        [2241] = {2106,"Tào Tháo","Từ sau khi Chí Tài mất, chẳng có ai giúp ta tính việc. Vùng Nhữ, Dĩnh có nhiều kẻ sĩ giỏi, ai có thể kế nối được?","Từ sau khi Chí Tài mất, chẳng có ai giúp ta tính việc. Vùng Nhữ, Dĩnh có nhiều kẻ sĩ giỏi, ai có thể kế nối được?","Tuân Úc","",},
        [2242] = {2106,"Tuân Úc","Dĩnh Xuyên Quách Gia mưu lược, là kẻ sĩ giỏi.","Dĩnh Xuyên Quách Gia mưu lược, là kẻ sĩ giỏi.","","",},
        [2243] = {2106,"Nhân vật","Quách Gia tiếng tăm lừng lẫy! Tào huynh, để đệ đi gặp hắn trước đã!","Quách Gia tiếng tăm lừng lẫy! Tào huynh, để đệ đi gặp hắn trước đã!","","",},
        [2244] = {2107,"Tuân Úc","Phụng Hiếu, đây là tiểu anh hùng ta từng nhắc đến, hắn đã dẹp loạn Thập Thường Thị, Phụng Nghi Đình trảm sát Đổng Trác, đánh bại Lý Thôi Quách Tị, hộ tống thiên tử trở về Lạc Dương.","Phụng Hiếu, đây là tiểu anh hùng ta từng nhắc đến, hắn đã dẹp loạn Thập Thường Thị, Phụng Nghi Đình trảm sát Đổng Trác, đánh bại Lý Thôi Quách Tị, hộ tống thiên tử trở về Lạc Dương.","Quách Gia","",},
        [2245] = {2107,"Tuân Úc","Thiên tử đã thông báo trước thiên hạ, phong hắn là Dương Uy Tướng. Hắn lại không thích công danh lợi lộc, chỉ nhận chức, không chịu nhận quân.","Thiên tử đã thông báo trước thiên hạ, phong hắn là Dương Uy Tướng. Hắn lại không thích công danh lợi lộc, chỉ nhận chức, không chịu nhận quân.","","",},
        [2246] = {2107,"Quách Gia","Tiểu anh hùng là Dương Uy Tướng! Ngưỡng mộ đã lâu.","Tiểu anh hùng là Dương Uy Tướng! Ngưỡng mộ đã lâu.","Nhân vật","",},
        [2247] = {2107,"Quách Gia","Tướng quân võ lược, tấm lòng độ lượng, thật là hào kiệt đương thế , thật khâm phục! Phụng Hiếu xin tặng Danh Tướng Lệnh này cho tướng quân!","Tướng quân võ lược, tấm lòng độ lượng, thật là hào kiệt đương thế , thật khâm phục! Phụng Hiếu xin tặng Danh Tướng Lệnh này cho tướng quân!","","",},
        [2248] = {2107,"Nhân vật","Đa tạ, quá khen quá khen, ha ha ha!","Đa tạ, quá khen quá khen, ha ha ha!","","",},
        [2249] = {35,"Ngân Bình","Tinh Thái, ta biết rất nhiều bí mật của tam quốc, ngươi muốn biết không?","Tinh Thái, ta biết rất nhiều bí mật của tam quốc, ngươi muốn biết không?","Tinh Thái","",},
        [2250] = {35,"Tinh Thái","Muốn! Mau nói ta biết đi, có những bí mật gì?","Muốn! Mau nói ta biết đi, có những bí mật gì?","","",},
        [2251] = {35,"Ngân Bình","Như là, Tào Tháo khi trẻ từng cùng Viên Thiệu đi cướp tân nương, Từ Thứ khi trẻ thích luyện kiếm hành hiệp trượng nghĩa, Viên Thuật là cha của tất cả mọi người...","Như là, Tào Tháo khi trẻ từng cùng Viên Thiệu đi cướp tân nương, Từ Thứ khi trẻ thích luyện kiếm hành hiệp trượng nghĩa, Viên Thuật là cha của tất cả mọi người...","","",},
        [2252] = {35,"Tinh Thái","Ha ha, thì ra các danh tướng đểu có câu chuyện hay như vậy, đi đâu để xem được những câu chuyện này?","Ha ha, thì ra các danh tướng đểu có câu chuyện hay như vậy, đi đâu để xem được những câu chuyện này?","","",},
        [2253] = {35,"Ngân Bình","Trong phó bản Danh Tướng! Khiêu chiến phó bản Danh Tướng, có thể kết bạn nhiều danh tướng hơn, tìm hiểu câu chuyện đằng sau danh tướng.","Trong phó bản Danh Tướng! Khiêu chiến phó bản Danh Tướng, có thể kết bạn nhiều danh tướng hơn, tìm hiểu câu chuyện đằng sau danh tướng.","","",},
        [2254] = {35,"Tinh Thái","Chúa công, vậy chúng ta mau đi khiêu chiến phó bản Danh Tướng đầu tiên đi.","Chúa công, vậy chúng ta mau đi khiêu chiến phó bản Danh Tướng đầu tiên đi.","","",},
        [2255] = {36,"Ngân Bình","Chúa công, lần đầu vượt ải phó bản Danh Tướng, được nhận nhiều Nguyên Bảo thưởng.","Chúa công, lần đầu vượt ải phó bản Danh Tướng, được nhận nhiều Nguyên Bảo thưởng.","Tinh Thái","",},
        [2256] = {36,"Tinh Thái","Khiêu chiến phó bản Danh Tướng còn được nhận Tướng Hồn, Tướng Hồn có thể đến Tiệm Võ Tướng đổi Tướng phẩm chất cao!","Khiêu chiến phó bản Danh Tướng còn được nhận Tướng Hồn, Tướng Hồn có thể đến Tiệm Võ Tướng đổi Tướng phẩm chất cao!","","",},
