        [2258] = {37,"Nhân vật","Tinh Thái, ta lấy được nhiều Tướng Lam và Tướng Lục, phẩm chất thấp quá hình như không làm gì được!","Tinh Thái, ta lấy được nhiều Tướng Lam và Tướng Lục, phẩm chất thấp quá hình như không làm gì được!","Tinh Thái","",},
        [2259] = {37,"Tinh Thái","Chúa công, Tướng Lục trở lên có thể #thu hồi#, thu hồi được nhận tài nguyên rất có ích!","Chúa công, Tướng Lục trở lên có thể #thu hồi#, thu hồi được nhận tài nguyên rất có ích!","","",},
