        [2274] = {40,"Tinh Thái","Ồ, chúc mừng chúa công tăng đến Lv58! Lv58 có thể đeo #Cẩm Nang# rồi!","Ồ, chúc mừng chúa công tăng đến Lv58! Lv58 có thể đeo #Cẩm Nang# rồi!","Nhân vật","",},
