        [2280] = {41,"Tinh Thái","Lợi hại thật! Sau khi đeo Cẩm Nang Ngân Bình, tấn công của chúa công tăng 1000 điểm!","Lợi hại thật! Sau khi đeo Cẩm Nang Ngân Bình, tấn công của chúa công tăng 1000 điểm!","Nhân vật","",},
        [2281] = {41,"Nhân vật","Đây không chỉ là Cẩm Nang Tướng Tím, Cẩm Nang Tướng Cam chẳng phải lợi hại hơn sao?","Đây không chỉ là Cẩm Nang Tướng Tím, Cẩm Nang Tướng Cam chẳng phải lợi hại hơn sao?","","",},
