        [2387] = {2405,"Nhân vật","Tam ca, huynh hiểu lầm rồi. Ta đến giúp huynh và đại ca, nhị ca, đây chỉ là kế hoãn binh.","Tam ca, huynh hiểu lầm rồi. Ta đến giúp huynh và đại ca, nhị ca, đây chỉ là kế hoãn binh.","Trương Phi","",},
        [2388] = {2405,"Nhân vật","Bên cạnh Lữ Bố có Trần Cung, Trần Cung luôn muốn gây bất lợi cho đại ca, nhân lúc Lữ Bố chuẩn bị binh lính cần thời gian, ta sẽ bảo vệ mọi ngươi ra khỏi thành, Tiểu Bái không cần cũng được!","Bên cạnh Lữ Bố có Trần Cung, Trần Cung luôn muốn gây bất lợi cho đại ca, nhân lúc Lữ Bố chuẩn bị binh lính cần thời gian, ta sẽ bảo vệ mọi ngươi ra khỏi thành, Tiểu Bái không cần cũng được!","","",},
        [2389] = {2405,"Trương Phi","Thì ra thế, tam ca là người thô lỗ, xin thứ tội.","Thì ra thế, tam ca là người thô lỗ, xin thứ tội.","","",},
        [2390] = {2405,"Tinh Thái","Cha, người cứ thích uống rượu, uống rượu xong lại gây chuyện, suýt chút đã hại mọi người mất mạng, sau này tuyệt đối không được uống rượu.","Cha, người cứ thích uống rượu, uống rượu xong lại gây chuyện, suýt chút đã hại mọi người mất mạng, sau này tuyệt đối không được uống rượu.","","",},
        [2391] = {2405,"Nhân vật","Hãy bỏ thành đi Hứa Đô, đầu hàng Tào Tháo.","Hãy bỏ thành đi Hứa Đô, đầu hàng Tào Tháo.","","",},
        [2392] = {2405,"Nhân vật","Tào Tháo hận Lữ Bố. Lữ Bố trước đây từng tấn công Duyện Châu, suýt chút đã diệt Tào Tháo.","Tào Tháo hận Lữ Bố. Lữ Bố trước đây từng tấn công Duyện Châu, suýt chút đã diệt Tào Tháo.","","",},
