        [2480] = {2704,"Nhân vật","Trước được Hoa Thần Y tặng Ngũ Cầm Hí, nay lại được Danh Tướng Lệnh, thật cảm ơn!","Trước được Hoa Thần Y tặng Ngũ Cầm Hí, nay lại được Danh Tướng Lệnh, thật cảm ơn!","","",},
        [2481] = {2704,"Hoa Đà","Tướng quân là người kết thúc thời buổi loạn lạc này, mong ngài hãy rèn luyện Ngũ Cầm Hí, giữ gìn sức khỏe!","Tướng quân là người kết thúc thời buổi loạn lạc này, mong ngài hãy rèn luyện Ngũ Cầm Hí, giữ gìn sức khỏe!","","",},
        [2482] = {2704,"Nhân vật","Xin hỏi, sao trông Hoa Thần Y trẻ vậy, là do luyện Ngũ Cầm Hí sao?","Xin hỏi, sao trông Hoa Thần Y trẻ vậy, là do luyện Ngũ Cầm Hí sao?","","",},
