        [2487] = {2704,"Nhân vật","He he! Được thôi! Nghe nói giặc Hoàng Cân xuất hiện tại Trần Lưu, chúng ta đi thôi!","He he! Được thôi! Nghe nói giặc Hoàng Cân xuất hiện tại Trần Lưu, chúng ta đi thôi!","","",},
        [2488] = {2801,"Nhân vật","Trần Lưu vốn là nơi phồn hoa, sao nay chỉ toàn đói rách thế này!","Trần Lưu vốn là nơi phồn hoa, sao nay chỉ toàn đói rách thế này!","Tinh Thái","",},
        [2489] = {2801,"Tinh Thái","Chúa công, hai năm nay nơi đây gặp nạn châu chấu, mùa màng mất sạch, lương thực khan hiếm, thê thảm lắm.","Chúa công, hai năm nay nơi đây gặp nạn châu chấu, mùa màng mất sạch, lương thực khan hiếm, thê thảm lắm.","Nhân vật","",},
        [2490] = {2801,"Nhân vật","Đây là phạm vi thế lực Tào Tháo, chúng ta đi gặp Tào Tháo xin ít lương thực cứu tế bá tánh.","Đây là phạm vi thế lực Tào Tháo, chúng ta đi gặp Tào Tháo xin ít lương thực cứu tế bá tánh.","Tinh Thái","",},
        [2491] = {2802,"Nhân vật","Tào huynh, ta một đường từ Giang Đông đến Trần Lưu, thấy cảnh dân chúng lầm than, sao huynh không cứu giúp?","Tào huynh, ta một đường từ Giang Đông đến Trần Lưu, thấy cảnh dân chúng lầm than, sao huynh không cứu giúp?","Tào Tháo","",},
        [2492] = {2802,"Tào Tháo","Tiểu huynh đệ không biết đó thôi, gần đây nạn châu chấu hoành hành, trong quân ta không có nhiều lương thực, huống chi vừa giao chiến với Lữ Bố, ta đang lo lắm đây?","Tiểu huynh đệ không biết đó thôi, gần đây nạn châu chấu hoành hành, trong quân ta không có nhiều lương thực, huống chi vừa giao chiến với Lữ Bố, ta đang lo lắm đây?","Nhân vật","",},
        [2493] = {2802,"Tuân Úc","Hai vị, tàn dư Hoàng Cân là Hà Nghi, Hoàng Thiệu, cướp bóc châu quận, có nhiều vàng lụa, lương thực,","Hai vị, tàn dư Hoàng Cân là Hà Nghi, Hoàng Thiệu, cướp bóc châu quận, có nhiều vàng lụa, lương thực,","Nhân vật","",},
        [2494] = {2802,"Tuân Úc","Bọn giặc này rất dễ phá; phá xong lấy lương thực của chúng để nuôi ba quân, triều đình vui, trăm họ mừng, là việc thuận theo trời.","Bọn giặc này rất dễ phá; phá xong lấy lương thực của chúng để nuôi ba quân, triều đình vui, trăm họ mừng, là việc thuận theo trời.","Nhân vật","",},
        [2495] = {2802,"Nhân vật","Ý hay lắm, Tào huynh, diệt Hoàng Cân, ta làm tiên phong, ta chờ tin huynh thắng trận quay về.","Ý hay lắm, Tào huynh, diệt Hoàng Cân, ta làm tiên phong, ta chờ tin huynh thắng trận quay về.","Tuân Úc","",},
        [2496] = {2803,"Hoàng Thiệu","Ta là Hoàng Thiệu! Ngươi là ai?","Ta là Hoàng Thiệu! Ngươi là ai?","Ngân Bình","",},
        [2497] = {2803,"Ngân Bình","Chúa công ta là Dương Uy Tướng được đương kim thiên tử sắc phong, bọn giặc Hoàng Cân, còn không bó tay chịu trói!","Chúa công ta là Dương Uy Tướng được đương kim thiên tử sắc phong, bọn giặc Hoàng Cân, còn không bó tay chịu trói!","Hoàng Thiệu","",},
        [2498] = {2803,"Hoàng Thiệu","Dương Uy Tướng gì, chưa nghe bao giờ! Mặc kệ ngươi là ai, dám xông vào Hoàng Cân Thiên Quân ta, hôm nay ta sẽ đánh cho các ngươi thua thê thảm!","Dương Uy Tướng gì, chưa nghe bao giờ! Mặc kệ ngươi là ai, dám xông vào Hoàng Cân Thiên Quân ta, hôm nay ta sẽ đánh cho các ngươi thua thê thảm!","Ngân Bình","",},
        [2499] = {2804,"Hoàng Thiệu","Tiểu nhân có mắt mà không biết Dương Uy Tướng, tướng quân tha mạng!","Tiểu nhân có mắt mà không biết Dương Uy Tướng, tướng quân tha mạng!","Ngân Bình","",},
        [2500] = {2804,"Ngân Bình","Bây giờ lại xin chúa công ta tha mạng! Hừ!","Bây giờ lại xin chúa công ta tha mạng! Hừ!","Hoàng Thiệu","",},
        [2501] = {2804,"Nhân vật","Được, không lấy mạng ngươi cũng được, hãy giao vàng bạc lương thực trong trại ra đây thì ta sẽ tha cho.","Được, không lấy mạng ngươi cũng được, hãy giao vàng bạc lương thực trong trại ra đây thì ta sẽ tha cho.","Hoàng Thiệu","",},
        [2502] = {2804,"Hoàng Thiệu","Không giấu gì tướng quân, trong trại chúng tôi có một đại hán, tướng quân muốn lấy lương thực vàng bạc thì phải thắng hắn.","Không giấu gì tướng quân, trong trại chúng tôi có một đại hán, tướng quân muốn lấy lương thực vàng bạc thì phải thắng hắn.","Nhân vật","",},
        [2503] = {2804,"Ngân Bình","Kẻ nào to gan dám cướp đồ của chúa công ta. Đi, đi xem xem!","Kẻ nào to gan dám cướp đồ của chúa công ta. Đi, đi xem xem!","Hoàng Thiệu","",},
        [2504] = {2805,"Ngân Bình","Chúa công nhìn đi, gã béo này đã cướp đồ của chúng ta.","Chúa công nhìn đi, gã béo này đã cướp đồ của chúng ta.","Nhân vật","",},
        [2505] = {2805,"Nhân vật","Vị tráng sĩ này cao to vạm vỡ, trông rất quen.","Vị tráng sĩ này cao to vạm vỡ, trông rất quen.","Ngân Bình","",},
        [2506] = {2805,"Ngân Bình","Chúa công thật quá khen, hắn béo thế kia mà. Này tên béo, mau giao Giặc Hoàng Cân ra đây!","Chúa công thật quá khen, hắn béo thế kia mà. Này tên béo, mau giao Giặc Hoàng Cân ra đây!","Nhân vật","",},
        [2507] = {2805,"Hứa Chử","Hừ hừ, các ngươi thắng được Lưu Tinh Chùy 180 cân trong tay ta đi đã!","Hừ hừ, các ngươi thắng được Lưu Tinh Chùy 180 cân trong tay ta đi đã!","Nhân vật","",},
        [2508] = {2805,"Nhân vật","Ngân Bình, ngươi đứng sau ta, đừng để Lưu Tinh Chùy của hắn làm bị thương!","Ngân Bình, ngươi đứng sau ta, đừng để Lưu Tinh Chùy của hắn làm bị thương!","Hứa Chử","",},
        [2509] = {2806,"Hứa Chử","Hứa Chử thua tâm phục khẩu phục, Danh Tướng Lệnh này xin trao cho tướng quân! Hứa Chử xin được theo tướng quân!","Hứa Chử thua tâm phục khẩu phục, Danh Tướng Lệnh này xin trao cho tướng quân! Hứa Chử xin được theo tướng quân!","Nhân vật","",},
