        [2511] = {2806,"Nhân vật","Tào huynh, ta tiến cử cho huynh 1 đại tướng, đây là Hứa Chử, võ nghệ ngang với Điển Vi.","Tào huynh, ta tiến cử cho huynh 1 đại tướng, đây là Hứa Chử, võ nghệ ngang với Điển Vi.","Tào Tháo","",},
        [2512] = {2806,"Tào Tháo","Ha ha, Tháo may mắn có được 2 hổ vệ, tả Điển Vi, hữu Hứa Chử, e là trên đời này không còn ai hại được ta!","Ha ha, Tháo may mắn có được 2 hổ vệ, tả Điển Vi, hữu Hứa Chử, e là trên đời này không còn ai hại được ta!","Nhân vật","",},
        [2513] = {2901,"Tào Tháo","Tiểu anh hùng, Trương Tú dùng Giả Hủ làm mưu sĩ, liên minh với Lưu Biểu, đóng quân ở Uyển Thành, ngươi thấy sao về chuyện này?","Tiểu anh hùng, Trương Tú dùng Giả Hủ làm mưu sĩ, liên minh với Lưu Biểu, đóng quân ở Uyển Thành, ngươi thấy sao về chuyện này?","Nhân vật","",},
        [2514] = {2901,"Nhân vật","Tào huynh, ta thấy hãy phái người khuyên hàng Trương Tú. Ta từng tiếp xúc Giả Hủ, rất mưu lược, lại còn biết thức thời. Ta tới giúp Tào huynh khuyên hàng, không mất sức mà vẫn lấy được Uyển Thành.","Tào huynh, ta thấy hãy phái người khuyên hàng Trương Tú. Ta từng tiếp xúc Giả Hủ, rất mưu lược, lại còn biết thức thời. Ta tới giúp Tào huynh khuyên hàng, không mất sức mà vẫn lấy được Uyển Thành.","Tào Tháo","",},
        [2515] = {2901,"Tào Tháo","Vậy thì tốt quá, xin đa tạ tiểu anh hùng!","Vậy thì tốt quá, xin đa tạ tiểu anh hùng!","Nhân vật","",},
        [2516] = {2902,"Tào Tháo","Phu nhân tên họ là gì?","Phu nhân tên họ là gì?","Trâu Thị","",},
        [2517] = {2902,"Trâu Thị","Thiếp là nương tử Trương Tế tên Trâu Thị.","Thiếp là nương tử Trương Tế tên Trâu Thị.","Tào Tháo","",},
        [2518] = {2902,"Tào Tháo","Phu nhân có biết ta không?","Phu nhân có biết ta không?","Trâu Thị","",},
        [2519] = {2902,"Trâu Thị","Nghe danh thừa tướng đã lâu, hôm nay may mắn được gặp.","Nghe danh thừa tướng đã lâu, hôm nay may mắn được gặp.","Tào Tháo","",},
        [2520] = {2902,"Tào Tháo","Ta vì phu nhân mà nạp Trương Tú đầu hàng, giúp hạn không bị diệt tộc.","Ta vì phu nhân mà nạp Trương Tú đầu hàng, giúp hạn không bị diệt tộc.","Trâu Thị","",},
        [2521] = {2902,"Tào Tháo","Hôm nay được gặp phu nhân quả vinh hạnh. Nàng có muốn cùng ta về kinh đô, cùng hưởng phú quý?","Hôm nay được gặp phu nhân quả vinh hạnh. Nàng có muốn cùng ta về kinh đô, cùng hưởng phú quý?","Trâu Thị","",},
        [2522] = {2902,"Trâu Thị","Thiếp nguyện theo thừa tướng.","Thiếp nguyện theo thừa tướng.","Tào Tháo","",},
        [2523] = {2903,"Trương Tú","Tháo lại sỉ nhục thím ta! Phải trảm Tào Tháo thì mới rửa được nỗi hận trong lòng ta!","Tháo lại sỉ nhục thím ta! Phải trảm Tào Tháo thì mới rửa được nỗi hận trong lòng ta!","Giả Hủ","",},
        [2524] = {2903,"Giả Hủ","Chuyện này không được tiết lộ. Ngày mai chờ Tháo ra trướng bàn việc, ngươi có thể nói là binh lính đầu hàng có nhiều người bỏ trốn, xin đưa đồn vào trung quân.","Chuyện này không được tiết lộ. Ngày mai chờ Tháo ra trướng bàn việc, ngươi có thể nói là binh lính đầu hàng có nhiều người bỏ trốn, xin đưa đồn vào trung quân.","Trương Tú","",},
        [2525] = {2903,"Trương Tú","Nhưng Điển Vi, thủ hạ của Tào Tháo rất dũng mãnh, phải ứng phó sao đây?","Nhưng Điển Vi, thủ hạ của Tào Tháo rất dũng mãnh, phải ứng phó sao đây?","Giả Hủ","",},
        [2526] = {2903,"Giả Hủ","Ngày mai hãy mời hắn uống rượu đến say mới về. Lúc đó cho người lẻn vào trướng, trộm kích của hắn.","Ngày mai hãy mời hắn uống rượu đến say mới về. Lúc đó cho người lẻn vào trướng, trộm kích của hắn.","Trương Tú","",},
        [2527] = {2903,"Trương Tú","Đúng là diệu kế! Ta phải diệt Tào Tháo","Đúng là diệu kế! Ta phải diệt Tào Tháo","Giả Hủ","",},
        [2528] = {2904,"Nhân vật","Lạ thật, trong thành sao hỗn loạn như vậy? Trương Tú chẳng phải đã đầu hàng Tào Tháo rồi sao?","Lạ thật, trong thành sao hỗn loạn như vậy? Trương Tú chẳng phải đã đầu hàng Tào Tháo rồi sao?","Ngân Bình","",},
        [2529] = {2904,"Ngân Bình","Chúa công, không hay rồi, Trương Tú vốn dĩ đã đầu hàng, Tào Tháo sau khi vào Uyển Thành đã vui thú với thím của Trương Tú là Trâu Thị, Trương Tú biết chuyện đã lên kế hoạch đối phó với Tào Tháo.","Chúa công, không hay rồi, Trương Tú vốn dĩ đã đầu hàng, Tào Tháo sau khi vào Uyển Thành đã vui thú với thím của Trương Tú là Trâu Thị, Trương Tú biết chuyện đã lên kế hoạch đối phó với Tào Tháo.","Nhân vật","",},
        [2530] = {2904,"Nhân vật","Vậy chúng ta mau đi cứu Tào Tháo.","Vậy chúng ta mau đi cứu Tào Tháo.","Ngân Bình","",},
        [2531] = {2905,"Tinh Thái","Chúng ta đã đến chậm một bước, Điển Vi vì bảo vệ Tào Tháo mà bị trọng thương.","Chúng ta đã đến chậm một bước, Điển Vi vì bảo vệ Tào Tháo mà bị trọng thương.","Nhân vật","",},
        [2532] = {2905,"Nhân vật","Tinh Thái, ngươi mau xem giúp Điển Vi.","Tinh Thái, ngươi mau xem giúp Điển Vi.","Tinh Thái","",},
        [2533] = {2905,"Tinh Thái","Chúa công, Tinh Thái không cứu được, Điển tướng quân bị thương quá nặng!","Chúa công, Tinh Thái không cứu được, Điển tướng quân bị thương quá nặng!","Nhân vật","",},
        [2534] = {2905,"Điển Vi","Ta chặn cổng trại, thừa tướng cưỡi ngựa chạy từ sau trại, nhưng truy binh quá nhiều, rất nguy hiểm.","Ta chặn cổng trại, thừa tướng cưỡi ngựa chạy từ sau trại, nhưng truy binh quá nhiều, rất nguy hiểm.","Nhân vật","",},
        [2535] = {2905,"Điển Vi","Dương Uy Tướng, ngươi và thừa tướng xưa nay thân thiết! Ngươi hãy mau đến cứu thừa tướng!","Dương Uy Tướng, ngươi và thừa tướng xưa nay thân thiết! Ngươi hãy mau đến cứu thừa tướng!","Nhân vật","",},
        [2536] = {2905,"Nhân vật","Điển tướng quân yên tâm, ta sẽ cứu Tào Tháo!","Điển tướng quân yên tâm, ta sẽ cứu Tào Tháo!","Điển Vi","",},
        [2537] = {2905,"Điển Vi","Chúa công, chạy mau... (Điển Vi thì thào, rồi lìa đời)","Chúa công, chạy mau... (Điển Vi thì thào, rồi lìa đời)","Nhân vật","",},
        [2538] = {3001,"Tinh Thái","Chúa công, chúng ta đuổi theo mà sao không thấy Tào Tháo, không lẽ ông ta không đi đường này?","Chúa công, chúng ta đuổi theo mà sao không thấy Tào Tháo, không lẽ ông ta không đi đường này?","Nhân vật","",},
        [2539] = {3001,"Nhân vật","Có lẽ Tào Tháo cưỡi ngựa chạy lung tung, không biết chạy về bên nào. Chớ lo, người phía trước hình như là Vu Cấm, thủ hạ của Tào Tháo.","Có lẽ Tào Tháo cưỡi ngựa chạy lung tung, không biết chạy về bên nào. Chớ lo, người phía trước hình như là Vu Cấm, thủ hạ của Tào Tháo.","Tinh Thái","",},
        [2540] = {3001,"Vu Cấm","Dương Uy Tướng, nghe nói Uyển Thành lại loạn, ngươi có thấy tướng quân không?","Dương Uy Tướng, nghe nói Uyển Thành lại loạn, ngươi có thấy tướng quân không?","Nhân vật","",},
        [2541] = {3001,"Nhân vật","Ta vừa ra khỏi Uyển Thành, thì Trương Tú làm phản, Điển Vi tướng quân đã tử trận, thừa tướng không biết chạy đi đâu rồi.","Ta vừa ra khỏi Uyển Thành, thì Trương Tú làm phản, Điển Vi tướng quân đã tử trận, thừa tướng không biết chạy đi đâu rồi.","Vu Cấm","",},
        [2542] = {3001,"Vu Cấm","Lại có chuyện này sao! Ta sẽ về Duyện Châu điều quân đến cứu viện!","Lại có chuyện này sao! Ta sẽ về Duyện Châu điều quân đến cứu viện!","Nhân vật","",},
        [2543] = {3001,"Nhân vật","Trương Tú người đông thế mạnh, như vậy thật tốt, chúng ta hãy chia ra hai hướng, ta tiếp tục tìm thừa tướng!","Trương Tú người đông thế mạnh, như vậy thật tốt, chúng ta hãy chia ra hai hướng, ta tiếp tục tìm thừa tướng!","Vu Cấm","",},
        [2544] = {3002,"Tào Ngang","Phụ thân, ngựa của phụ thân đã trúng 3 tên, nay mắt ngựa lại trúng 1 tên, không thể chạy được nữa. Phụ thân, hãy mau cưỡi ngựa của con đi đi!","Phụ thân, ngựa của phụ thân đã trúng 3 tên, nay mắt ngựa lại trúng 1 tên, không thể chạy được nữa. Phụ thân, hãy mau cưỡi ngựa của con đi đi!","Tào Tháo","",},
        [2545] = {3002,"Tào Tháo","Ta cưỡi ngựa của con, còn con làm thế nào?","Ta cưỡi ngựa của con, còn con làm thế nào?","Tào Ngang","",},
        [2546] = {3002,"Tào Ngang","Phụ thân cứ yên tâm, con võ nghệ cao cường, sẽ ở lại chặn hậu! (Nói xong vỗ mông ngựa, ngựa chở Tào Tháo phóng đi, phía sau tên bắn như mưa, Tào Ngang ra sức chiến đấu)","Phụ thân cứ yên tâm, con võ nghệ cao cường, sẽ ở lại chặn hậu! (Nói xong vỗ mông ngựa, ngựa chở Tào Tháo phóng đi, phía sau tên bắn như mưa, Tào Ngang ra sức chiến đấu)","Tào Tháo","",},
        [2547] = {3003,"Đinh Phu Nhân","(Đinh phu nhân nghe nói Tào Ngang tử trận, tức giận về quê nhà) Tào Tháo, ngài đã hại con trai ta, giờ đây ta không còn gì để lưu luyến nữa!","(Đinh phu nhân nghe nói Tào Ngang tử trận, tức giận về quê nhà) Tào Tháo, ngài đã hại con trai ta, giờ đây ta không còn gì để lưu luyến nữa!","Tào Tháo","",},
        [2548] = {3003,"Tào Tháo","Phu nhân, chúng ta cùng ngồi xe về nhà, được không?","Phu nhân, chúng ta cùng ngồi xe về nhà, được không?","Đinh Phu Nhân","",},
        [2549] = {3003,"Đinh Phu Nhân","Không!","Không!","Tào Tháo","",},
        [2550] = {3003,"Tào Tháo","Phu nhân, về với ta được không?","Phu nhân, về với ta được không?","Đinh Phu Nhân","",},
        [2551] = {3003,"Đinh Phu Nhân","Không!","Không!","Tào Tháo","",},
        [2552] = {3003,"Tào Tháo","Cả đời ta làm chuyện gì cũng không thấy hối hận. Nếu qua đời mà vẫn còn linh hồn, nếu Tào Ngang hỏi ta mẫu thân nó ở đâu thì ta trả lời sao đây!","Cả đời ta làm chuyện gì cũng không thấy hối hận. Nếu qua đời mà vẫn còn linh hồn, nếu Tào Ngang hỏi ta mẫu thân nó ở đâu thì ta trả lời sao đây!","Đinh Phu Nhân","",},
        [2553] = {3004,"Hạ Hầu Đôn","Dương Uy Tướng, ta kính trọng ngươi và thừa tướng, nhưng sao ngươi lại tiêu diệt quân Thanh Châu của ta?","Dương Uy Tướng, ta kính trọng ngươi và thừa tướng, nhưng sao ngươi lại tiêu diệt quân Thanh Châu của ta?","Nhân vật","",},
        [2554] = {3004,"Nhân vật","Hạ Hầu tướng quân, lại dám hỏi lại ta sao? Quân Thanh Châu của ngươi lúc đầu là Giặc Hoàng Cân mà ta thu phục cho thừa tướng.","Hạ Hầu tướng quân, lại dám hỏi lại ta sao? Quân Thanh Châu của ngươi lúc đầu là Giặc Hoàng Cân mà ta thu phục cho thừa tướng.","Hạ Hầu Đôn","",},
        [2555] = {3004,"Nhân vật","Ta từ Uyển Thành đến ứng cứu thừa tướng, dọc đường lại thấy quân Thanh Châu cướp bóc nhà dân, nên ta diệt! Sao, không phục à?","Ta từ Uyển Thành đến ứng cứu thừa tướng, dọc đường lại thấy quân Thanh Châu cướp bóc nhà dân, nên ta diệt! Sao, không phục à?","Hạ Hầu Đôn","",},
        [2556] = {3004,"Hạ Hầu Đôn","Hạ Hầu Đôn lỗ mãng! Có chuyện thế này là tại Hạ Hầu Đôn trị quân không nghiêm!","Hạ Hầu Đôn lỗ mãng! Có chuyện thế này là tại Hạ Hầu Đôn trị quân không nghiêm!","Nhân vật","",},
        [2557] = {3005,"Tào Tháo","Vu Cấm, quân Thanh Châu nói ngươi thừa chuyện ở Uyển Thành mà làm phản, ngươi có biết tội không!","Vu Cấm, quân Thanh Châu nói ngươi thừa chuyện ở Uyển Thành mà làm phản, ngươi có biết tội không!","Vu Cấm","",},
        [2558] = {3005,"Nhân vật","Tào huynh, chuyện này oan cho đệ quá!","Tào huynh, chuyện này oan cho đệ quá!","Tào Tháo","",},
        [2559] = {3005,"Tào Tháo","Hả?","Hả?","Nhân vật","",},
        [2560] = {3005,"Nhân vật","Quân Thanh Châu đó vốn là giặc Hoàng Cân, chắc huynh còn nhớ. Trong số họ có vài người thừa lúc hỗn loạn cướp bóc nhà dân, ta và Vu Cấm tướng quân nhìn thấy, đã dạy cho chúng một bài học.","Quân Thanh Châu đó vốn là giặc Hoàng Cân, chắc huynh còn nhớ. Trong số họ có vài người thừa lúc hỗn loạn cướp bóc nhà dân, ta và Vu Cấm tướng quân nhìn thấy, đã dạy cho chúng một bài học.","Tào Tháo","",},
        [2561] = {3005,"Tào Tháo","Thì ra là vậy. Vu Cấm, suýt nữa đã trách lầm ngươi, sao ngươi không báo cho ta biết sớm?","Thì ra là vậy. Vu Cấm, suýt nữa đã trách lầm ngươi, sao ngươi không báo cho ta biết sớm?","Vu Cấm","",},
        [2562] = {3005,"Vu Cấm","Lúc đó tình hình khẩn cấp, kẻ địch đuổi theo phía sau, ta chống cự giặc, Tào công là người sáng suốt, chắc sẽ không tin vào lời đồn nhảm.","Lúc đó tình hình khẩn cấp, kẻ địch đuổi theo phía sau, ta chống cự giặc, Tào công là người sáng suốt, chắc sẽ không tin vào lời đồn nhảm.","Tào Tháo","",},
        [2563] = {3005,"Tào Tháo","Tướng quân trong lúc bối rối, thế mà nghiêm được binh, bền được lũy, mặc người gièm chê, làm cho chuyển bại thành thắng, dù danh tướng đời xưa, vị tất đã có ai hơn!","Tướng quân trong lúc bối rối, thế mà nghiêm được binh, bền được lũy, mặc người gièm chê, làm cho chuyển bại thành thắng, dù danh tướng đời xưa, vị tất đã có ai hơn!","Nhân vật","",},
        [2564] = {3005,"Nhân vật","Tào huynh anh minh!","Tào huynh anh minh!","Tào Tháo","",},
        [2565] = {3005,"Tào Tháo","Trận Uyển Thành, ta mất con trưởng và cháu yêu, cũng không thương là mấy, chỉ thương khóc Điển Vi mà thôi!","Trận Uyển Thành, ta mất con trưởng và cháu yêu, cũng không thương là mấy, chỉ thương khóc Điển Vi mà thôi!","Nhân vật","",},
        [2566] = {3101,"Viên Thuật","Xưa Hán Cao Tổ chỉ là một đình trưởng ở Tứ Thượng, thế mà lấy được thiên hạ; Đã qua 400 năm, khí số đã hết, khắp nơi loạn tứ tung như vạc sôi.","Xưa Hán Cao Tổ chỉ là một đình trưởng ở Tứ Thượng, thế mà lấy được thiên hạ; Đã qua 400 năm, khí số đã hết, khắp nơi loạn tứ tung như vạc sôi.","Các Tượng","",},
        [2567] = {3101,"Viên Thuật","Nhà ta 4 đời làm đến tam công, trăm họ ai cũng trông ngóng; ta muốn ứng vận trời, thuận lòng người, lên ngôi cửu ngũ, các người nghĩ thế nào?","Nhà ta 4 đời làm đến tam công, trăm họ ai cũng trông ngóng; ta muốn ứng vận trời, thuận lòng người, lên ngôi cửu ngũ, các người nghĩ thế nào?","Các Tượng","",},
        [2568] = {3101,"Các Tượng","Nay minh công gia thế tuy rằng quý, nhưng chưa được thịnh bằng nhà Chu; Nhà Hán tuy rằng suy, nhưng chưa đến nỗi tàn bạo như vua Trụ nhà Ân. Việc ấy quyết không nền làm.","Nay minh công gia thế tuy rằng quý, nhưng chưa được thịnh bằng nhà Chu; Nhà Hán tuy rằng suy, nhưng chưa đến nỗi tàn bạo như vua Trụ nhà Ân. Việc ấy quyết không nền làm.","Viên Thuật","",},
        [2569] = {3101,"Viên Thuật","Ta họ Viên do từ họ Trần mà ra, thuộc về hành Thổ, lấy Thổ kế vào Hòa chính ứng vận trời. Có câu sấm: Thay nhà Hán ấy là chỗ cao ở giữa đường. Tên tự ta là Công Lộ, thế là chính ứng câu sấm ấy.","Ta họ Viên do từ họ Trần mà ra, thuộc về hành Thổ, lấy Thổ kế vào Hòa chính ứng vận trời. Có câu sấm: Thay nhà Hán ấy là chỗ cao ở giữa đường. Tên tự ta là Công Lộ, thế là chính ứng câu sấm ấy.","Các Tượng","",},
        [2570] = {3101,"Viên Thuật","Lại có Ngọc Tỉ truyền quốc, nếu ta không làm vua, thì hóa ra trái đạo trời đất. Ý ta đã quyết, ai còn can nữa ta trảm!","Lại có Ngọc Tỉ truyền quốc, nếu ta không làm vua, thì hóa ra trái đạo trời đất. Ý ta đã quyết, ai còn can nữa ta trảm!","Các Tượng","",},
        [2571] = {3102,"Tinh Thái","Chúa công, nghe nói Viên Thuật to gan làm càn, muốn xưng đế ở Thọ Xuyến!","Chúa công, nghe nói Viên Thuật to gan làm càn, muốn xưng đế ở Thọ Xuyến!","Nhân vật","",},
        [2572] = {3102,"Ngân Bình","Viên Thuật xưa nay lòng dạ bất chính, lại muốn xưng đế! Chúa công nhà ta là Dương Uy Tướng được thiên tử Đại Hán phong cho!","Viên Thuật xưa nay lòng dạ bất chính, lại muốn xưng đế! Chúa công nhà ta là Dương Uy Tướng được thiên tử Đại Hán phong cho!","Nhân vật","",},
        [2573] = {3102,"Nhân vật","Viên Thuật mà trở thành hoàng đế, ta sẽ trèo lên thành Lạc Dương 3 vòng! Hừ!","Viên Thuật mà trở thành hoàng đế, ta sẽ trèo lên thành Lạc Dương 3 vòng! Hừ!","Ngân Bình","",},
        [2574] = {3102,"Tinh Thái","Chúa công, giờ trời đang lạnh, ngài trèo lên thành Lạc Dương sẽ bị cóng đấy!","Chúa công, giờ trời đang lạnh, ngài trèo lên thành Lạc Dương sẽ bị cóng đấy!","Nhân vật","",},
        [2575] = {3102,"Ngân Bình","Tinh Thái, câu đùa của ngươi nhạt quá!","Tinh Thái, câu đùa của ngươi nhạt quá!","Nhân vật","",},
        [2576] = {3102,"Nhân vật","(Lúng túng cười) Đi đi đi, tìm người cùng đánh Viên Thuật!","(Lúng túng cười) Đi đi đi, tìm người cùng đánh Viên Thuật!","Ngân Bình","",},
        [2577] = {3103,"Nhân vật","Viên Thuật, ngươi ngông cuồng tự xưng đế, bị chư hầu xem thường, ta đã liên minh với Tào Tháo, Lưu Bị và Lữ Bố, 3 đại quân đang tiến về Thọ Xuyến, ngươi sẽ thành mục tiêu công kích!","Viên Thuật, ngươi ngông cuồng tự xưng đế, bị chư hầu xem thường, ta đã liên minh với Tào Tháo, Lưu Bị và Lữ Bố, 3 đại quân đang tiến về Thọ Xuyến, ngươi sẽ thành mục tiêu công kích!","Viên Thuật","",},
        [2578] = {3103,"Viên Thuật","Ngọc Tỉ trong tay, thiên hạ của ta! Ai dám ngăn ta, ta sẽ diệt hắn!","Ngọc Tỉ trong tay, thiên hạ của ta! Ai dám ngăn ta, ta sẽ diệt hắn!","Nhân vật","",},
        [2579] = {3104,"Ngân Bình","Viên Thuật này thật buồn cười! Chúa công nhìn hắn đi, đánh không thắng liền bỏ chạy! Thế là có được 1 Danh Tướng Lệnh!","Viên Thuật này thật buồn cười! Chúa công nhìn hắn đi, đánh không thắng liền bỏ chạy! Thế là có được 1 Danh Tướng Lệnh!","Nhân vật","",},
        [2580] = {3104,"Nhân vật","Viên Thuật tuy bị chúng ta đánh bại, nhưng phạm vi thế lực của hắn rất lớn, chúng ta vẫn phải nghĩ cách.","Viên Thuật tuy bị chúng ta đánh bại, nhưng phạm vi thế lực của hắn rất lớn, chúng ta vẫn phải nghĩ cách.","Ngân Bình","",},
        [2581] = {3104,"Ngân Bình","Viên Thuật chiếm Hoa Dương, Nhữ Ân và Giang Hoài, địa bàn lớn, người đông, lương thực dồi dào, phải nghĩ cách làm suy yếu thế lực của hắn.","Viên Thuật chiếm Hoa Dương, Nhữ Ân và Giang Hoài, địa bàn lớn, người đông, lương thực dồi dào, phải nghĩ cách làm suy yếu thế lực của hắn.","Nhân vật","",},
        [2582] = {3104,"Nhân vật","Thế lực của Viên Thuật, ai khác thì không nói, vùng Giang Đông này thì ta có thể tiêu diệt hắn!","Thế lực của Viên Thuật, ai khác thì không nói, vùng Giang Đông này thì ta có thể tiêu diệt hắn!","Ngân Bình","",},
        [2583] = {3104,"Ngân Bình","Lúc đầu Tôn Kiên tìm được Ngọc Tỉ từ Lạc Dương, đem đến Giang Đông, sau bị Viên Thuật cướp, Tôn Sách rất hận Viên Thuật!","Lúc đầu Tôn Kiên tìm được Ngọc Tỉ từ Lạc Dương, đem đến Giang Đông, sau bị Viên Thuật cướp, Tôn Sách rất hận Viên Thuật!","Nhân vật","",},
        [2584] = {3104,"Nhân vật","Đi, đến Giang Đông tìm Tôn Sách ôn chuyện cũ!","Đi, đến Giang Đông tìm Tôn Sách ôn chuyện cũ!","Ngân Bình","",},
        [2585] = {3201,"Tôn Sách","Tiểu anh hùng hiếm khi tới Giang Đông ta, ngươi đã giúp ta rất nhiều lần, ta phải tiếp đã đàng hoàng! Người đâu, gọi rượu ngon món ngon!","Tiểu anh hùng hiếm khi tới Giang Đông ta, ngươi đã giúp ta rất nhiều lần, ta phải tiếp đã đàng hoàng! Người đâu, gọi rượu ngon món ngon!","Nhân vật","",},
        [2586] = {3201,"Nhân vật","Tôn huynh khách sáo, ha ha! Vậy phải đem rượu quý của huynh ra đây, đừng tiếc!","Tôn huynh khách sáo, ha ha! Vậy phải đem rượu quý của huynh ra đây, đừng tiếc!","Tôn Sách","",},
        [2587] = {3201,"Tôn Sách","Nói hay lắm, nào, ta kính đệ 1 ly!","Nói hay lắm, nào, ta kính đệ 1 ly!","Nhân vật","",},
        [2588] = {3201,"Nhân vật","(Ngà ngà say) Tôn huynh, đệ có một chuyện khuyên nhủ!","(Ngà ngà say) Tôn huynh, đệ có một chuyện khuyên nhủ!","Tôn Sách","",},
        [2589] = {3201,"Tôn Sách","Hai ta không phải người ngoài, có việc gì cứ nói!","Hai ta không phải người ngoài, có việc gì cứ nói!","Nhân vật","",},
        [2590] = {3201,"Nhân vật","Viên Thuật tiếm quyền thiên tử, bị cô lập, ắt sẽ trở thành mục tiêu công kích, ngươi nên đoạn tuyệt với ông ta thì hơn.","Viên Thuật tiếm quyền thiên tử, bị cô lập, ắt sẽ trở thành mục tiêu công kích, ngươi nên đoạn tuyệt với ông ta thì hơn.","Tôn Sách","",},
        [2591] = {3201,"Tôn Sách","Ha ha, đúng là anh hùng có khác. Viên Thuật đã có ý xưng đế, ta từng viết thư can gián, ai ngờ ông ta không nghe. Nghe ngươi nói chuyện này hôm nay, ta càng muốn đoạn tuyệt với ông ta!","Ha ha, đúng là anh hùng có khác. Viên Thuật đã có ý xưng đế, ta từng viết thư can gián, ai ngờ ông ta không nghe. Nghe ngươi nói chuyện này hôm nay, ta càng muốn đoạn tuyệt với ông ta!","Nhân vật","",},
        [2592] = {3201,"Nhân vật","Ha ha, Tôn huynh rất có trình độ!","Ha ha, Tôn huynh rất có trình độ!","Tôn Sách","",},
        [2593] = {3201,"Tôn Sách","Gần đây ta định tấn công Lư Giang, thống nhất 6 quận Giang Đông, tiểu anh hùng có muốn giúp ta không?","Gần đây ta định tấn công Lư Giang, thống nhất 6 quận Giang Đông, tiểu anh hùng có muốn giúp ta không?","Nhân vật","",},
        [2594] = {3201,"Nhân vật","Xin sẵn lòng!","Xin sẵn lòng!","Tôn Sách","",},
        [2595] = {3201,"Tôn Sách","Ha ha! Tiểu anh hùng quả nhiên hào khí ngút trời!","Ha ha! Tiểu anh hùng quả nhiên hào khí ngút trời!","Nhân vật","",},
        [2596] = {3202,"Tôn Sách","Thái thú Lư Giang Lưu Huân chạy đến Hứa Xương nương nhờ Tào Tháo.","Thái thú Lư Giang Lưu Huân chạy đến Hứa Xương nương nhờ Tào Tháo.","Nhân vật","",},
        [2597] = {3202,"Nhân vật","Hoàng Tổ cũng bị chúng ta đánh bại, nhưng tiếc chưa báo thù được cho Tôn huynh!","Hoàng Tổ cũng bị chúng ta đánh bại, nhưng tiếc chưa báo thù được cho Tôn huynh!","Tôn Sách","",},
        [2598] = {3202,"Tôn Sách","Đừng vội, đợi ta thống nhất Giang Đông, ta sớm muộn cũng sẽ tiêu diệt hắn ta, để tế vong hồn của cha ta!","Đừng vội, đợi ta thống nhất Giang Đông, ta sớm muộn cũng sẽ tiêu diệt hắn ta, để tế vong hồn của cha ta!","Nhân vật","",},
        [2599] = {3202,"Nhân vật","Chỉ còn lại Dự Chương, hãy xông lên!","Chỉ còn lại Dự Chương, hãy xông lên!","Tôn Sách","",},
        [2600] = {3203,"Nhân vật","Tôn huynh hôm nay có vẻ rất vui!","Tôn huynh hôm nay có vẻ rất vui!","Tôn Sách","",},
        [2601] = {3203,"Tôn Sách","Ha ha, Ngu Phiên khuyên hàng thành công, Dự Chương không cần tốn một binh một tốt đã có thể chiếm được, đương nhiên ta vui!","Ha ha, Ngu Phiên khuyên hàng thành công, Dự Chương không cần tốn một binh một tốt đã có thể chiếm được, đương nhiên ta vui!","Nhân vật","",},
        [2602] = {3203,"Nhân vật","Dưới trướng Tôn huynh quả thật nhiều người đa tài, vị thiếu niên phía trước là ai? Nhìn dáng vẻ chắc là nhân vật quan trọng.","Dưới trướng Tôn huynh quả thật nhiều người đa tài, vị thiếu niên phía trước là ai? Nhìn dáng vẻ chắc là nhân vật quan trọng.","Tôn Sách","",},
        [2603] = {3203,"Tôn Sách","Chúng ta hãy cùng đi xem thử nào.","Chúng ta hãy cùng đi xem thử nào.","Nhân vật","",},
        [2604] = {3204,"Tôn Sách","Ta là Tôn Sách, ngươi là ai?","Ta là Tôn Sách, ngươi là ai?","Lục Tốn","",},
        [2605] = {3204,"Lục Tốn","Bẩm tướng quân, tiểu sinh Lục Tốn, tự Bá Ngô, vốn là người Ngô Quận.","Bẩm tướng quân, tiểu sinh Lục Tốn, tự Bá Ngô, vốn là người Ngô Quận.","Tôn Sách","",},
        [2606] = {3204,"Tôn Sách","Ta biết Ngô Quận có tứ đại gia tộc, Lục, Cố, Chu, Trương. Ngươi là người của gia tộc Lục?","Ta biết Ngô Quận có tứ đại gia tộc, Lục, Cố, Chu, Trương. Ngươi là người của gia tộc Lục?","Lục Tốn","",},
        [2607] = {3204,"Lục Tốn","Đúng vậy.","Đúng vậy.","Tôn Sách","",},
        [2608] = {3204,"Tôn Sách","Rất tốt, quả là phong thái của người từ gia tộc lớn, ta tin chắc sau này sẽ có thành quả lớn, ngươi đã có gia thất chưa?","Rất tốt, quả là phong thái của người từ gia tộc lớn, ta tin chắc sau này sẽ có thành quả lớn, ngươi đã có gia thất chưa?","Lục Tốn","",},
        [2609] = {3204,"Lục Tốn","Vẫn chưa.","Vẫn chưa.","Tôn Sách","",},
        [2610] = {3204,"Tôn Sách","Ha ha, tốt, vậy ta sẽ gả con gái lớn của ta cho ngươi.","Ha ha, tốt, vậy ta sẽ gả con gái lớn của ta cho ngươi.","Lục Tốn","",},
        [2611] = {3205,"Tôn Sách","Tiểu anh hùng, con rể mới của ta thế nào?","Tiểu anh hùng, con rể mới của ta thế nào?","Nhân vật","",},
        [2612] = {3205,"Nhân vật","Xuất thân từ gia tộc lớn, lại có học thức, tương lai sẽ rộng mở.","Xuất thân từ gia tộc lớn, lại có học thức, tương lai sẽ rộng mở.","Tôn Sách","",},
        [2613] = {3205,"Tôn Sách","Ha ha, ta cũng cảm thấy vậy, hôm nay rất vui, chúng ta hãy ra ngoài săn bắn!","Ha ha, ta cũng cảm thấy vậy, hôm nay rất vui, chúng ta hãy ra ngoài săn bắn!","Nhân vật","",},
        [2614] = {3205,"Nhân vật","Tôn huynh, hãy mang thêm tùy tùng.","Tôn huynh, hãy mang thêm tùy tùng.","Tôn Sách","",},
        [2615] = {3205,"Tôn Sách","Ta là Tiểu Bá Vương Tôn Sách! Hiện ta đã bình định Lục Quận, Giang Đông đều là của ta! Ta biết ngươi thích Danh Tướng Lệnh, nên đã sai người tìm kiếm mang tặng cho ngươi!","Ta là Tiểu Bá Vương Tôn Sách! Hiện ta đã bình định Lục Quận, Giang Đông đều là của ta! Ta biết ngươi thích Danh Tướng Lệnh, nên đã sai người tìm kiếm mang tặng cho ngươi!","Nhân vật","",},
        [2616] = {3205,"Nhân vật","Tôn huynh quả nhiên khí khái hơn người, đa tạ!","Tôn huynh quả nhiên khí khái hơn người, đa tạ!","Tôn Sách","",},
        [2617] = {3301,"Tôn Sách","Tiểu anh hùng, có thể giúp ta một việc nữa không?","Tiểu anh hùng, có thể giúp ta một việc nữa không?","Nhân vật","",},
        [2618] = {3301,"Nhân vật","Tôn huynh là vì hai người con gái Đại Kiều, Tiểu Kiều của Kiều Công mà đến?","Tôn huynh là vì hai người con gái Đại Kiều, Tiểu Kiều của Kiều Công mà đến?","Tôn Sách","",},
        [2619] = {3301,"Tôn Sách","Tiểu anh hùng quả là thần cơ diệu toán.","Tiểu anh hùng quả là thần cơ diệu toán.","Nhân vật","",},
        [2620] = {3301,"Nhân vật","Tinh Thái và Tiểu Kiều vốn quen biết, cô ấy đã nói việc này với ta!","Tinh Thái và Tiểu Kiều vốn quen biết, cô ấy đã nói việc này với ta!","Tôn Sách","",},
        [2621] = {3301,"Tôn Sách","Thì ra là thế, Tôn mỗ thành tâm nhờ ngươi giúp đỡ, ta và Công Cẩn đã từng gặp hai tỉ muội họ, ta đã thích họ.","Thì ra là thế, Tôn mỗ thành tâm nhờ ngươi giúp đỡ, ta và Công Cẩn đã từng gặp hai tỉ muội họ, ta đã thích họ.","Nhân vật","",},
        [2622] = {3301,"Tôn Sách","Nhưng Kiều Công vì việc ta tấn công Uyển Thành không đồng ý gả họ cho ta. Giờ ngươi là Dương Uy Tướng do thiên tử phong, ngươi hãy giúp ta cầu thân, chắc chắn ôn ấy sẽ không từ chối.","Nhưng Kiều Công vì việc ta tấn công Uyển Thành không đồng ý gả họ cho ta. Giờ ngươi là Dương Uy Tướng do thiên tử phong, ngươi hãy giúp ta cầu thân, chắc chắn ôn ấy sẽ không từ chối.","Nhân vật","",},
        [2623] = {3301,"Nhân vật","Để ta thử xem sao!","Để ta thử xem sao!","Tôn Sách","",},
        [2624] = {3302,"Nhân vật","Tinh Thái sao lại là cô?","Tinh Thái sao lại là cô?","Tinh Thái","",},
        [2625] = {3302,"Tinh Thái","Chúa công, Kiều Công nói người thông qua tỉ thí của hai chị em Tiểu Kiều, thì ông ấy sẽ đồng ý!","Chúa công, Kiều Công nói người thông qua tỉ thí của hai chị em Tiểu Kiều, thì ông ấy sẽ đồng ý!","Nhân vật","",},
        [2626] = {3302,"Nhân vật","Việc này đơn giản, không cần nhiều lời, ta sẽ dễ dàng vượt qua tỉ thí!","Việc này đơn giản, không cần nhiều lời, ta sẽ dễ dàng vượt qua tỉ thí!","Tinh Thái","",},
        [2627] = {3302,"Tinh Thái","Chúa công lợi hại, hãy bắt đầu nghênh chiến!","Chúa công lợi hại, hãy bắt đầu nghênh chiến!","Nhân vật","",},
        [2628] = {3303,"Ngân Bình","Thượng Hương tỉ tỉ, võ nghệ người cao cường, tương lai nhất định sẽ lấy một vị đại anh hùng!","Thượng Hương tỉ tỉ, võ nghệ người cao cường, tương lai nhất định sẽ lấy một vị đại anh hùng!","Thượng Hương","",},
        [2629] = {3303,"Thượng Hương","Ta không muốn lấy chồng, ta muốn ra trận diệt địch!","Ta không muốn lấy chồng, ta muốn ra trận diệt địch!","Ngân Bình","",},
        [2630] = {3303,"Ngân Bình","Tỉ tỉ, lấy đại anh hùng thì mới có cơ hội giúp phu quân ra chiến trường! Nếu lấy một người bình thường thì chỉ có thể giúp phu quân sinh con đẻ cái!","Tỉ tỉ, lấy đại anh hùng thì mới có cơ hội giúp phu quân ra chiến trường! Nếu lấy một người bình thường thì chỉ có thể giúp phu quân sinh con đẻ cái!","Thượng Hương","",},
        [2631] = {3303,"Thượng Hương","Ngươi nói cũng có lý, để ta tìm mẹ ta, ta phải lấy một vị đại anh hùng!","Ngươi nói cũng có lý, để ta tìm mẹ ta, ta phải lấy một vị đại anh hùng!","Ngân Bình","",},
        [2632] = {3304,"Tinh Thái","Đại Kiều tỉ tỉ, người rất đẹp, quả là quốc sắc thiên hương!","Đại Kiều tỉ tỉ, người rất đẹp, quả là quốc sắc thiên hương!","Đại Kiều","",},
        [2633] = {3304,"Đại Kiều","Tinh Thái muội muội, muội cũng rất dễ thương.","Tinh Thái muội muội, muội cũng rất dễ thương.","Tinh Thái","",},
        [2634] = {3304,"Ngân Bình","Quan Ngân Bình mới là đệ nhất thiên hạ!","Quan Ngân Bình mới là đệ nhất thiên hạ!","Tinh Thái","",},
        [2635] = {3304,"Tinh Thái","...Quan Ngân Bình!","...Quan Ngân Bình!","Ngân Bình","",},
        [2636] = {3304,"Ngân Bình","Đừng đùa nữa! Đại Kiều tỉ tỉ, chúa công bảo ta hỏi tỉ có đồng ý lấy Tôn Sách không?","Đừng đùa nữa! Đại Kiều tỉ tỉ, chúa công bảo ta hỏi tỉ có đồng ý lấy Tôn Sách không?","Đại Kiều","",},
