        [2638] = {3304,"Ngân Bình","Ha ha, vậy thì tốt, quả là trai tài gái sắc!","Ha ha, vậy thì tốt, quả là trai tài gái sắc!","Đại Kiều","",},
        [2639] = {3305,"Tiểu Kiều","Dương Uy Tướng, ta cuối cùng có thể gặp được người! Xin tha thứ cho sự lỗ mãng của Tiểu Kiều!","Dương Uy Tướng, ta cuối cùng có thể gặp được người! Xin tha thứ cho sự lỗ mãng của Tiểu Kiều!","Nhân vật","",},
        [2640] = {3305,"Nhân vật","Sao lại nói vậy?","Sao lại nói vậy?","Tiểu Kiều","",},
        [2641] = {3305,"Tiểu Kiều","Thật ra ta và Công Cẩn sớm đã quen biết nhau, Thỏ Trắng của ta cũng là do Công Cẩn tặng! Để chị và Tôn Lang gặp nhau cũng là ý của ta! Yêu cầu tỉ thí là do ý của ta!","Thật ra ta và Công Cẩn sớm đã quen biết nhau, Thỏ Trắng của ta cũng là do Công Cẩn tặng! Để chị và Tôn Lang gặp nhau cũng là ý của ta! Yêu cầu tỉ thí là do ý của ta!","Nhân vật","",},
        [2642] = {3305,"Ngân Bình","Tiểu Kiều muội muội quả thật nghịch ngợm!","Tiểu Kiều muội muội quả thật nghịch ngợm!","Tiểu Kiều","",},
        [2643] = {3305,"Tiểu Kiều","Ta yêu cầu tỉ thí là vì muốn gặp Dương Uy Tướng!","Ta yêu cầu tỉ thí là vì muốn gặp Dương Uy Tướng!","Ngân Bình","",},
        [2644] = {3305,"Tiểu Kiều","Dương Uy Tướng, Tiểu Kiều tuy ở Giang Đông, nhưng sớm đã nghe danh người.","Dương Uy Tướng, Tiểu Kiều tuy ở Giang Đông, nhưng sớm đã nghe danh người.","Nhân vật","",},
        [2645] = {3305,"Tiểu Kiều","Hôm nay, được thấy phong thái của tướng quân quả danh bất hư truyền! Công Cẩn nói người thích Danh Tướng Lệnh, ta đây có 1 cái, tặng cho tướng quân!","Hôm nay, được thấy phong thái của tướng quân quả danh bất hư truyền! Công Cẩn nói người thích Danh Tướng Lệnh, ta đây có 1 cái, tặng cho tướng quân!","Nhân vật","",},
        [2646] = {3305,"Nhân vật","Ha ha, đa tạ! Nếu hai tỉ muội họ đều đồng ý, vậy thì hãy sớm tiến hành hôn lễ!","Ha ha, đa tạ! Nếu hai tỉ muội họ đều đồng ý, vậy thì hãy sớm tiến hành hôn lễ!","Tiểu Kiều","",},
        [2647] = {3401,"Chu Du","Dương Uy Tướng, Công Cẩn thật đa tạ ngươi, nếu không có ngươi, e rằng hôn sự này khó thành.","Dương Uy Tướng, Công Cẩn thật đa tạ ngươi, nếu không có ngươi, e rằng hôn sự này khó thành.","Nhân vật","",},
        [2648] = {3401,"Nhân vật","Từ xưa Giang Đông nổi tiếng nhiều mỹ nhân, Đại Tiểu Kiều quốc sắc thiên hương, người và Bá Phù đều có được mỹ nhân, quả thật đáng mừng.","Từ xưa Giang Đông nổi tiếng nhiều mỹ nhân, Đại Tiểu Kiều quốc sắc thiên hương, người và Bá Phù đều có được mỹ nhân, quả thật đáng mừng.","Chu Du","",},
        [2649] = {3401,"Chu Du","Dương Uy Tướng quá khen...","Dương Uy Tướng quá khen...","Nhân vật","",},
        [2650] = {3401,"Nhân vật","Mau mang rượu cho Công Cẩn! Ta xin cạn chén trước!","Mau mang rượu cho Công Cẩn! Ta xin cạn chén trước!","Chu Du","",},
        [2651] = {3401,"Chu Du","Tướng quân uống nhiều rồi!","Tướng quân uống nhiều rồi!","Nhân vật","",},
        [2652] = {3401,"Nhân vật","Không, ta không say, vị kia có phải là Thái Sử Từ tướng quân không? Ta muốn tỉ thí với hắn!","Không, ta không say, vị kia có phải là Thái Sử Từ tướng quân không? Ta muốn tỉ thí với hắn!","Chu Du","",},
        [2653] = {3402,"Ngân Bình","Thái Sử Từ tướng quân, chúa công ta uống say nên muốn tỉ thí với ngài!","Thái Sử Từ tướng quân, chúa công ta uống say nên muốn tỉ thí với ngài!","Thái Sử Từ","",},
        [2654] = {3402,"Thái Sử Từ","Ngân Bình cô nương yên tâm, ta sẽ không đả thương chúa công đâu!","Ngân Bình cô nương yên tâm, ta sẽ không đả thương chúa công đâu!","Ngân Bình","",},
        [2655] = {3402,"Ngân Bình","Ta chỉ sợ chúa công vốn lợi hại, sau khi uống rượu sẽ càng mạnh! Ta lo ngươi bị đả thương nên nhắc nhở!","Ta chỉ sợ chúa công vốn lợi hại, sau khi uống rượu sẽ càng mạnh! Ta lo ngươi bị đả thương nên nhắc nhở!","Thái Sử Từ","",},
        [2656] = {3402,"Thái Sử Từ","...Đa tạ cô nương nhắc nhở.","...Đa tạ cô nương nhắc nhở.","Ngân Bình","",},
        [2657] = {3403,"Tinh Thái","Chúa công, Tinh Thái đã chuẩn bị canh giải rượu! Hãy mau uống.","Chúa công, Tinh Thái đã chuẩn bị canh giải rượu! Hãy mau uống.","Nhân vật","",},
        [2658] = {3403,"Nhân vật","Đa tạ Tinh Thái, ta đã đỡ nhiều! Hôm qua uống hơi nhiều.","Đa tạ Tinh Thái, ta đã đỡ nhiều! Hôm qua uống hơi nhiều.","Tinh Thái","",},
        [2659] = {3403,"Tinh Thái","Chúa công, bên ngoài có vài vị anh hùng đến xin gặp, họ đợi đã lâu.","Chúa công, bên ngoài có vài vị anh hùng đến xin gặp, họ đợi đã lâu.","Nhân vật","",},
        [2660] = {3403,"Nhân vật","Là ai vậy?","Là ai vậy?","Tinh Thái","",},
        [2661] = {3403,"Tinh Thái","Đây là thiệp xin gặp của họ, xin chúa công xem qua!","Đây là thiệp xin gặp của họ, xin chúa công xem qua!","Nhân vật","",},
        [2662] = {3403,"Nhân vật","Thì ra là Lỗ Túc, Lữ Mông, báo cho Chu Du, chúng ta cùng nhau đến gặp họ.","Thì ra là Lỗ Túc, Lữ Mông, báo cho Chu Du, chúng ta cùng nhau đến gặp họ.","Tinh Thái","",},
        [2663] = {3404,"Lỗ Túc","Lỗ Túc bái kiến tướng quân!","Lỗ Túc bái kiến tướng quân!","Nhân vật","",},
        [2664] = {3404,"Nhân vật","Tử Kính huynh, đã lâu không gặp! Ta nghe Công Cẩn nhắc qua, người là chỗ quen biết cũ với ngài ấy.","Tử Kính huynh, đã lâu không gặp! Ta nghe Công Cẩn nhắc qua, người là chỗ quen biết cũ với ngài ấy.","Lỗ Túc","",},
        [2665] = {3404,"Chu Du","Lúc xưa khi ta còn là Sào Trưởng, nghe đồn Tử Kính huynh hay giúp đỡ người khác nên đến nhờ huynh. Tử Kính huynh đã cho ta cả kho lương thực.","Lúc xưa khi ta còn là Sào Trưởng, nghe đồn Tử Kính huynh hay giúp đỡ người khác nên đến nhờ huynh. Tử Kính huynh đã cho ta cả kho lương thực.","Lỗ Túc","",},
        [2666] = {3404,"Nhân vật","Tử Kính huynh quả là hào phóng! Ai ai đều đồn huynh là vị thần tài sống, quả thật không sai!","Tử Kính huynh quả là hào phóng! Ai ai đều đồn huynh là vị thần tài sống, quả thật không sai!","Lỗ Túc","",},
        [2667] = {3404,"Lỗ Túc","Tướng quân sau này nếu có khó khăn có thể tìm ta, Tử Kính chắc chắn sẽ không từ chối!","Tướng quân sau này nếu có khó khăn có thể tìm ta, Tử Kính chắc chắn sẽ không từ chối!","Nhân vật","",},
        [2668] = {3405,"Lữ Mông","Lữ Mông bái kiến tướng quân!","Lữ Mông bái kiến tướng quân!","Nhân vật","",},
        [2669] = {3405,"Nhân vật","Tử Minh tuổi trẻ tài cao, quả là anh hùng xuất thiếu niên!","Tử Minh tuổi trẻ tài cao, quả là anh hùng xuất thiếu niên!","Lữ Mông","",},
        [2670] = {3405,"Lữ Mông","Tướng quân quá khen!","Tướng quân quá khen!","Nhân vật","",},
        [2671] = {3405,"Nhân vật","Sao người lại mang theo mỏ neo và thẻ tre?","Sao người lại mang theo mỏ neo và thẻ tre?","Lữ Mông","",},
        [2672] = {3405,"Lữ Mông","Mỏ neo là vũ khí do ta chế tạo, thẻ tre là binh thư, Tử Kính huynh khuyên ta nên đọc nhiều binh thư để sau này báo quốc.","Mỏ neo là vũ khí do ta chế tạo, thẻ tre là binh thư, Tử Kính huynh khuyên ta nên đọc nhiều binh thư để sau này báo quốc.","Nhân vật","",},
        [2673] = {3405,"Nhân vật","Được! Tương lai của Tử Minh chắc chắn sẽ rộng mở, Giang Đông quả nhiên nhiều nhân tài!","Được! Tương lai của Tử Minh chắc chắn sẽ rộng mở, Giang Đông quả nhiên nhiều nhân tài!","Lữ Mông","",},
        [2674] = {3406,"Chu Du","Dương Uy Tướng, hãy xem!","Dương Uy Tướng, hãy xem!","Nhân vật","",},
        [2675] = {3406,"Nhân vật","Đây là thư Tào Tháo viết cho người!","Đây là thư Tào Tháo viết cho người!","Chu Du","",},
        [2676] = {3406,"Chu Du","Tào Tháo dám khuyên ta đầu quân cho hắn ta.","Tào Tháo dám khuyên ta đầu quân cho hắn ta.","Nhân vật","",},
        [2677] = {3406,"Nhân vật","Công Cẩn văn thao võ lược, vốn là anh hùng đương đại, Tào Tháo muốn chiêu mộ cũng là việc bình thường.","Công Cẩn văn thao võ lược, vốn là anh hùng đương đại, Tào Tháo muốn chiêu mộ cũng là việc bình thường.","Chu Du","",},
        [2678] = {3406,"Chu Du","Tuy nói thế, nhưng ta và Bá Phù vốn có giao tình! Tào Tháo vốn có dã tâm, e rằng sau này chắc chắn phải chiến đấu với hắn.","Tuy nói thế, nhưng ta và Bá Phù vốn có giao tình! Tào Tháo vốn có dã tâm, e rằng sau này chắc chắn phải chiến đấu với hắn.","Nhân vật","",},
        [2679] = {3406,"Nhân vật","Công Cẩn có thể yên tâm! Viên Thiệu đang chiếm Ký Châu, Viên Thuật chiếm Hoài Nam, lại có thêm Lữ Bố, Tào Tháo đang gặp địch ba hướng, khó lòng chống lại, hơn nữa Giang Đông còn cách một con sông lớn!","Công Cẩn có thể yên tâm! Viên Thiệu đang chiếm Ký Châu, Viên Thuật chiếm Hoài Nam, lại có thêm Lữ Bố, Tào Tháo đang gặp địch ba hướng, khó lòng chống lại, hơn nữa Giang Đông còn cách một con sông lớn!","Chu Du","",},
        [2680] = {3406,"Chu Du","Tướng quân lần này đến Giang Đông, đã giúp đỡ rất nhiều, Danh Tướng Lệnh, xin tặng cho tướng quân.","Tướng quân lần này đến Giang Đông, đã giúp đỡ rất nhiều, Danh Tướng Lệnh, xin tặng cho tướng quân.","Nhân vật","",},
        [2681] = {3501,"Hạ Hầu Uyên","Thừa tướng muốn bình loạn thiên hạ, cần phải chiêu mộ Trương Tú, sau đó ổn định Hà Bắc.","Thừa tướng muốn bình loạn thiên hạ, cần phải chiêu mộ Trương Tú, sau đó ổn định Hà Bắc.","Tào Tháo","",},
        [2682] = {3501,"Tào Tháo","Trương Tú, là ai?","Trương Tú, là ai?","Hạ Hầu Uyên","",},
        [2683] = {3501,"Nhân vật","Bẩm thừa tướng, Trương Tú vốn là con trai của Phiêu Kỵ Tướng Trương Tế, nắm giữ Danh Tướng Lệnh, đóng quân tại Uyển Thành.","Bẩm thừa tướng, Trương Tú vốn là con trai của Phiêu Kỵ Tướng Trương Tế, nắm giữ Danh Tướng Lệnh, đóng quân tại Uyển Thành.","Tào Tháo","",},
        [2684] = {3501,"Nhân vật","Danh hiệu Bắc Địa Thương Vương, dùng Hổ Thủ Kim Thương, Bách Điễu Triều Phụng Thương uy chấn thiên hạ.","Danh hiệu Bắc Địa Thương Vương, dùng Hổ Thủ Kim Thương, Bách Điễu Triều Phụng Thương uy chấn thiên hạ.","Tào Tháo","",},
        [2685] = {3501,"Nhân vật","Người này vốn là anh hùng đương đại, nếu có thể thu phục sẽ có tác dụng lớn.","Người này vốn là anh hùng đương đại, nếu có thể thu phục sẽ có tác dụng lớn.","Tào Tháo","",},
        [2686] = {3501,"Tào Tháo","Theo ý của ngươi là ta nên lập tức tấn công phía nam.","Theo ý của ngươi là ta nên lập tức tấn công phía nam.","Nhân vật","",},
