        [2936] = {4603,"Khổng Dung","Điền Phong, Thư Thụ đều là trung thần. Nhan Lương, Văn Xú vô cùng dũng mãnh. Cao Lãm, Trương Hợp, Thuần Vu Quỳnh đều là danh tướng nổi tiếng.","Điền Phong, Thư Thụ đều là trung thần. Nhan Lương, Văn Xú vô cùng dũng mãnh. Cao Lãm, Trương Hợp, Thuần Vu Quỳnh đều là danh tướng nổi tiếng.","Tuân Úc","",},
        [2937] = {4603,"Khổng Dung","Thế thì làm sao y là người vô dụng được.","Thế thì làm sao y là người vô dụng được.","Tuân Úc","",},
        [2938] = {4603,"Tuân Úc","Viên Thiệu tuy quân binh đông nhưng lại không kỷ luật, Điền Phong là người không được Viên Thiệu tin tưởng, Hứa Du thì tham lam còn Thẩm Phối thì vô mưu, nên có cũng như không.","Viên Thiệu tuy quân binh đông nhưng lại không kỷ luật, Điền Phong là người không được Viên Thiệu tin tưởng, Hứa Du thì tham lam còn Thẩm Phối thì vô mưu, nên có cũng như không.","Khổng Dung","",},
        [2939] = {4603,"Tuân Úc","Người đông nhưng không hòa hợp, sớm muộn cũng sẽ có chuyện. Nhan Lương, Văn Xú cũng chỉ là bọn thất phu, muốn bắt rất dễ.","Người đông nhưng không hòa hợp, sớm muộn cũng sẽ có chuyện. Nhan Lương, Văn Xú cũng chỉ là bọn thất phu, muốn bắt rất dễ.","Khổng Dung","",},
        [2940] = {4603,"Tuân Úc","Còn lại đều là những kẻ không ra gì, nên không có gì đáng lo cả.","Còn lại đều là những kẻ không ra gì, nên không có gì đáng lo cả.","Khổng Dung","",},
