        [2942] = {4603,"Tào Tháo","Lệnh cho Lưu Đại, Vương Trung dẫn theo năm vạn quân đến Từ Châu tiến đánh Lưu Bị.","Lệnh cho Lưu Đại, Vương Trung dẫn theo năm vạn quân đến Từ Châu tiến đánh Lưu Bị.","Tuân Úc","",},
        [2943] = {4604,"Lưu Bị","Viên Bản Sơ tuy cho binh đồn trú ở Lê Dương nhưng vì các mưu thần bất hòa nên vẫn chưa có động tĩnh gì.","Viên Bản Sơ tuy cho binh đồn trú ở Lê Dương nhưng vì các mưu thần bất hòa nên vẫn chưa có động tĩnh gì.","Trần Đăng","",},
        [2944] = {4604,"Lưu Bị","Tào Tháo không biết đang ở đâu. Nghe nói trong Lê Dương Thành không thấy cờ của quân Tào, nhưng sao ở đây lại có cờ hiệu của y?","Tào Tháo không biết đang ở đâu. Nghe nói trong Lê Dương Thành không thấy cờ của quân Tào, nhưng sao ở đây lại có cờ hiệu của y?","Trần Đăng","",},
        [2945] = {4604,"Trần Đăng","Tào Tháo quỷ kế đa đoan, y rất xem trọng Hà Bắc nên nhất định sẽ không rời khỏi nơi đó. Cờ hiệu của Tào Tháo ở đây nhưng y chắc chắn không có mặt ở đây đâu.","Tào Tháo quỷ kế đa đoan, y rất xem trọng Hà Bắc nên nhất định sẽ không rời khỏi nơi đó. Cờ hiệu của Tào Tháo ở đây nhưng y chắc chắn không có mặt ở đây đâu.","Lưu Bị","",},
