        [2957] = {4606,"Lưu Bị","Lưu Đại từng là Duyện Châu Thích Sử, lại là chư hầu khi thảo phạt Đổng Trác ở Hổ Lao Quan, giờ làm tiền phong, đệ đừng khinh địch.","Lưu Đại từng là Duyện Châu Thích Sử, lại là chư hầu khi thảo phạt Đổng Trác ở Hổ Lao Quan, giờ làm tiền phong, đệ đừng khinh địch.","Trương Phi","",},
        [2958] = {4606,"Trương Phi","Chỉ vậy thôi thì có gì đáng sợ chứ! Đệ cũng sẽ như nhị ca vậy, nhất định sẽ bắt y về đây.","Chỉ vậy thôi thì có gì đáng sợ chứ! Đệ cũng sẽ như nhị ca vậy, nhất định sẽ bắt y về đây.","Lưu Bị","",},
